pe":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Becoming a backend developer - Part 2: Building the server","url":"backend-developer-part-2","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"58WBWjhcWcuAMI6qIAyiC4","type":"Entry","createdAt":"2018-12-13T20:46:00.073Z","updatedAt":"2018-12-13T20:46:36.867Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Suragch","url":"suragch","email":"studymongolian@gmail.com","twitter":"https://twitter.com/suragch","github":"https://github.com/suragch","guestWriter":true}}],"projectRepo":"https://github.com/suragch/backend_demo","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SdjzfMUtikUcK2CWI8uyC","type":"Entry","createdAt":"2018-07-31T17:19:35.290Z","updatedAt":"2019-08-28T08:41:23.854Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"no pusher tech","url":"no-pusher-tech","featured":false}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3EB32t4KaI0IGIEI6AocoA","type":"Entry","createdAt":"2018-02-20T16:52:11.196Z","updatedAt":"2018-02-26T12:31:10.440Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Android","url":"android","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1st5ugwNDbvlnFFHBadh5b","type":"Entry","createdAt":"2019-03-13T18:08:14.919Z","updatedAt":"2019-03-13T18:08:14.920Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Dart","url":"dart","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3Q3oRmiXjOmcSmweY4gKY8","type":"Entry","createdAt":"2018-09-12T09:33:28.375Z","updatedAt":"2018-09-12T09:33:28.375Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Flutter","url":"flutter","type":"framework","featured":false}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"54VH6KuDAsYY80KIeQE0OO","type":"Entry","createdAt":"2018-02-12T14:50:20.551Z","updatedAt":"2018-02-26T12:31:15.549Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Kotlin","url":"kotlin","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3WH6Mz2j7iyIaqCeOKIIkQ","type":"Entry","createdAt":"2018-02-12T14:44:51.150Z","updatedAt":"2018-02-12T14:44:51.150Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Node.js","url":"node-js","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}}],"reviewDate":"2019-03-22","description":"In this three-part series, we will cover all the basics of what it takes for a mobile app developer to become a backend developer. You will create backends using Node and Dart, with mobile frontend examples using Android, Swift, and Flutter.","prerequisite":"You need experience in at least one of Android, iOS or Flutter development.","content":"This is a guide for mobile app developers. In this three-part series we are covering all the basics of what it takes to become a backend developer.\n\n\n- [Part 1: Foundational concepts](https://pusher.com/tutorials/backend-developer-part-1)\n- Part 2: Building the server \n- Part 3: Connecting to the server from a mobile app\n\n\n## Introduction\n\nIn the [first tutorial](https://pusher.com/tutorials/backend-developer-part-1) we went over background knowledge related to client-server communication. This included URIs, HTTP, REST APIs, and JSON. If any of these topics are unfamiliar to you, go back and review them. \n\nNow, let’s build a server! \n\n## Prerequisites\n\nI'm not assuming you have any backend server experience, but I won't be repeating online documentation, so you will need to be able to following the installation directions from the links I give you. \n\nI will be using [Visual Studio Code](https://code.visualstudio.com/) to do the server programming. Feel free to use another IDE if you prefer.\n\n\n## Building the backend\n\nFor development purposes, we are going to install the server software on our local machine and have it communicate directly with our mobile app running in an emulator. \n\nBelow are two examples of a backend server: Node.js and Server-Side Dart. You only need to choose one. [Node.js](https://nodejs.org/) is very [popular](https://insights.stackoverflow.com/trends?tags=node.js) and you write the server code in JavaScript. [Server Side Dart](https://www.dartlang.org/server) is not nearly as popular as Node.js, but for Flutter developers the advantage is that you can use the same language for the frontend and the backend. It really doesn't matter which one you choose, but if you can't decide, go with Node.js.\n\n\n## Backend example one: Node.js\n\n\n\u003e **Note:** This tutorial was tested with Node.js 10.15.1\n\n### Install Node.js\n\nGo to [nodejs.org](https://nodejs.org) to download and install Node.js. \n\nThe [Getting Started Guide](https://nodejs.org/en/docs/guides/getting-started-guide/) shows the code for a basic HTTP server. It doesn’t explain the code, though, so I have added comments below:\n\n```javascript\n    // The require() function loads modules in Node.js. \n    // A module is code that is in another file.\n    // 'http' is a builtin module in Node.js. It allows data transfer\n    // using the HTTP protocol and enables setting up an HTTP server.\n    const http = require('http');\n    \n    // During development we will use the localhost.\n    const hostname = '127.0.0.1';\n    const port = 3000;\n    \n    // set up an http server\n    // Two parameters are passed in:\n    //     req = request (from the client)\n    //     res = response (from the server)\n    const server = http.createServer((req, res) =\u003e {\n    \n      // A status code of 200 means OK (A 404 would mean Not Found)\n      res.statusCode = 200;\n    \n      // A header adds additional information. \n      // Here we are using a name-value pair to set the\n      // media type (MIME type) as plain text (as opposed to html).\n      res.setHeader('Content-Type', 'text/plain');\n    \n      // This writes a message and then ends the response.\n      res.end('Hello World\\n');\n    });\n    \n    // This causes the server to listen for requests from clients on \n    // the hostname and port that we defined above.\n    server.listen(port, hostname, () =\u003e {\n      console.log(`Server running at http://${hostname}:${port}/`);\n    });\n```\n\nGo ahead and test the code by running the app as described in the Getting Started Guide. You should see “Hello World” in the browser window when you navigate to http://localhost:3000.\n\n### Implement our API\n\nIn Part 1 [ADD LINK] I told you the REST API that we were going to make would look like this:\n\n```\n    GET http://localhost:3000/              // get all items\n    GET http://localhost:3000/id            // get item at id\n    POST http://localhost:3000/             // create new item\n    PUT http://localhost:3000/id            // replace item at id\n    PATCH http://localhost:3000/id          // update item at id\n    DELETE http://localhost:3000/id         // delete item at id\n```\n\nOur client app that we will make in Part 3 is going to look like this:\n\n![backend-mobile-2-1](//images.ctfassets.net/1es3ne0caaid/7MvuGc4TMFanlAXX9KNXX7/a80f94db1eb63c5146ad64dc2daefd89/backend-mobile-2-1.png)\n\nSo we need to make our server handle all of these requests. First go to the terminal and create a new directory for our Node.js project.\n\n```\n    mkdir nodejs_server\n    cd nodejs_server\n```\n\nThe way to create a new project is to use the Node Package Manager (npm). Run the following command and accept the default values for everything. (If you need to edit this info later you can open `package.json`.)\n\n```\n    npm init\n```\n\nWe are also going to use the [Express](https://expressjs.com/) framework, which simplifies a lot of the HTTP protocol handling.\n\n```\n    npm install express --save\n```\n\nNow create the server file that we named in the `npm init` step above:\n\n```\n    touch index.js\n```\n\nOpen it in an editor (I'm using [VSCode](https://code.visualstudio.com/)), and paste in the following:\n\n```javascript\n    // nodejs_server/index.js\n    \n    var express = require('express');\n    var bodyParser = require('body-parser');\n    var app = express();\n    \n    // bodyParser is a type of middleware\n    // It helps convert JSON strings\n    // the 'use' method assigns a middleware\n    app.use(bodyParser.json({ type: 'application/json' }));\n    \n    const hostname = '127.0.0.1';\n    const port = 3000;\n    \n    // http status codes\n    const statusOK = 200;\n    const statusNotFound = 404;\n    \n    // using an array to simulate a database for demonstration purposes\n    var mockDatabase = [\n        {\n            fruit: \"apple\",\n            color: \"red\"\n        },\n        {\n            fruit: \"banana\",\n            color: \"yellow\"\n        }\n    ]\n    \n    // Handle GET (all) request\n    app.get('/', function(req, res) {\n        // error checking\n        if (mockDatabase.length \u003c 1) {\n            res.statusCode = statusNotFound;\n            res.send('Item not found');\n            return;\n        }\n        // send response\n        res.statusCode = statusOK;\n        res.send(mockDatabase);\n    });\n    \n    // Handle GET (one) request\n    app.get('/:id', function(req, res) {\n        // error checking\n        var id = req.params.id;\n        if (id \u003c 0 || id \u003e= mockDatabase.length) {\n            res.statusCode = statusNotFound;\n            res.send('Item not found');\n            return;\n        }\n        // send response\n        res.statusCode = statusOK;\n        res.send(mockDatabase[id]);\n    });\n    \n    // Handle POST request\n    app.post('/', function(req, res) {\n        // get data from request\n        var newObject = req.body; // TODO validate data\n        mockDatabase.push(newObject);\n        // send created item back with id included\n        var id = mockDatabase.length - 1;\n        res.statusCode = statusOK;\n        res.send(`Item added with id ${id}`);\n    });\n    \n    // Handle PUT request\n    app.put('/:id', function(req, res) {\n        // replace current object\n        var id = req.params.id;     // TODO validate id\n        var replacement = req.body; // TODO validate data\n        mockDatabase[id] = replacement;\n        // report back to the client\n        res.statusCode = statusOK;\n        res.send(`Item replaced at id ${id}`);\n    });\n    \n    // Handle PATCH request \n    app.patch('/:id', function(req, res) {\n        // update current object\n        var id = req.params.id;        // TODO validate id\n        var newColor = req.body.color; // TODO validate data\n        mockDatabase[id].color = newColor;\n        // report back to the client\n        res.statusCode = statusOK;\n        res.send(`Item updated at id ${id}`);\n    });\n    \n    // Handle DELETE request \n    app.delete('/:id', function(req, res) {\n        // delete specified item\n        var id = req.params.id;  // TODO validate id\n        mockDatabase.splice(id, 1);\n        // send response back\n        res.statusCode = statusOK;\n        res.send(`Item deleted at id ${id}`);\n    });\n    \n    app.listen(port, hostname, function () {\n        console.log(`Listening at http://${hostname}:${port}/...`);\n    });\n```\n\nSave the file and run it in the terminal.\n\n```\n    node index.js\n```\n\nThe server is now running on your machine. You can use [Postman](https://www.getpostman.com/) (see [docs](https://learning.getpostman.com/getting-started/) and [tutorial](https://www.youtube.com/watch?v=t5n07Ybz7yI)) to test the server now, or you can use one of the client apps that we will make in part three. \n\n![backend-mobile-2-2](//images.ctfassets.net/1es3ne0caaid/4gkCYUOTv1gGOai0cnnMlc/ce7cd897542def20af71963b0daf9116/backend-mobile-2-2.gif)\n\n### Further study\n\n\n- [NodeJS REST API](https://www.youtube.com/watch?v=F7NVpxxmmgM\u0026list=PL0dzCUj1L5JE4w_OctDGyZOhML6OtJSqR) \n- [Building a RESTful API with Node.js](https://www.youtube.com/watch?v=0oXYLzuucwE\u0026index=1\u0026list=PL55RiY5tL51q4D-B63KBnygU6opNPFk_q) \n- [Node.js Crash Course](https://www.youtube.com/watch?v=fBNz5xF-Kx4) \n- [Building a Node.js REST API with Express](https://medium.com/@jeffandersen/building-a-node-js-rest-api-with-express-46b0901f29b6) \n\n\n## Backend example two: Server Side Dart\n\n\n\u003e **Note:** This tutorial was tested with Dart 2.1.2\n\n### Install Dart\n\nIf you have Flutter installed on your system, then Dart is already installed. But if not, then go to [this link](https://www.dartlang.org/tools/sdk) to download and install the Dart SDK. \n\nCheck if `dart/bin` is in your system path:\n\n```\n    # Linux or Mac\n    echo $PATH\n    \n    # Windows Command Prompt\n    echo %path%\n    \n    # Windows Powershell\n    $env:Path -split ';'\n```\n\nIf it isn't and you just installed it from the link above (because you don't have Flutter), you can add it to your path like this:\n\n```\n    # Linux or Mac\n    export PATH=\"$PATH:/usr/lib/dart/bin\"\n```\n\nOn Windows it is easiest to use the GUI to set environment variables.\n\nIf you already had Flutter/Dart installed, find your Flutter SDK directory. Then you can add the path like this (replacing `\u003cflutter\u003e`):\n\n```\n    export PATH=\"$PATH:\u003cflutter\u003e/bin/cache/dart-sdk/bin\"\n```\n\nThis only updates the path until you restart your machine. You will probably want to update your `.bash_profile` (or whatever you use on your system) to make it permanent.\n\n### Install Aqueduct\n\nWe are also going to use the [Aqueduct](https://aqueduct.io/) framework to make HTTP request APIs easier to build. Now that we have Dart installed, we can install Aqueduct like this:\n\n```\n    pub global activate aqueduct\n```\n\nFollow the directions to add the `$HOME/.pub-cache/bin` to your path if you are instructed to.\n\n### Implement our API\n\nIn [part one](https://pusher.com/tutorials/backend-developer-part-1) I told you the REST API that we were going to make would look like this:\n\n```\n    GET http://localhost:3000/              // get all items\n    GET http://localhost:3000/id            // get item at id\n    POST http://localhost:3000/             // create new item\n    PUT http://localhost:3000/id            // replace item at id\n    PATCH http://localhost:3000/id          // update item at id\n    DELETE http://localhost:3000/id         // delete item at id\n```\n\nOur client app that we will make in part three is going to look like this:\n\n![backend-mobile-2-1](//images.ctfassets.net/1es3ne0caaid/7MvuGc4TMFanlAXX9KNXX7/a80f94db1eb63c5146ad64dc2daefd89/backend-mobile-2-1.png)\n\nSo we need to make our server handle all of these requests.\n\nFirst go to the terminal and cd to the directory that you want to create the server project folder in. Then type:\n\n```\n    aqueduct create dart_server\n```\n\nOpen the project in an editor. The [Aqueduct documentation](https://aqueduct.io/docs/) recommends [IntelliJ IDEA](https://www.jetbrains.com/idea/), but I am using [Visual Studio Code](https://code.visualstudio.com/) with the [Dart plugin](https://dartcode.org/).\n\nOpen the `lib/channel.dart` file and replace it with the following code:\n\n```dart\n    // dart_server/lib/channel.dart\n    \n    import 'package:dart_server/controller.dart';\n    import 'dart_server.dart';\n    \n    // This class sets up the controller that will handle our HTTP requests\n    class DartServerChannel extends ApplicationChannel {\n    \n      @override\n      Future prepare() async {\n        // auto generated code\n        logger.onRecord.listen((rec) =\u003e print(\"$rec ${rec.error ?? \"\"} ${rec.stackTrace ?? \"\"}\"));\n      }\n    \n      @override\n      Controller get entryPoint {\n        final router = Router();\n    \n        // We are only setting up one route. \n        // We could add more below if we had them.\n        // A route refers to the path portion of the URL.\n        router\n          .route('/[:id]') // the root path with an optional id variable\n          .link(() =\u003e MyController()); // requests are forwarded to our controller\n        return router;\n      }\n    }\n```\n\nIn the comments I talked about a controller. Let's make that now. Create a file called `controller.dart` in the `lib/` directory. Paste in the code below to handle HTTP requests:\n\n```dart\n    // dart_server/lib/controller.dart\n    \n    import 'dart:async';\n    import 'dart:io';\n    import 'package:aqueduct/aqueduct.dart';\n    \n    // using a list to simulate a database for demonstration purposes\n    List\u003cMap\u003cString, dynamic\u003e\u003e mockDatabase = [\n        {\n            'fruit': 'apple',\n            'color': 'red'\n        },\n        {\n            'fruit': 'banana',\n            'color': 'yellow'\n        }\n    ];\n    \n    class MyController extends ResourceController {\n    \n      // Handle GET (all) request\n      @Operation.get()\n      Future\u003cResponse\u003e getAllFruit() async {\n        // return the whole list\n        return Response.ok(mockDatabase);\n      }\n    \n      // Handle GET (one) request\n      @Operation.get('id')\n      Future\u003cResponse\u003e getFruitByID(@Bind.path('id') int id) async {\n        // error checking\n        if (id \u003c 0 || id \u003e= mockDatabase.length){\n          return Response.notFound(body: 'Item not found');\n        }\n        // return json for item at id\n        return Response.ok(mockDatabase[id]);\n      }\n    \n      // Handle POST request\n      @Operation.post()\n      Future\u003cResponse\u003e addFruit() async {\n        // get json from request\n        final Map\u003cString, dynamic\u003e item = await request.body.decode(); // TODO validate\n        // create item (TODO return error status code if there was a problem)\n        mockDatabase.add(item);\n        // report back to client\n        final int id = mockDatabase.length - 1;\n        return Response.ok('Item added with id $id');\n      }\n    \n      // Handle PUT request\n      @Operation.put('id')\n      Future\u003cResponse\u003e putContent(@Bind.path('id') int id) async {\n        // error checking\n        if (id \u003c 0 || id \u003e= mockDatabase.length){\n          return Response.notFound(body: 'Item not found');\n        }\n        // get the updated item from the client\n        final Map\u003cString, dynamic\u003e item = await request.body.decode(); // TODO validate\n        // make the update\n        mockDatabase[id] = item;\n        // report back to the client\n        return Response.ok('Item replaced at id $id');\n      }\n    \n      // Handle PATCH request \n      // (PATCH does not have its own @Operation method so \n      // the constructor parameter is used.)\n      @Operation('PATCH', 'id')\n      Future\u003cResponse\u003e patchContent(@Bind.path('id') int id) async {\n        // error checking\n        if (id \u003c 0 || id \u003e= mockDatabase.length){\n          return Response.notFound(body: 'Item not found');\n        }\n        // get the updated item from the client\n        final Map\u003cString, dynamic\u003e item = await request.body.decode(); // TODO validate\n        // make the partial update\n        mockDatabase\\[id\\]['color'] = item['color'];\n        // report back to the client\n        return Response.ok('Item updated at id $id');\n      }\n    \n      // Handle DELETE request \n      @Operation.delete('id')\n      Future\u003cResponse\u003e deleteContent(@Bind.path('id') int id) async {\n        // error checking\n        if (id \u003c 0 || id \u003e= mockDatabase.length){\n          return Response.notFound(body: 'Item not found');\n        }\n        // do the delete\n        mockDatabase.removeAt(id);\n        // report back to the client\n        return Response.ok('Item deleted at id $id');\n      }\n    }\n```\n\nSave your changes.\n\n### Testing the server\n\nMake sure you are inside the root of your project folder:\n\n```\n    cd dart_server\n```\n\nNormally you would start the server in the terminal like this:\n\n```\n    aqueduct serve\n```\n\nHowever, Aqueduct defaults to listening on port 8888. In all of the client apps and Node.js we are using port 3000, so let’s do that here, too. I'm also limiting the number of server instances (aka isolates) to one. This is only because we are using a mock database. For your production server with a real database, you can let the server choose the number of isolates to run. So start the server like this:\n\n```\n    aqueduct serve --port 3000 --isolates 1\n```\n\nThe server is now running on your machine. You can use [Postman](https://www.getpostman.com/) (see [docs](https://learning.getpostman.com/getting-started/) and [tutorial](https://www.youtube.com/watch?v=t5n07Ybz7yI)) to test the server now, or you can use one of the client apps that we will make in part three.\n\n![backend-mobile-2-3](//images.ctfassets.net/1es3ne0caaid/6OyYdQ0ng1ea5noxxMedcy/eb43f52374be3179130f22b48797b11a/backend-mobile-2-3.gif)\n\n### Supplemental code\n\nThe following is a model class that includes code to do JSON conversions. I’m including it here for your reference, but you don’t need to do anything with it today. For more help converting JSON to objects in Dart see [this post](https://stackoverflow.com/a/54657953).\n\n```dart\n    class Fruit {\n    \n      Fruit(this.fruit, this.color);\n    \n      // named constructor\n      Fruit.fromJson(Map\u003cString, dynamic\u003e json)\n          : fruit = json['fruit'] as String,\n            color = json['color'] as String;\n    \n      int id;\n      String fruit;\n      String color;\n    \n      // method\n      Map\u003cString, dynamic\u003e toJson() {\n        return {\n          'fruit': fruit,\n          'color': color,\n        };\n      }\n    }\n```\n\n### Further study\n\n\n- [Build RESTful Web APIs with Dart, Aqueduct and PostgreSQL #2: Routing and Controllers](https://youtu.be/K4EqGjMjCY0)\n- [Dart: First API And Create Controller | 1/7 | Aqueduct | Backend Course](https://youtu.be/XCOYxjoJDb8)\n\n\n## Conclusion\n\nIn this tutorial, we saw two ways to create a backend server. Although the details were somewhat different, the REST API that we implemented was exactly the same. If you don’t like Node.js or Server Side Dart, there are many more to choose from. (I was playing around with [Server Side Swift](https://stackoverflow.com/questions/47958172/server-side-swift-basic-client-server-communication-example-for-ios-and-android) for a while before I decided to pursue Flutter for the frontend.) Whatever server technology you choose, just implement the REST API that we used here.\n\nYou can find the server code for this lesson on [GitHub](https://github.com/suragch/backend_demo).\n\nIn the [last part](https://pusher.com/tutorials/backend-developer-part-3) of this tutorial we will learn how to make Android, iOS, and Flutter client apps that are able to communicate with the servers that we made in this lesson.","metaTitle":"Becoming a backend developer - Part 2: Building the server","metaDescription":"This is a guide for mobile app developers. In this three-part series we will cover all the basics of what it takes to become a backend developer. Create backends using Node and Dart, with mobile frontend examples using Android, Swift and Flutter. In part two, build backends with Node and Dart."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4jn7xUIcCX451DvTdSWgjO","type":"Entry","createdAt":"2019-03-21T16:19:33.774Z","updatedAt":"2019-03-21T16:19:33.774Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Creating a Laravel logger - Part 2: Creating a Pusher logger package","url":"laravel-logger-part-2","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/logger","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"644GONySFGieg4wAUc8k0C","type":"Entry","createdAt":"2018-02-12T14:37:53.963Z","updatedAt":"2019-08-28T08:41:31.723Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":4,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Beams","url":"beams","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3EB32t4KaI0IGIEI6AocoA","type":"Entry","createdAt":"2018-02-20T16:52:11.196Z","updatedAt":"2018-02-26T12:31:10.440Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Android","url":"android","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SxOuediAgcSuiWyUmkw40","type":"Entry","createdAt":"2018-02-12T15:09:21.130Z","updatedAt":"2018-09-05T15:44:43.776Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Beams","url":"push-notifications","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4xVQOGkGOQOquAGeo0y4gA","type":"Entry","createdAt":"2018-02-12T14:45:31.260Z","updatedAt":"2018-02-26T12:31:10.439Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Laravel","url":"laravel","type":"framework","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"14BNsUS2NWuSmyuwYkg2I4","type":"Entry","createdAt":"2018-02-12T14:49:45.502Z","updatedAt":"2018-02-12T14:49:45.502Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"PHP","url":"php","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2tOJiaXqzKIqosUKm22cMk","type":"Entry","createdAt":"2018-02-12T14:43:50.931Z","updatedAt":"2018-02-12T14:43:50.931Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Vue.js","url":"vue-js","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"54VH6KuDAsYY80KIeQE0OO","type":"Entry","createdAt":"2018-02-12T14:50:20.551Z","updatedAt":"2018-02-26T12:31:15.549Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Kotlin","url":"kotlin","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}}],"reviewDate":"2019-03-21","description":"In this six-part series, build a Laravel error logging service. You will create a custom Laravel package to include in Laravel apps, along with web, Android and iOS frontends. Alerts will publish in realtime, featuring push notifications to mobile. In part two, create the logger package.","prerequisite":"For this part of the series, you will need PHP 7.13+, Laravel 5.7+ and Composer installed on your machine.","content":"In this tutorial, we will build a Laravel package from scratch to help us dispatch logs to Pusher. All logs will be sent to Pusher Channels but error logs will be sent both to [Pusher Channels](https://pusher.com/channels) and [Pusher Beams](https://pusher.com/beams). Let’s dig in!\n\nThis is the second part of our six-part series on building a logging monitoring system. In the [first part](https://pusher.com/tutorials/laravel-logger-part-1), we created the shell for the Laravel application. The application has the UI that will help us manually dispatch logs.\n\n## Requirements\n\nTo follow along with this series you need the following things:\n\n\n- Completed previous parts of the series.\n- Laravel installed on your local machine. [Installation guide](https://laravel.com/docs/5.7/installation).\n- Knowledge of PHP and the Laravel framework.\n- Composer installed on your local machine. [Installation guide](https://getcomposer.org/doc/00-intro.md#installation-linux-unix-macos).\n- [Android Studio](https://developer.android.com/studio/install) \u003e= 3.x installed on your machine (If you are building for Android).\n- Knowledge of Kotlin and the Android Studio IDE.\n- [Xcode](https://developer.apple.com/xcode/) \u003e= 10.x installed on your machine (If you are building for iOS).\n- Knowledge of the Swift programming language and the Xcode IDE.\n\n\n## Creating our Laravel package\n\nThe first thing we will do is create a new folder to store the package. To do this `cd` to the project you started from the first part and create a new `packages` folder by running this on your terminal:\n\n```\n    $ mkdir packages\n```\n\nThis creates a new folder named `packages`. Next, we will create the main package folder based on the name of our package. Our package will be called `PusherLogger`. Create the folder like this:\n\n```\n    $ cd packages\n    $ mkdir PusherLogger\n```\n\nAfter creating the folder, we will start adding files to the folder. The first file we need is the `composer.json` file. This file will contain information about our package like the name, description, dependencies, and other properties. To generate this file, `cd` to the `PusherLogger` directory and run this command:\n\n```\n    $ composer init\n```\n\nThis initiates the composer config generator that will request information about your package. Follow the wizard to provide information about your package. At the end of it, your `composer.json` file should look similar to this:\n\n```json\n    {\n        \"name\": \"package/pusher-logger\",\n        \"description\": \"A package to distribute logs to Pusher\",\n        \"type\": \"library\",\n        \"authors\": [\n            {\n                \"name\": \"Neo Ighodaro\",\n                \"email\": \"neo@creativitykills.co\"\n            }\n        ],\n        \"require\": {}\n    }\n```\n\n\u003e You should change `package` in the `name` property to your own name.\n\nNext, we will add dependencies needed for package. We will add it to the the `require` object of the `composer.json` file. Add the dependencies like so:\n\n```json\n    {\n        // [...]\n        \n        \"require\": {\n          \"php\": \"\u003e=7.1.3\",\n          \"illuminate/support\": \"~5\",\n          \"monolog/monolog\": \"^1.24.0\",\n          \"pusher/pusher-php-server\": \"^3.2\",\n          \"pusher/pusher-push-notifications\": \"^1.0\"\n        }\n    }\n```\n\nFor this package, we require PHP `7.1.3`  and up, the Pusher channel package, and Pusher push notifications package to help us broadcast the logs to Pusher.\n\nNext, let’s instruct the package where it should load the files from. Add the snippet below to the `composer.json` file:\n\n```json\n    {\n        // [...]\n        \n        \"autoload\": {\n            \"psr-4\": {\n                \"PackageNamespace\\\\PusherLogger\\\\\": \"src/\"\n            }\n        },\n        \"autoload-dev\": {\n            \"psr-4\": {\n                \"PackageNamespace\\\\PusherLogger\\\\Tests\\\\\": \"tests\"\n            }\n        },\n        \"extra\": {\n            \"laravel\": {\n                \"providers\": [\n                    \"PackageNamespace\\\\PusherLogger\\\\PusherLoggerServiceProvider\"\n                ],\n                \"aliases\": {\n                    \"PusherLogger\": \"PackageNamespace\\\\PusherLogger\\\\PusherLogger\"\n                }\n            }\n        }\n    }\n```\n\n\u003e You can use a different camelcase namespace from `PackageNamespace` if you wish. Just remember to replace the namespace everywhere you changed it below.\n\nNow we have instructed Composer’s autoloader how to load files from a certain namespace. This tells the package to look out for the `/src` directory for the package files. This directory is not available yet so create the folder in your `PusherLogger` folder. You can do that by running this command:\n\n```\n    $ mkdir src\n```\n\nNavigate to the `src` folder and create two files. First we will create the `PusherLoggerServiceProvider.php` file and paste the following into it:\n\n```php\n    \u003c?php // File: ./src/PusherLoggerServiceProvider.php\n    \n    namespace PackageNamespace\\PusherLogger;\n    \n    use Pusher\\Pusher;\n    use Illuminate\\Support\\ServiceProvider;\n    use Pusher\\PushNotifications\\PushNotifications;\n    \n    class PusherLoggerServiceProvider extends ServiceProvider\n    {\n        /**\n         * Bootstrap the application services.\n         *\n         * @return void\n         */\n        public function boot()\n        {\n          //\n        }\n        \n        /**\n         * Register the application services.\n         *\n         * @return void\n         */\n        public function register()\n        {\n          //\n        }\n    }\n```\n\n\u003e You can replace `PackageNamespace` with your own namespace.\n\n\n\u003e Service providers are the central place of all Laravel application bootstrapping. Your own application, as well as all of Laravel's core services are bootstrapped via service providers. - [Laravel documentation](https://laravel.com/docs/5.7/providers)\n\nThis class extends the `Illuminate\\Support\\ServiceProvider` class. Our class contains two methods - `register` and `boot`. The `boot` method loads event listeners, routes, or any other piece of functionality while the `register` method only bind things into the service container.\n\nInside a service provider class, the `app` container can be accessed via the `$app` property. So in our `PusherLoggerServiceProvider` class we will bind an alias `pusher-logger` to the `PusherLogger` class. Update the `register` method like this:\n\n```php\n    \u003c?php // File: ./src/PusherLoggerServiceProvider.php\n    \n    // [...]\n    \n    class PusherLoggerServiceProvider extends ServiceProvider\n    {\n      // [...]\n      \n      public function register()\n      {\n          $this-\u003eapp-\u003ebind('pusher-logger', function () {\n              $config = config('broadcasting.connections.pusher');\n              \n              $key = $config['key'] ?? '';\n              $secret = $config['secret'] ?? '';\n              $app_id = $config['app_id'] ?? '';\n              \n              $pusher = new Pusher($key, $secret, $app_id, [\n                  'useTLS' =\u003e true,\n                  'encrypted' =\u003e true,\n                  'cluster' =\u003e $config\\['options'\\]['cluster'] ?? '',\n              ]);\n              \n              $beams = new PushNotifications([\n                  'secretKey' =\u003e $config\\['beams'\\]['secret_key'] ?? '',\n                  'instanceId' =\u003e $config\\['beams'\\]['instance_id'] ?? '',\n              ]);\n              \n              return new PusherLogger($pusher, $beams);\n          });\n      }\n    }\n```\n  \nAbove, we are binding `pusher-logger` to the `Closure` above. Inside the `Closure`, we are registering an instance of a `PusherLogger` class, which we will create later. This class receives an instance of a configured `Pusher` object, and a configured `PushNotifications` object. Since we are using Laravel’s [service container](https://laravel.com/docs/5.7/container#binding), it means anytime we try to use the `pusher-logger` service, we will get a `PusherLogger` instance with both Pusher and Push Notifications configured.\n\nNext, let us create our second class. The class will be a [Facade](https://laravel.com/docs/5.7/facades). A Facade is one of the architecture concepts Laravel provides. It is a static interface to classes that are available in the application's service container, meaning that our Facade classes represent another class bound in the service container.\n\nTo create this class, first make a directory named `Facades` in the `src` directory and then create the `PusherLogger.php` file inside it. When you have created the file, paste the following code into the file:\n\n```php\n    \u003c?php // File: ./src/Facades/PusherLogger.php\n    \n    namespace PackageNamespace\\PusherLogger\\Facades;\n    \n    use Illuminate\\Support\\Facades\\Facade;\n    \n    class PusherLogger extends Facade\n    {\n        protected static function getFacadeAccessor()\n        {\n            return 'pusher-logger';\n        }\n    }\n```\n\nIn the `getFacadeAccessor` method of the class above, we returned `pusher-logger` which corresponds to the alias we bound to the `PusherLogger` class earlier in the service provider.\n\nNow we can use the `PusherLogger` Facade as a proxy to the original `PusherLogger` class with the package logic. Let’s create the original `PusherLogger` class. In the `src` directory, create a new file named `PusherLogger.php` and paste the following code into it:\n\n```php\n    \u003c?php // File: ./src/PusherLogger.php\n    \n    namespace PackageNamespace\\PusherLogger;\n    \n    use Pusher\\Pusher;\n    use Pusher\\PushNotifications\\PushNotifications;\n    \n    class PusherLogger\n    {\n        /**\n         * @var \\Pusher\\Pusher\n         */\n        protected $pusher;\n        \n        /**\n         * @var \\Pusher\\PushNotifications\\PushNotifications\n         */\n        protected $beams;\n        \n        /**\n         * @var string\n         */\n        protected $event;\n    \n        /**\n         * @var string\n         */\n        protected $channel;\n    \n        /**\n         * @var string\n         */\n        protected $message;\n    \n        /**\n         * @var string\n         */\n        protected $level;\n    \n        /**\n         * @var array\n         */\n        protected $interests = [];\n    \n        // Log levels\n        const LEVEL_INFO  = 'info';\n        const LEVEL_DEBUG = 'debug';\n        const LEVEL_ERROR = 'error';\n    \n        /**\n         * PusherLogger constructor.\n         *\n         * @param \\Pusher\\Pusher $pusher\n         * @param \\Pusher\\PushNotifications\\PushNotifications $beams\n         */\n        public function __construct(Pusher $pusher, PushNotifications $beams)\n        {\n            $this-\u003ebeams = $beams;\n            \n            $this-\u003epusher = $pusher;\n        }\n    }\n```\n\nIn the snippet above, we declared some variables we will use later in the class. We also declared the class constructor to receive instances of the `Pusher` object and the `PushNotifications` object just as we did in the service provider binding above. \n\nWe also have some properties and constants for the class. We can use the constants outside the class when specifying the log level. This would make it easy to change the values later on if we wanted to.\n\nIn the same class, let’s define a some methods, which will be how we will set the other `protected` class properties. In the same file, paste the following code:\n\n```php\n    // File: ./src/PusherLogger.php\n    // [...]\n    \n    /**\n     * Sets the log message.\n     *\n     * @param  string $message\n     * @return self\n     */\n    public function setMessage(string $message): self\n    {\n        $this-\u003emessage = $message;\n        \n        return $this;\n    }\n    \n    /**\n     * Sets the log level.\n     *\n     * @param  string $level\n     * @return self\n     */\n    public function setLevel(string $level): self\n    {\n        $this-\u003elevel = $level;\n        \n        return $this;\n    }\n    \n    /**\n     * Sets the Pusher channel.\n     *\n     * @param  string $channel\n     * @return self\n     */\n    public function setChannel(string $channel): self\n    {\n        $this-\u003echannel = $channel;\n    \n        return $this;\n    }\n    \n    /**\n     * Sets the event name.\n     *\n     * @param  string $event\n     * @return self\n     */\n    public function setEvent(string $event): self\n    {\n        $this-\u003eevent = $event;\n    \n        return $this;\n    }\n    \n    /**\n     * Set the interests for Push notifications.\n     *\n     * @param  array $interests\n     * @return self\n     */\n    public function setInterests(array $interests): self\n    {\n        $this-\u003einterests = $interests;\n    \n        return $this;\n    }\n    \n    // [...]\n```\n\nAbove, we have defined some similar methods. They just set the corresponding `protected`  class properties and then return the class instance so they are chainable.\n\nNext, we will add other helper methods to be used in the package. Add the following methods to the `PusherLogger.php` class:\n\n```php\n    // File: ./src/PusherLogger.php\n    // [...]\n    \n    /**\n     * Quickly log a message.\n     *\n     * @param string $message\n     * @param string $level\n     * @return self\n     */\n    public static function log(string $message, string $level): self\n    {\n        return app('pusher-logger')\n            -\u003esetMessage($message)\n            -\u003esetLevel($level);\n    }\n    \n    /**\n     * Dispatch a log message.\n     *\n     * @return bool\n     */\n    public function send(): bool\n    {\n        $this-\u003epusher-\u003etrigger($this-\u003echannel, $this-\u003eevent, $this-\u003etoPushHttp());\n        \n        if ($this-\u003elevel === static::LEVEL_ERROR) {\n            $this-\u003ebeams-\u003epublishToInterests($this-\u003einterests, $this-\u003etoPushBeam());\n        }\n    \n        return true;\n    }\n    \n    // [...]\n```\n\nThe first method is a quick shorthand we can use when dispatching log messages and the second method is the method that dispatches log messages to the Pusher clients. In the `send` method, we are checking to see if the log level is an error level. If it is, we will also send a push notification so the administrator can be aware that an error has occurred.\n\nWhen creating a log, we need to set the channel, events and interests (when using Pusher Beams) in which the log would be sent to. Here’s an example of how we can use the logger:\n\n```php\n    use PackageNamespace\\PusherLogger\\PusherLogger;\n    \n    PusherLogger::log('Winter is Coming', PusherLogger::LEVEL_WARNING)\n            -\u003esetEvent('log-event')\n            -\u003esetChannel('log-channel')\n            -\u003esetInterests(['log-interest'])\n            -\u003esend()\n```\n\nThe final function in the snippet is the function that sends the data to Pusher. In the function, all logs are sent to Pusher Channels, but error logs are also sent to Pusher Beams so that the client can receive a notification.\n\nWhile defining the `send` function, we used two new methods that composes the data to be sent to Pusher Channels and Pusher Beams respectively. Add the methods to the same class like so:\n\n```php\n    // File: ./src/PusherLogger.php\n    // [...]\n    \n    public function toPushHttp()\n     {\n         return [\n             'title' =\u003e 'PusherLogger' . ' '. ucwords($this-\u003elevel),\n             'message' =\u003e $this-\u003emessage,\n             'loglevel' =\u003e $this-\u003elevel\n         ];\n     }\n    \n     public function toPushBeam()\n     {\n         return [\n             'apns' =\u003e [\n                 'aps' =\u003e [\n                     'alert' =\u003e [\n                         'title' =\u003e 'PusherLogger' . ' '. ucwords($this-\u003elevel),\n                         'body' =\u003e $this-\u003emessage,\n                         'loglevel' =\u003e $this-\u003elevel\n                     ],\n                 ],\n             ],\n             'fcm' =\u003e [\n                 'notification' =\u003e [\n                     'title' =\u003e 'PusherLogger' . ' '. ucwords($this-\u003elevel),\n                     'body' =\u003e $this-\u003emessage,\n                     'loglevel' =\u003e $this-\u003elevel\n                 ],\n             ],\n         ];\n    }\n```\n\n## Creating a log handler\n\nLaravel uses [Monolog](https://github.com/Seldaek/monolog), which is a powerful logging package for PHP. We can [create custom handlers](https://laravel.com/docs/5.7/logging#creating-monolog-handler-channels) for Monolog and so let’s do one that will be for our Pusher logger.\n\nCreate a new file in the `src`  directory of the package called `PusherLoggerHandler.php` and paste the following code:\n\n```php\n    \u003c?php // File: ./src/PusherLoggerHandler.php\n    \n    namespace PackageNamespace\\PusherLogger;\n    \n    use Monolog\\Logger;\n    use Monolog\\Handler\\AbstractProcessingHandler;\n    \n    class PusherLoggerHandler extends AbstractProcessingHandler\n    {\n        protected function write(array $record): void\n        {\n            $level = strtolower(Logger::getLevelName($record['level']));\n    \n            PusherLogger::log($record['message'], $level)\n                -\u003esetEvent('log-event')\n                -\u003esetChannel('log-channel')\n                -\u003esetInterests(['log-interest'])\n                -\u003esend();\n        }\n    }\n```\n\nAbove, we have the custom handler that will be hooked into our Laravel Monolog instance. When we do, logs will be automatically pushed to our Pusher application as needed. We will do that in the next part. \n\nThat’s all.\n\n\n## Conclusion\n\nIn this part of the series, we have been able to set up the logic we need to be able to push logs to Pusher. In the [next part](https://pusher.com/tutorials/laravel-logger-part-3) of the series, we will integrate this package with our Laravel application and see how everything will work together.\n\nThe source code is available on [GitHub](https://github.com/neoighodaro/logger). ","metaTitle":"Creating a Laravel logger - Part 2","metaDescription":"In this six-part series, build a Laravel error logging service. You will create a custom Laravel package to include in Laravel apps, along with web, Android and iOS frontends. Alerts will publish in realtime, featuring push notifications to mobile. In part two, create the logger package."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"QRNM2nbB5h8g4zlBUYGaR","type":"Entry","createdAt":"2019-03-19T17:36:04.012Z","updatedAt":"2020-06-15T16:02:14.641Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Becoming a backend developer - Part 1: Foundational concepts","url":"backend-developer-part-1","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"58WBWjhcWcuAMI6qIAyiC4","type":"Entry","createdAt":"2018-12-13T20:46:00.073Z","updatedAt":"2018-12-13T20:46:36.867Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Suragch","url":"suragch","email":"studymongolian@gmail.com","twitter":"https://twitter.com/suragch","github":"https://github.com/suragch","guestWriter":true}}],"projectRepo":"https://github.com/suragch/backend_demo","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SdjzfMUtikUcK2CWI8uyC","type":"Entry","createdAt":"2018-07-31T17:19:35.290Z","updatedAt":"2019-08-28T08:41:23.854Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"no pusher tech","url":"no-pusher-tech","featured":false}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3EB32t4KaI0IGIEI6AocoA","type":"Entry","createdAt":"2018-02-20T16:52:11.196Z","updatedAt":"2018-02-26T12:31:10.440Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Android","url":"android","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3Q3oRmiXjOmcSmweY4gKY8","type":"Entry","createdAt":"2018-09-12T09:33:28.375Z","updatedAt":"2018-09-12T09:33:28.375Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Flutter","url":"flutter","type":"framework","featured":false}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"54VH6KuDAsYY80KIeQE0OO","type":"Entry","createdAt":"2018-02-12T14:50:20.551Z","updatedAt":"2018-02-26T12:31:15.549Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Kotlin","url":"kotlin","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3WH6Mz2j7iyIaqCeOKIIkQ","type":"Entry","createdAt":"2018-02-12T14:44:51.150Z","updatedAt":"2018-02-12T14:44:51.150Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Node.js","url":"node-js","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1st5ugwNDbvlnFFHBadh5b","type":"Entry","createdAt":"2019-03-13T18:08:14.919Z","updatedAt":"2019-03-13T18:08:14.920Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Dart","url":"dart","type":"language"}}],"reviewDate":"2019-03-19","description":"This is a tutorial series for mobile app developers. In this three-part series, we will cover all the basics of what it takes to become a backend developer. You will create backends using Node and Dart, with mobile frontend examples using Android, Swift and Flutter. In part one, cover key concepts around APIs.","prerequisite":"You need experience in at least one of Android, iOS or Flutter development.","content":"This is a guide for mobile app developers. In this three-part series we will cover all the basics of what it takes to become a backend developer.\n\n\n- Part 1: Foundational concepts\n- Part 2: Building the server\n- Part 3: Connecting to the server from a mobile app\n\n\n## Introduction\n\nSo you have some experience developing mobile apps for Android or iOS. You may even have a few apps on Google Play or in the App Store. More and more, though, you are getting ideas for new apps that would require a server backend. The problem is that you don't know how to do server programming. Maybe you work for a small company or maybe you’re on your own, and you don't have the resources to hire a backend developer. Now you've decided that it's finally time to take the leap and learn how to do it yourself. If that's you, then you've come to the right place.\n\nIn this series I will take you through everything you need to know to get started building a backend server and connecting it to your mobile apps. There are a myriad of choices of platforms and frameworks for both the backend and frontend. I will show you how to build two backend servers (Node.js and Server-Side Dart) and three frontend apps (Android, iOS, and Flutter). Choose one backend server and one frontend app to follow along with. \n\nThe purpose of telling you how to do the same thing on different platforms is so that you can see that the process is basically the same no matter what you choose. Even if you decide on a different technology, you can still follow along and just Google how to adapt it to your particular situation.\n\nNo matter what platforms you choose for your backend and frontend, there are a few foundational topics related to client-server communication that are important to know. They include URIs, HTTP, REST APIs, and JSON. If you are already familiar with these topics, feel free to skip directly to Part 2: Building the server.\n\n\n## URI\n\nA URI (Uniform Resource Identifier) is an address for a resource. A resource, for example, could be a webpage or a file or a database row. A specific type of URI is a URL. (See [difference between URI, URL, and URN](https://stackoverflow.com/questions/176264/what-is-the-difference-between-a-uri-a-url-and-a-urn).) You’re probably familiar with URIs from your mobile app development and certainly familiar with URLs from web browsers, but you might not be aware of their components, especially the query string.\n\n![backend-mobile-1-1](//images.ctfassets.net/1es3ne0caaid/5GMcbCR4mwdfYzuWPd1v8Z/89f52b49dc6a2d01b0d676bafa2b7e6d/backend-mobile-1-1.png)\n\nThe query string starts with a `?` question mark and is followed by parameters. The parameters are separated by `\u0026` ampersands. So in the example above we have two parameters:\n\n\n- `q = flutter`\n- `language = en`\n\nSee [this page](https://doepud.co.uk/blog/anatomy-of-a-url) for more explanation about the parts of a URI. \n\n### URI example\n\nWhen our client app accesses resources on our server, it will use a URI to reference them. Here is an actual example that we will use:\n\n```\n    http://localhost:3000/0\n```\n\nI’ll talk about the `http` protocol part of the URI in the next section. We’ll be using `localhost` (that is, `127.0.0.1`) to refer to our own machine, where the server will be running. This will simplify the development process. Then we will (somewhat arbitrarily) choose port `3000` to communicate over. Our path is simply the root `/`. We could have used something like `/fruit` or `/items`. In fact, a more complex server app would probably define several different paths (also known as routes) that clients could connect to to access different types of resources. In our minimal server, though, we’ll just use one path. The final `0` at the end of the path will be used as a parameter to refer to the ID of a database row. (Actually for us it will refer to the index of an array that is pretending to be a database, but the client doesn’t have to know that.)\n\n## HTTP\n\nHTTP (Hypertext Transfer Protocol) is a set of rules for how to transfer data on the web. You've seen `http://` in web addresses, of course. Now you’re going to use the same protocol to transfer data between your app and the server.\n\n### Verbs\n\nThe primary HTTP verbs that we are going to be using are:\n\n\n- `GET` - for requesting data from the server\n- `POST` - for sending new data to the server\n- `PUT` - for updating data on the server (fully replace an existing resource)\n- `PATCH` - for updating data on the server (partially modify an existing resource)\n- `DELETE` - for deleting data on the server\n\nYou will note that these are very similar to the CRUD operations of a database:\n\n\n- Create -\u003e `POST`\n- Read -\u003e `GET`\n- Update -\u003e `PUT` or `PATCH`\n- Delete -\u003e `DELETE`\n\nThese HTTP verbs are sent by clients to the server. This is known as a **request**. After the server receives a request it processes it and then sends back a **response** to the client. Both the request and the response contain metadata known as **headers**. The primary data itself is sent in the **body**. \n\n### Status codes\n\nOne of the headers is the status code. When a client makes an HTTP request, the server should send back an HTTP status code in its response. This lets the client know if the request was successful or not. You’ve seen the 404 Not Found response whenever you click on a broken link. Here are some more that you should get familiar with:\n\n\n- 1xx Informational\n- 2xx Success\n\n      - 200 OK\n      - 201 Created\n\n- 3xx Redirection\n\n      - 301 Moved Permanently\n\n- 4xx Client Error\n\n      - 400 Bad Request\n      - 401 Unauthorized\n      - 403 Forbidden\n      - 404 Not Found\n\n- 5xx Server Error \n\n      - 500 Internal Server Error\n      - 502 Bad Gateway\n\nIt’s your responsibility as a backend developer to choose the right code to add in your response to a client request. You can read more about HTTP status codes [here](https://www.restapitutorial.com/httpstatuscodes.html) and [here](https://restfulapi.net/http-status-codes/). \n\n### Content type\n\nAnother header is `Content-Type`, which is the Media Type (formerly known as MIME type). This tells us the type of data that is in the body. It could be plain text or HTML or JSON. Here are a few examples as they look encoded:\n\n\n- `application/json`\n- `application/x-www-form-urlencoded`\n- `image/png`\n- `text/plain`\n- `text/html`\n\nHere is a [complete list](https://www.iana.org/assignments/media-types/media-types.xhtml). We will be using JSON ( `application/json` ) primarily. I’ll explain it more in a little while.\n\n### Examples\n\nLet’s look at a few examples of the raw text of some HTTP requests. The following is a GET request:\n\n```\n    GET 3000/0\n    Content-Type: application/json\n    cache-control: no-cache\n    Postman-Token: 00572d5b-a2ec-4195-8436-ef1e797c0751\n    User-Agent: PostmanRuntime/7.6.0\n    Accept: */*\n    Host: localhost:3000\n    accept-encoding: gzip, deflate\n```\n\nNotice that it starts with the HTTP verb and URI path. The rest of the lines here are headers. I used [Postman](https://www.getpostman.com/) to make the request so that I could get the raw text. \n\nThis is the response that the server gave to that GET request:\n\n```\n    HTTP/1.1 200\n    status: 200\n    X-Powered-By: Express\n    Content-Type: application/json; charset=utf-8\n    Content-Length: 31\n    ETag: W/\"1f-xulzIcqO6GqXGwKlplM3A8YgRg8\"\n    Date: Fri, 08 Mar 2019 19:30:16 GMT\n    Connection: keep-alive\n    \n    {\"fruit\":\"apple\",\"color\":\"red\"}\n```\n\nYou can see that the status code is 200. Everything is OK. The content type of the body is JSON. You can find the body at the end, separated from the headers by a single blank line.\n\nHere is a POST request:\n\n```\n    POST 3000\n    Content-Type: application/json\n    cache-control: no-cache\n    Postman-Token: 6b985c28-ef59-4dac-930c-63b3245de3f1\n    User-Agent: PostmanRuntime/7.6.0\n    Accept: */*\n    Host: localhost:3000\n    accept-encoding: gzip, deflate\n    content-length: 46\n    \n    { \"fruit\": \"kiwi\", \"color\": \"purple\" }\n```\n\nAnd here is the response from the server:\n\n```\n    HTTP/1.1 200\n    status: 200\n    X-Powered-By: Express\n    Content-Type: text/html; charset=utf-8\n    Content-Length: 20\n    ETag: W/\"14-gSUAeNN7vCHjCoiKRj0ziSuiiQI\"\n    Date: Fri, 08 Mar 2019 19:34:18 GMT\n    Connection: keep-alive\n    \n    Item added with id 2\n```\n\nYou don’t need to understand the details of all that raw text. The frameworks that we are going to use to interact with the HTTP protocol will hide most of it from us. I just share these examples with you to let you know what is happening under the hood. If you do need to get at the headers, though, you will be able to access them from within the framework.\n\n\n## REST API\n\n### REST\n\nWhen we do our client-server communication, we will be using what is called a REST (or RESTful) API. (REST stands for Representational State Transfer, but I’ve never found it very helpful to know that.) In addition to using the HTTP protocol, REST adds some additional rules for how the client and the server should interact. As a beginner, remember these important REST principles:\n\n\n- The server should not save any state about a client.\n- Only use the HTTP verbs as they are defined. For example, while it is technically possible to create a resource on the server by sending the data through a GET request, REST says we should use a POST request for creating new resources.\n\nThere is a lot more to REST, but you don't need to understand everything right now. Check out [this](https://www.youtube.com/watch?v=Q-BpqyOT3a8) and [this](https://www.youtube.com/watch?v=0oXYLzuucwE\u0026index=1\u0026list=PL55RiY5tL51q4D-B63KBnygU6opNPFk_q) if you are interested.\n\n### API\n\nThe API is the set of methods that we will handle on the server. It’s our own personal protocol for communicating between the client and the server. Once we have our API defined, it doesn't matter what is running on the server or who the clients are. This is a prime example of [clean architecture](https://pusher.com/tutorials/clean-architecture-introduction).\n\n![backend-mobile-1-2](//images.ctfassets.net/1es3ne0caaid/5fFYkzDLBS2G0hqW7s8kII/cac6226ef0211c8b4c10e6234705866e/backend-mobile-1-2.png)\n\n### Our API\n\nThe REST API is defined in terms of HTTP verbs ( `GET`, `POST`, `PUT`, etc.) along with a URI path. This is the API that we will use for our server and clients in the second and third parts of this series:\n\n```\n    GET http://localhost:3000/              // get all items\n    GET http://localhost:3000/id            // get item at id\n    POST http://localhost:3000/             // create new item\n    PUT http://localhost:3000/id            // replace item at id\n    PATCH http://localhost:3000/id          // update item at id\n    DELETE http://localhost:3000/id         // delete item at id\n```\n\n## JSON\n\nWhen communicating with a server we need to pass data back and forth. This becomes more difficult when we want to pass complex objects (that is, most anything besides strings, numbers, and booleans). Since we can't pass an object directly using in the HTTP protocol, we can convert it to a string (also known as serialization), transfer it in the body of an HTTP message, and then convert it from a string back to an object.\n\nA common way to do that is with JavaScript Object Notation (JSON). We’re not limited to JavaScript, though. There are JSON libraries for Java, Kotlin, Swift, Objective-C, Dart, and many other languages.\n\n### Example\n\nJSON formats the data in a class to a list of key-value pairs. Here is what a simple User object would look like when converted to JSON:\n\n```json\n    {\n       \"id\" : 3,\n       \"name\" : \"Mary\",\n    }\n```\n\nNotes:\n\n- The curly braces represent an object.\n- The keys and any string values need double quotes. Other values don’t.\n- JSON encodes objects, arrays, strings, numbers, booleans, and nulls. \n- Objects and arrays can be nested.\n- See the [documentation](https://www.json.org/) for a full explanation.  Here are [a few more examples](https://medium.com/@studymongolian/easy-json-examples-7c70785b4707?source=friends_link\u0026sk=dbca368da83bc2273c9a6073159c8c39).\n\nWhite space does not matter. So this:\n\n```json\n    {\"fruit\":\"apple\",\"color\":\"red\"}\n```\n\nis the same as this:\n\n```json\n    {\n      \"fruit\": \"apple\",\n      \"color\": \"red\"\n    }\n```\n\n### In a model object\n\nWhen you create a model object, it’s convenient to add a constructor and helper method that can serialize and deserialize (encode and decode) between the object and JSON. For example, here is a model object in Dart. The constructor creates an object from a JSON map. And a JSON map can be created from the object using the `toJson()` method. \n\n```dart\n    class Fruit {\n    \n      int id;\n      String fruit;\n      String color;\n    \n      Fruit(this.fruit, this.color);\n    \n      // named constructor\n      Fruit.fromJson(Map\u003cString, dynamic\u003e json)\n          : fruit = json['fruit'],\n            color = json['color'];\n    \n      // method\n      Map\u003cString, dynamic\u003e toJson() {\n        return {\n          'fruit': fruit,\n          'color': color,\n        };\n      }\n    }\n```\n\nAs I said, all of the platforms and languages have libraries for converting between a raw JSON string (which is what you have in the HTTP body) and a Map or Dictionary or whatever key-value data structure is used in your language.\n\n\n## Conclusion\n\nYou now have all of the basic background knowledge you need to start building your backend server. In part two I’ll show you how to make the server, and in part three we’ll make a simple frontend app to connect to the server. These will implement the REST API that we learned about above.\n\nThe code for the servers and for the frontend apps is on [GitHub](https://github.com/suragch/backend_demo).","metaTitle":"Becoming a backend developer - Part 1: Foundational concepts","metaDescription":"This is a guide for mobile app developers. In this three-part series we will cover all the basics of what it takes to become a backend developer. You will create backends using Node and Dart, with mobile frontend examples using Android, Swift and Flutter. In part one, cover key concepts around APIs."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5xDBTJKeJXrmXfcEF1UMoh","type":"Entry","createdAt":"2019-03-19T17:31:02.421Z","updatedAt":"2019-03-19T17:31:02.421Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Creating a Laravel logger - Part 1: Getting started","url":"laravel-logger-part-1","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/logger","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"644GONySFGieg4wAUc8k0C","type":"Entry","createdAt":"2018-02-12T14:37:53.963Z","updatedAt":"2019-08-28T08:41:31.723Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":4,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Beams","url":"beams","featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SxOuediAgcSuiWyUmkw40","type":"Entry","createdAt":"2018-02-12T15:09:21.130Z","updatedAt":"2018-09-05T15:44:43.776Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Beams","url":"push-notifications","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"54VH6KuDAsYY80KIeQE0OO","type":"Entry","createdAt":"2018-02-12T14:50:20.551Z","updatedAt":"2018-02-26T12:31:15.549Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Kotlin","url":"kotlin","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4xVQOGkGOQOquAGeo0y4gA","type":"Entry","createdAt":"2018-02-12T14:45:31.260Z","updatedAt":"2018-02-26T12:31:10.439Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Laravel","url":"laravel","type":"framework","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"14BNsUS2NWuSmyuwYkg2I4","type":"Entry","createdAt":"2018-02-12T14:49:45.502Z","updatedAt":"2018-02-12T14:49:45.502Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"PHP","url":"php","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2tOJiaXqzKIqosUKm22cMk","type":"Entry","createdAt":"2018-02-12T14:43:50.931Z","updatedAt":"2018-02-12T14:43:50.931Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Vue.js","url":"vue-js","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3EB32t4KaI0IGIEI6AocoA","type":"Entry","createdAt":"2018-02-20T16:52:11.196Z","updatedAt":"2018-02-26T12:31:10.440Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Android","url":"android","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}}],"reviewDate":"2019-03-19","description":"In this six-part series, build a Laravel error logging service. You will create a custom Laravel package to include in Laravel apps, along with web, Android and iOS frontends. Alerts will publish in realtime, featuring push notifications to mobile. In part one, build the log dispatcher.","prerequisite":"For this part of the series, you will need PHP 7.13+, Laravel 5.7+ and Composer installed on your machine.","content":"In this tutorial series, we will see how we can create a simple realtime monitoring application. It will have mobile applications that will be able to see logs as they happen on your application and thus make it easier to find out what is wrong with an application and fix it.\n\nWhen building applications, logging is an essential part of diagnosing the problems with the application. Many times, applications can fail when a client is viewing the website or application. These incidents can often go unnoticed especially if it is not reported by the client, which happens a lot.\n\nMost languages and frameworks come with in-built logging systems that will help you diagnose problems with your application. However, these logs are stored on the filesystem and will require logging into your server and searching for the log messages, which is inconvenient. There are services like [BugSnag](https://bugsnag.com) and [Sentry](https://sentry.io) that offer log monitoring as a service which makes it easy to work with application logs. \n\nThe system will include a log dispatcher that will work hand in hand with a Laravel package to distribute logs to various clients (a web dashboard, an Android app, an iOS app) in realtime. For this first part, we will build the dispatcher. The app will contain a simple page with a modal that has a form. \n\nHere’s a demo of how the page will look and the modal behavior:\n\n![laravel-log-1-1](//images.ctfassets.net/1es3ne0caaid/nvHlteXI4Kbxasz7yd7aR/f17aef7a58276ce9b38b57b4ce029890/laravel-log-1-1.gif)\n\n## Requirements\n\nTo follow along with this series you need the following things:\n\n- Laravel installed on your local machine. [Installation guide](https://laravel.com/docs/5.7/installation).\n- Knowledge of PHP and the Laravel framework.\n- Composer installed on your local machine. [Installation guide](https://getcomposer.org/doc/00-intro.md#installation-linux-unix-macos).\n- [Android Studio](https://developer.android.com/studio/install) \u003e= 3.x installed on your machine (If you are building for Android).\n- Knowledge of Kotlin and the Android Studio IDE.\n- [Xcode](https://developer.apple.com/xcode/) \u003e= 10.x installed on your machine (If you are building for iOS).\n- Knowledge of the Swift programming language and the Xcode IDE.\n\n\n## Setting up \n\nLet’s start setting up our environment so we can build the logging dispatcher.\n\n### Installing PHP\n\n\u003e If you already have PHP 7.1.3 or above installed on your machine, you can skip this section.\n\nThe first step for our setup is to install PHP. This can be more complicated than usual depending on your machine. So, in order to not unnecessarily elongate this post, I will just provide explicit resources for that.\n\nTo install PHP:\n\n- For Windows users, use [this](https://www.sitepoint.com/how-to-install-php-on-windows/) guide.\n- For Ubuntu users, use [this](https://www.digitalocean.com/community/tutorials/how-to-install-linux-apache-mysql-php-lamp-stack-on-ubuntu-16-04) guide.\n- For Mac, PHP usually comes pre-installed. You can verify using this command:\n\n```\n    $ php -v\n```\n\nIf you don’t have it installed or it’s a lower version that required, you can use [this post](https://medium.com/@romaninsh/install-php-7-2-xdebug-on-macos-high-sierra-with-homebrew-july-2018-d7968fe7e8b8) as a guide to installing PHP on your Mac.\n\n### Installing Composer and the Laravel installer\n\n\u003e If you have already installed the Laravel CLI, you can skip this part entirely.\n\nNext, let’s install Laravel. Laravel utilizes Composer to manage its dependencies so, before using Laravel, we have to make sure Composer is installed. If you don’t have it, you can follow the following steps to get it installed:\n\nRun this command in your terminal to download the composer installer:\n\n```\n    // For Mac\n    $ php -r \"copy('https://getcomposer.org/installer', 'composer-setup.php');\"\n    \n    // For Linux\n    $ curl -sS https://getcomposer.org/installer -o composer-setup.php\n```\n\n\u003e If you are using a Windows machine, you can use the composer setup file [here](https://getcomposer.org/Composer-Setup.exe).\n\nNext, run this command to verify your downloaded file:\n\n```\n    $ php -r \"if (hash_file('sha384', 'composer-setup.php') === '48e3236262b34d30969dca3c37281b3b4bbe3221bda826ac6a9a62d6444cdb0dcd0615698a5cbe587c3f0fe57a54d8f5') { echo 'Installer verified'; } else { echo 'Installer corrupt'; unlink('composer-setup.php'); } echo PHP_EOL;\"\n```\n\nFrom the snippet above, you are verifying the hash of the downloaded installer. If the hash matches the expected hash from Composer. You can find the current Composer hash [here](https://composer.github.io/pubkeys.html).\n\nNext, let’s install composer globally so we can run the command from anywhere. To do this, run the following command on your terminal:\n\n```\n    $ php composer-setup.php --install-dir=/usr/local/bin --filename=composer\n```\n\nThis command installs composer in the `/usr/local/bin` directory.\n\n\n\u003e If you are using Linux, you might want to add `sudo` before the `php` command so the command is run as the `root` user.\n\nNext, run the command below to install the Laravel installer: \n\n```\n    $ composer global require laravel/installer\n```\n\nAfter the installation is complete, you should be able to run the `laravel` command on your terminal. Try this to verify:\n\n```\n    $ laravel -V\n```\n\nIf you have installed it successfully, you would see the version of the Laravel installer currently installed on your machine. \n\n## Building our app\n\nNow that we have the Laravel installer, let’s create a new Laravel application. To create a new Laravel application, run the following command:\n\n```\n    $ laravel new logger-app\n```\n\nThis command will create a new Laravel application in a directory named `logger-app`. \n\nAlternatively, you can create a Laravel app using the composer `create-project` command like this:\n\n```\n    $ composer create-project --prefer-dist laravel/laravel logger-app\n```\n\nNow you can `cd` to your applications directory and run the following command:\n\n```\n    $ php artisan serve\n```\n\n![laravel-log-1-2](//images.ctfassets.net/1es3ne0caaid/6XYpNStMGJRjQIv7MXGusD/a9998eea0ba5f9eed21e8e1c9740aef6/laravel-log-1-2.png)\n\nThis will start a local PHP server on port 8000. Now, you can visit the URL http://localhost:8000 and you will see the Laravel welcome screen.\n\n![laravel-log-1-3](//images.ctfassets.net/1es3ne0caaid/18qfpZpAYIfVWEODINv6Qx/537dbb83900ea8dc6312e5e6ad9b4ea2/laravel-log-1-3.png)\n\nThe page rendered here is the default view of newly created apps. This route is registered in the `routes/web.php`  file. If you look at the single route there, you should see that it loads the `welcome.blade.php` view. All views are in the `/resources/views/` directory. For the sake of brevity, let’s update this view instead of creating a new one.\n\nFirst, we need to import the app CSS file, add the link to the file in the `\u003chead\u003e` tag like so:\n\n```php\n    \u003c!-- File: ./resources/views/welcome.blade.php --\u003e\n    \u003c!-- [...] --\u003e\n    \u003chead\u003e\n        \u003c!-- Leave existing styles --\u003e\n        \u003c!-- [...] --\u003e\n        \u003clink rel=\"stylesheet\" href=\"{{ asset('css/app.css') }}\"\u003e \n        \u003cmeta name=\"csrf-token\" content=\"{{ csrf_token() }}\" /\u003e\n    \u003c/head\u003e\n```\n\nAbove, we are using the `asset` function to load the asset from the `public/css` directory. It is recommended that you use the `asset` function as it will add the full URL to the asset.\n\n![laravel-log-1-4](//images.ctfassets.net/1es3ne0caaid/3QaBwNbV3ucuVaOcWd0gHK/1ce5de7a3fe769f7c796e7e50c827683/laravel-log-1-4.png)\n\nNext, let’s replace the design with ours. Still in the `welcome.blade.php` file, replace the code in the `\u003cbody\u003e` tag with this:\n\n```php\n    \u003c!-- File: ./resources/views/welcome.blade.php --\u003e\n    \u003c!-- [...] --\u003e\n    \u003cdiv class=\"flex-center position-ref full-height\"\u003e\n        \u003cdiv class=\"content\"\u003e\n            \u003cdiv class=\"title m-b-md\"\u003e\n              Laravel Logger\n            \u003c/div\u003e\n    \n            \u003cdiv class=\"button\"\u003e\n              \u003cbutton type=\"button\" class=\"btn btn-lg btn-primary\" data-toggle=\"modal\" data-target=\"#exampleModal\"\u003e\n                Send Log\n              \u003c/button\u003e\n            \u003c/div\u003e\n            \n            \u003c!-- Modal will be inserted here --\u003e\n        \u003c/div\u003e\n    \u003c/div\u003e   \n    \n    \u003cscript src=\"{{ asset('js/app.js') }}\"\u003e\u003c/script\u003e     \n    \u003c!-- [...] --\u003e\n```\n\nAbove, we replaced the HTML on the previous page with ours. We also added a button that when clicked a modal, which we will create soon, will open. In the snippet, we added comments for where other parts of the code will be added, you should watch out for that too.\n\nNow for the modal, in the same file where it specifies the “Modal will be inserted here” add the following code:\n\n```php\n    @includeIf('partials.modal')\n```\n\nThis will include the view `resources/views/partials/modal.blade.php` if it exists. Right now it does not so create it and paste the following code into the file:\n\n```php\n    \u003c!-- File: ./resources/views/partials/modal.blade.php --\u003e\n    \u003cdiv class=\"modal fade\" id=\"exampleModal\" tabindex=\"-1\" role=\"dialog\" aria-labelledby=\"exampleModalLabel\" aria-hidden=\"true\"\u003e\n        \u003cdiv class=\"modal-dialog\" role=\"document\"\u003e\n            \u003cdiv class=\"modal-content\"\u003e\n                \u003cdiv class=\"modal-header\"\u003e\n                    \u003ch5 class=\"modal-title\" id=\"exampleModalLabel\"\u003e\n                        Send Log\n                    \u003c/h5\u003e\n                    \u003cbutton type=\"button\" class=\"close\" data-dismiss=\"modal\" aria-label=\"Close\"\u003e\n                    \u003cspan aria-hidden=\"true\"\u003e\u0026times;\u003c/span\u003e\n                    \u003c/button\u003e\n                \u003c/div\u003e\n                \u003cdiv class=\"modal-body\"\u003e\n                    \u003cdiv class=\"form-group\"\u003e\n                        \u003clabel for=\"exampleFormControlSelect1\"\u003eLog Level\u003c/label\u003e\n                        \u003cselect name=\"level\" class=\"form-control\" id=\"level\"\u003e\n                            \u003coption value=\"info\"\u003eInfo\u003c/option\u003e\n                            \u003coption value=\"warning\"\u003eWarning\u003c/option\u003e\n                            \u003coption value=\"error\"\u003eError\u003c/option\u003e\n                        \u003c/select\u003e\n                    \u003c/div\u003e\n                    \u003cdiv class=\"form-group\"\u003e\n                        \u003clabel for=\"exampleFormControlTextarea1\"\u003eLog Message\u003c/label\u003e\n                        \u003ctextarea name=\"message\" class=\"form-control\" id=\"message\" rows=\"2\"\u003e\u003c/textarea\u003e\n                    \u003c/div\u003e\n                \u003c/div\u003e\n                \u003cdiv class=\"modal-footer\"\u003e\n                    \u003cbutton type=\"button\" class=\"btn btn-secondary\" data-dismiss=\"modal\"\u003eClose\u003c/button\u003e\n                    \u003cbutton type=\"button\" class=\"btn btn-danger\"\u003eDispatch\u003c/button\u003e\n                \u003c/div\u003e\n            \u003c/div\u003e\n        \u003c/div\u003e\n    \u003c/div\u003e\n```\n\nThe modal content is made of three sections, the header, body and, footer. The header shows a description of the modal. Next, is the body which contains the form. This contains an input for the log message and the log level (warning, error, or info). Lastly, the footer has two buttons: a button to dispatch (send) the form details and another to close the modal.\n\nWith this, we have finished building the frontend of the app. Now, we can run our app, and it should look like this:\n\n![laravel-log-1-1](//images.ctfassets.net/1es3ne0caaid/nvHlteXI4Kbxasz7yd7aR/f17aef7a58276ce9b38b57b4ce029890/laravel-log-1-1.gif)\n\n## Conclusion\n\nIn this first tutorial of the series, we have built a logger app that will trigger logs using Laravel. The app dispatches logs of different levels. For now, this app does not actually send the log anywhere or even generate it. \n\nIn the next [part](https://pusher.com/tutorials/laravel-logger-part-2), we will build a package that will send the log messages to Pusher. We will then link the package to this app to make it work then the **Dispatch** button on the modal is pressed.\n\nThe source code is available on [GitHub](https://github.com/neoighodaro/logger).","metaTitle":"Creating a Laravel logger - Part 1: Getting started","metaDescription":"In this six-part series, build a Laravel error logging service. You will create a custom Laravel package to include in Laravel apps, along with web, Android and iOS frontends. Alerts will publish in realtime, featuring push notifications to mobile. In part one, build the log dispatcher."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3yFfUCIoqqr2LYTcmy0ENE","type":"Entry","createdAt":"2019-03-18T20:11:20.475Z","updatedAt":"2019-03-18T20:11:20.475Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Rich notifications with Vapor - Part 2: iOS Application","url":"rich-notifications-vapor-part-2","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"9VkyxkESQ0IQmyKy8wq6C","type":"Entry","createdAt":"2018-09-18T18:40:03.858Z","updatedAt":"2018-09-18T18:40:03.858Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christopher Batin","url":"christopher-batin","email":"chris@batin.me","twitter":"https://twitter.com/cjbatin","github":"https://github.com/cjbatin/","guestWriter":true}}],"projectRepo":"https://github.com/cjbatin/Rich-Notifications-Pusher-Beams","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"644GONySFGieg4wAUc8k0C","type":"Entry","createdAt":"2018-02-12T14:37:53.963Z","updatedAt":"2019-08-28T08:41:31.723Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":4,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Beams","url":"beams","featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SxOuediAgcSuiWyUmkw40","type":"Entry","createdAt":"2018-02-12T15:09:21.130Z","updatedAt":"2018-09-05T15:44:43.776Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Beams","url":"push-notifications","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}}],"reviewDate":"2019-03-18","description":"This two-part series will show you how to build a Vapor backed for an iOS application, featuring rich push notifications. The finished app will provide notifications containing images and videos. In part two, build the frontend.","prerequisite":"You will need Xcode 10+, Vapor 3+, Cocoapods, and some understanding of Vapor.","content":"## Introduction\n\nIn this part of the tutorial we will be building an iOS application that handles receiving a push notification with rich content. The application will only show a notification in the notification center. We won’t be handling the notification when we have the application open.\n\nSometimes sending notifications with only text just isn’t quite enough. This is where rich notifications come in. We can add images and videos to our notification to give the user a richer experience directly from there notification center. \n\n## Prerequisites\n\n- Make sure you have completed [part one](https://pusher.com/tutorials/rich-notifications-vapor-part-1) of this tutorial.\n- A basic understanding of Vapor - please complete my “Getting started with Vapor” [Part One](https://pusher.com/tutorials/getting-started-vapor-part-one) and [Part Two](https://pusher.com/tutorials/getting-started-vapor-part-two).\n- Xcode 10+\n- MacOS\n- Vapor 3.0 - Install instructions [here](https://docs.vapor.codes/3.0/install/macos/).\n- An iOS device for testing notifications.\n- An understanding of iOS development and Xcode environment.\n- Cocoapods - Install instructions [here](https://guides.cocoapods.org/using/getting-started.html).\n- A REST client such as Postman and a basic understanding of how to use it.\n\n\n## Handling basic notifications\n\n### Adding our dependency\n\nStart by creating a new single view application and name it whatever you wish. Open the terminal and go to the working directory of the newly created project and run the following command.\n\n```\n    $ pod init\n```\n\nOpen the newly created `Podfile` and add the following pod:\n\n```\n    pod 'PushNotifications'\n```\n\nIn the terminal run:\n\n```\n    $ pod install\n```\n\nMake sure you close your Xcode project and reopen the newly created Xcode Workspace (YOUR-APP.xcworkspace) before continuing.\n\n### Set up notifications\n\nWithin your project capabilities make sure you have switched on the **Push Notifications** capability. Also turn on the **Background Modes** capability and tick the box for **Remote Notifications**. \n\nOpen your `AppDelegate.swift` file and replace its contents with the following. Remembering to replace the instance ID with your own.\n\n```swift\n    // AppDelegate.swift\n    import UIKit\n    import PushNotifications\n    \n    @UIApplicationMain\n    class AppDelegate: UIResponder, UIApplicationDelegate {\n        var window: UIWindow?\n        let pushNotifications = PushNotifications.shared\n    \n        func application(_ application: UIApplication, didFinishLaunchingWithOptions launchOptions: [UIApplication.LaunchOptionsKey: Any]?) -\u003e Bool {\n            self.pushNotifications.start(instanceId: \"YOUR_INSTANCE_ID\")\n            self.pushNotifications.registerForRemoteNotifications()\n            try? self.pushNotifications.subscribe(interest: \"general\")\n    \n            return true\n        }\n    \n        func application(_ application: UIApplication, didRegisterForRemoteNotificationsWithDeviceToken deviceToken: Data) {\n            self.pushNotifications.registerDeviceToken(deviceToken)\n        }\n    \n        func application(_ application: UIApplication, didReceiveRemoteNotification userInfo: [AnyHashable: Any], fetchCompletionHandler completionHandler: @escaping (UIBackgroundFetchResult) -\u003e Void) {\n            self.pushNotifications.handleNotification(userInfo: userInfo)\n        }\n    \n    }\n```\n\nYou can now run the application on your iOS device and accept to receive notifications. The SDK will then manage registering our interest in “general”. This is the same interest that we have defined in our server that we are pushing to. \n\nIf we were to send a notification now we would see a notification on the lock, however it would not show any rich content.\n\n## Handling rich notifications\n\n### Create a new target\n\nRich notifications are handled by a separate target within our application. To add our new target go to **File → New → Target.** Select the **Notification Service Extension.** \n\n![vapor-beams-rich-notifications-service-extension](//images.ctfassets.net/1es3ne0caaid/6Fkgjr6rHT4RASRmniqJxe/9ed829ade9ca9bbc98d565d47bb3d90d/vapor-beams-rich-notifications-service-extension.png)\n\n\n\u003e **Note:** If you wish to customize how a notification will look on the lock screen. You can use the **Notification Content Extension** to do this. You may find [this tutorial](https://pusher.com/docs/push_notifications/ios/ios10) useful if you wish to learn more about this.\n\nOn the next screen give your target a sensible name and make sure it is set to the correct project and embed in the correct application. Once you have confirmed this click **Finish**. You might be asked to activate your target, select **Activate.**\n\n![vapor-beams-rich-notifications-new-target](//images.ctfassets.net/1es3ne0caaid/1R1U9iAtq2lzlpcttfaphN/cc1c6981e72ee9686578a239c4de4b61/vapor-beams-rich-notifications-new-target.png)\n\n### Handle download\n\nYour new target will create a new `NotificationService.swift` file. This file will handle the notification you have received and attempt to show it. \n\n```swift\n    // NotificationService.swift\n    import UserNotifications\n    \n    class NotificationService: UNNotificationServiceExtension {\n    \n        var contentHandler: ((UNNotificationContent) -\u003e Void)?\n        var bestAttemptContent: UNMutableNotificationContent?\n    \n        override func didReceive(_ request: UNNotificationRequest, withContentHandler contentHandler: @escaping (UNNotificationContent) -\u003e Void) {\n            self.contentHandler = contentHandler\n            bestAttemptContent = (request.content.mutableCopy() as? UNMutableNotificationContent)\n    \n            // Get the custom data from the notification payload\n            if let notificationData = request.content.userInfo[\"data\"] as? [String: Any] {\n                // Grab the attachment\n                if let urlString = notificationData[\"attachment-url\"] as? String,\n                    let fileUrl = URL(string: urlString) {\n                    // Download the attachment\n                    URLSession.shared.downloadTask(with: fileUrl) { (location, response, error) in\n                        if let location = location {\n                            // Move temporary file to remove .tmp extension\n                            let tmpDirectory = NSTemporaryDirectory()\n                            let tmpFile = \"file://\".appending(tmpDirectory).appending(fileUrl.lastPathComponent)\n                            let tmpUrl = URL(string: tmpFile)!\n                            try! FileManager.default.moveItem(at: location, to: tmpUrl)\n    \n                            // Add the attachment to the notification content\n                            if let attachment = try? UNNotificationAttachment(identifier: \"\", url: tmpUrl) {\n                                self.bestAttemptContent?.attachments = [attachment]\n                            }\n                        }\n                        // Serve the notification content\n                        self.contentHandler!(self.bestAttemptContent!)\n                        }.resume()\n                }\n            }\n        }\n        \n        override func serviceExtensionTimeWillExpire() {\n            // Called just before the extension will be terminated by the system.\n            // Use this as an opportunity to deliver your \"best attempt\" at modified content, otherwise the original push payload will be used.\n            if let contentHandler = contentHandler, let bestAttemptContent =  bestAttemptContent {\n                contentHandler(bestAttemptContent)\n            }\n        }\n    }\n```\n\nIt may feel like a lot is going on here but in fact it is all quite simple. We simply check to see if we have a URL within our payload. We then use this URL to download the content to a temporary file and attempt to display this. \n\n\n\u003e **Note:** Rich notifications have a timeout called by the system, if the original content has not been downloaded then the original payload without the rich content will be displayed. It is therefore considered best practice to make sure the notification makes sense without the rich content. \n\n### Testing your integration\n\nNow we can begin testing our integration. If you have closed your Vapor project reopen it in the usual way in Xcode and hit the run button. You should see a message in the console saying it has started and is listening on http://localhost:8080. To begin our first make sure the iPhone is on the lock screen. Our test requires us to use Postman (or another REST client) to pass data through. Open postman or your equivalent REST client and set it up so that it looks like this.\n\n\n![vapor-beams-rich-notifications-postman](//images.ctfassets.net/1es3ne0caaid/7dghRFgOFDVEHO7Kcsr4Az/6bf99fa7b7b4e85f9ee1b3a946f6b6cb/vapor-beams-rich-notifications-postman.png)\n\nThis will show a notification like below. You can force touch to see the full image like the image on the right.\n\n![vapor-beams-rich-notifications-demo-1](//images.ctfassets.net/1es3ne0caaid/6IVOpsmTijE3lzFOSoAzfo/79d2a58e95d49ec583089fd65fd4f6ad/vapor-beams-rich-notifications-demo-1.png)\n![vapor-beams-rich-notifications-demo-2](//images.ctfassets.net/1es3ne0caaid/7MmZkfFJUHIcXc1s85kkGk/18111532c2f30b49af4fb4842612ee1e/vapor-beams-rich-notifications-demo-2.png)\n\n\nYou can change the URL in the post request to contain anyone of the types discussed in the first part of this tutorial. For example you may which to show a video, gif or audio clip.\n\n## Conclusion\n\nIn this part of the tutorial we have learnt how to handle both text notifications and rich notifications in our iOS application. We also learnt how we can test the notifications using postman and change the data that we are sending.\n\nThe source code for this project can be found [here](https://github.com/cjbatin/Rich-Notifications-Pusher-Beams).","metaTitle":"Rich notifications with Vapor - Part 2: iOS Application","metaDescription":"This two-part series will show you how to build a Vapor backed for an iOS application, featuring rich push notifications. The finished app will provide notifications containing images and videos. In part two, build the frontend."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"7HtROx1ePMzq8PmDxY2PuC","type":"Entry","createdAt":"2019-03-14T16:09:48.495Z","updatedAt":"2019-03-14T16:09:48.495Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Rich notifications with Vapor - Part 1: Building the backend","url":"rich-notifications-vapor-part-1","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"9VkyxkESQ0IQmyKy8wq6C","type":"Entry","createdAt":"2018-09-18T18:40:03.858Z","updatedAt":"2018-09-18T18:40:03.858Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christopher Batin","url":"christopher-batin","email":"chris@batin.me","twitter":"https://twitter.com/cjbatin","github":"https://github.com/cjbatin/","guestWriter":true}}],"projectRepo":"https://github.com/cjbatin/Rich-Notifications-Pusher-Beams","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"644GONySFGieg4wAUc8k0C","type":"Entry","createdAt":"2018-02-12T14:37:53.963Z","updatedAt":"2019-08-28T08:41:31.723Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":4,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Beams","url":"beams","featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SxOuediAgcSuiWyUmkw40","type":"Entry","createdAt":"2018-02-12T15:09:21.130Z","updatedAt":"2018-09-05T15:44:43.776Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Beams","url":"push-notifications","type":"use case"}}],"reviewDate":"2019-03-14","description":"This two-part series will show you how to build a Vapor backed for an iOS application, featuring rich push notifications. The finished app will provide notifications containing images and videos. In part one, build the backend.","prerequisite":"You will need Xcode 10+, Vapor 3+, Cocoapods, and some understanding of Vapor.","content":"## Introduction\n\nIn this tutorial we are going to build our backend service using Vapor. It will be responsible for sending a notification to our iOS application.\n\nSometimes sending notifications with only text just isn’t quite enough. This is where rich notifications come in. We can add images and videos to our notification to give the user a richer experience directly from their notification center. \n\n## Prerequisites\n- A basic understanding of Vapor - please complete my “Getting started with Vapor” [Part One](https://pusher.com/tutorials/getting-started-vapor-part-one) and [Part Two](https://pusher.com/tutorials/getting-started-vapor-part-two).\n- Xcode 10+\n- MacOS\n- Vapor 3.0 - Install instructions [here](https://docs.vapor.codes/3.0/install/macos/).\n- An iOS device for testing notifications.\n- An understanding of iOS development and Xcode environment.\n- Cocoapods - Install instructions [here](https://guides.cocoapods.org/using/getting-started.html).\n- A REST client such as [Postman](https://www.getpostman.com) and a basic understanding of how to use it.\n\n\n## Setting up our Vapor backend\n\n### Creating our Vapor project\n\nFrom a terminal in your working directory enter the following command to create your Vapor application.\n\n```\n     $ vapor new RichNotifyServer\n     $ cd RichNotifyServer\n```\n \n Now we will build your application before opening it in Xcode. Remember your first build could take some time to complete. Vapor update will make sure that you have the latest packages. \n\n```\n    $ vapor build\n    $ vapor update\n```\n\nNow open your project in Xcode. Remember to open using Xcode you must run the following command in terminal:\n\n```\n    $ vapor xcode -y\n```\n\nAlso remember to change the run scheme to be the “run” scheme in case it is not already set to this.\n\n![vapor-beams-rich-notifications-run-scheme](//images.ctfassets.net/1es3ne0caaid/3gi5IDZ7gJ1o3IJBNsyvLd/8839f40ab9470606545f25493d66c2ee/vapor-beams-rich-notifications-run-scheme.png)\n\n \n### Adding our dependency\n\nWe need to add the Pusher Beams package to our Swift Package Manager (SPM). Start by opening the `Package.swift` file from the project navigator. This is our dependencies management file, a bit like our `Podfile` or `Cartfile` in iOS development. \n\nBelow the line that gives us our Vapor package dependency:\n\n```\n    .package(url: \"https://github.com/vapor/vapor.git\", from: \"3.0.0\"),\n```\n\nAdd the following to add our Pusher Beams dependency:\n\n```\n    .package(url: \"git@github.com:pusher/push-notifications-server-swift.git\", .branch(\"master\")),\n```\n\nWe need to tell SPM what dependencies each target should have. We do this by editing targets section in the same file. Find the line:\n\n```\n.target(name: \"App\", dependencies: [\"FluentSQLite\", \"Vapor\"]),\n```\n\nEdit it so that it becomes:\n\n```\n    .target(name: \"App\", dependencies: [\"FluentSQLite\", \"Vapor\", \"PushNotifications\"]),\n```\n\nWe’ve added the name of our dependency to the array of dependencies for the app target. For more information on the dependency go [here](https://docs.pusher.com/beams/reference/server-sdk-swift) and for more information relating to SPM go [here](https://swift.org/package-manager/). Your `Package.swift` file should now look something like this:\n\n```swift\n    // Package.swift\n    // swift-tools-version:4.0\n    import PackageDescription\n    \n    let package = Package(\n        name: \"RichNotifyServer\",\n        dependencies: [\n            // 💧 A server-side Swift web framework.\n            .package(url: \"https://github.com/vapor/vapor.git\", from: \"3.0.0\"),\n            .package(url: \"git@github.com:pusher/push-notifications-server-swift.git\", .branch(\"master\")),\n            // 🔵 Swift ORM (queries, models, relations, etc) built on SQLite 3.\n            .package(url: \"https://github.com/vapor/fluent-sqlite.git\", from: \"3.0.0\")\n        ],\n        targets: [\n            .target(name: \"App\", dependencies: [\"FluentSQLite\", \"Vapor\", \"PushNotifications\"]),\n            .target(name: \"Run\", dependencies: [\"App\"]),\n            .testTarget(name: \"AppTests\", dependencies: [\"App\"])\n        ]\n    )\n```\n\nNow we need to close Xcode and install the package and reopen it. It’s best practice in Vapor to create and install files outside of our Xcode project using command line tools and then reopen Xcode using the toolbox command. After closing Xcode run the following command in the terminal in our working directory.\n\n```\n    $ swift build\n```\n\nReopen Xcode by running:\n\n```\n    $ vapor xcode -y\n```\n\n## Creating a Beams instance\n\nLog in or create an account to access your dashboard [here](https://dash.pusher.com). Create a new Beams instance using the dashboard.\n\n![vapor-beams-rich-notifications-new-beams](//images.ctfassets.net/1es3ne0caaid/2fAmcFVuqGCJH9vCD1xNN8/12785108f9a0bf74ed6962306b290c8b/vapor-beams-rich-notifications-new-beams.png)\n\nComplete step one of the iOS setup guide, we will pick up the remainder later on in this tutorial. Press the **X** to exit the setup guide and you will be returned to your dashboard for that instance. Scroll to the bottom of this page and you will find your Pusher Beams instance ID and secret key, make note of these you will need them later.\n\n\n## Sending notifications\n\n### Rich notifications\n\nRich notifications allow you to send different types of multi-media content. There are specific types of supported content that you can send. You can find up to date information on those content types [here](https://developer.apple.com/documentation/usernotifications/unnotificationattachment). You should also make note of the maximum size these files can be.  \n\n### Creating our routes\n\nWe will be creating a simple application with one post request that we can use in order to send different push notifications using our REST client.\n\nOpen your `routes.swift` file and replace the contents with the following:\n\n```swift\n    //../Sources/App/routes.swift\n    import Vapor\n    import Vapor\n    // 1\n    import PushNotifications\n    /// Register your application's routes here.\n    public func routes(_ router: Router) throws {\n        // 2\n        router.post(PushContent.self, at: \"push/\") { req, data -\u003e String in\n            PushNotificationService.send(content: data)\n            return \"Push Success\"\n        }\n    }\n    \n    // 3\n    struct PushContent: Content {\n        let title: String\n        let message: String\n        let dataURL: String\n    }\n    \n    //4\n    class PushNotificationService {\n        class func send(content: PushContent) throws {\n            let pushNotifications = PushNotifications(instanceId: \"YOUR_INSTANCE_ID\" , secretKey:\"YOUR_SECRET_KEY\")\n            let interests = [\"general\"]\n            let publishRequest = [\n                \"apns\": [\n                    \"aps\": [\n                        \"alert\": [\n                            \"title\": content.title,\n                            \"body\": content.message\n                        ],\n                        \"mutable-content\": 1\n                    ],\n                    \"data\": [\n                        \"attachment-url\": content.dataURL\n                    ]\n                ]\n            ]\n            pushNotifications.publishToInterests(interests, publishRequest, completion: { publishID in\n                print(\"Published \\(publishID)\")\n            })\n        }\n    }\n```\n\n1. We need to import the new PushNotifications dependency we have just installed.\n2. Here we create a new POST route that is capable of sending a specific push notification based on the data that is passed in using the wrapper class we have defined below.\n3. This is the structure that our post request is expecting the data to arrive in. This includes a string that contains the URL of the data that we want to show in our notification.\n4. This is our wrapper class. Here we create an instance of the dependency we have installed using the Beams instances ID and secret key you created earlier. We only have one interest in our app (which is general). We create a publish request using for APNS using the message that is passed in using the method call and we then publish this using our dependency. Make note of the `\"mutable-content\"` flag in the data structure, this will be intercepted by our notification service extension in iOS to display the data. Also make note of our `\"data\"` key that includes our data url. Our application will use this information in order to download the image or video that we have sent.\n\nHit the run button in Xcode, you should see a message in the console saying it has started and is listening on http://localhost:8080. Leave this running in the background whilst you complete part two.\n\n## Conclusion\n\nWe’ve learnt how to add a dependency the Beams server side SDK as a dependency to Vapor and setup a basic route that can publish notifications to an iOS app that has registered for the interest. We’ve learnt about the different types of rich push notifications that can be sent and their limitations. \n\nNow that you have a basic backend setup we need to go ahead and create the iOS application that can display these notifications. Stay tuned for part two!\n\nThe source code for this project can be found [here](https://github.com/cjbatin/Rich-Notifications-Pusher-Beams).","metaTitle":"Rich notifications with Vapor - Part 1: Building the backend","metaDescription":"This two-part series will show you how to build a Vapor backed for an iOS application, featuring rich push notifications. The finished app will provide notifications containing images and videos. In part one, build the backend."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5pUPNbZbnaiuGaUcoKg2U","type":"Entry","createdAt":"2019-02-12T20:47:14.095Z","updatedAt":"2019-02-12T20:47:14.095Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a to-do list app for the Apple watch","url":"to-do-list-apple-watch","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/todo-watch-app","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}}],"reviewDate":"2019-02-12","description":"This tutorial will guide you through building an app for the Apple watch. Users will be able to create and manage tasks in a simple to-do list app, with the watch and mobile apps communicating in realtime.","prerequisite":"You will need Xcode and Node installed on your machine.","content":"## Introduction\n\nIn this post, you will build a realtime iOS application involving a watch and an iPhone. We will be creating a realtime todo watch app with an accompanying iPhone app. \n\nHere is how your application will look afterward:\n\n![watchOS-todo-demo-1](//images.ctfassets.net/1es3ne0caaid/4EeinSMyO4iM6gc4SII0Ga/5450d9fa9473ac35ceb8487dabba51c5/watchOS-todo-demo-1.gif)\n\n## Prerequisites\n\nTo follow along in this article, you need the following:\n\n- A Pusher Channel app. You can create one [here](https://pusher.com/channels) (take note of your app keys as you’ll need them later).\n- Xcode installed on your machine. You can download [here](https://developer.apple.com/xcode/).\n- Node JS and NPM (Node Package Manager) installed on your machine. Check [here](https://nodejs.org/en/) for the latest releases.\n\nLet’s get started.\n\n## Building the backend\n\nOur app will be powered by a local Node.js server. Create a folder on your machine, say `todo-backend`. Create a `package.json` file inside it and set it up like so:\n\n``` json\n    // File: ./package.json\n    {\n      \"name\": \"todo\",\n      \"version\": \"1.0.0\",\n      \"description\": \"\",\n      \"main\": \"index.js\",\n      \"scripts\": {\n        \"test\": \"echo \\\"Error: no test specified\\\" \u0026\u0026 exit 1\"\n      },\n      \"keywords\": [],\n      \"author\": \"\",\n      \"license\": \"ISC\",\n      \"dependencies\": {\n        \"body-parser\": \"^1.18.3\",\n        \"express\": \"^4.16.4\",\n        \"pusher\": \"^2.2.0\"\n      }\n    }\n```\n\nThis file contains a description to our app and the dependencies we will make use of. Next, create an `index.js` file and paste this:\n\n``` javascript\n    // File: ./index.js\n    const express = require('express');\n    const bodyParser = require('body-parser');\n    const Pusher = require('pusher');\n    const app = express();\n    \n    app.use(bodyParser.json());\n    \n    var pusher = new Pusher({\n      appId: 'PUSHER_APP_ID',\n      key: 'PUSHER_APP_KEY',\n      secret: 'PUSHER_APP_SECRET',\n      cluster: 'PUSHER_APP_CLUSTER'\n    });\n    \n    app.post('/addItem', function (req, res) {\n      pusher.trigger('todo', 'addItem', { text: req.body.value});\n      res.send(200);\n    })\n    \n    app.listen(process.env.PORT || 5000);\n```\n\nHere, we added a single endpoint that will add todo items. Our watch app will make use of this API and send a todo trigger to Pusher.\n\n\n\u003e Replace `PUSHER_APP_*` placeholders with the credentials from your dashboard\n\nRun the `npm install` command in the directory to install the Node modules after which you run your server using:\n\n```\n    $ node index.js\n```\n\nThe localhost runs on port `5000`.\n\n\n## Building the iOS app\n\n### Creating a project\n\nOpen your Xcode and create a new project. You will be presented with a template wizard. Choose `watchOS` and select **iOS App with WatchKit App**.\n\n![watchOS-todo-demo-2](//images.ctfassets.net/1es3ne0caaid/5IIVlZNj1e8qgou2oC0cg4/d76772a0bbc7e2339ea8c82ac2d8e615/watchOS-todo-demo-2.png)\n\nNext, you enter a product name - say `TodoWatchApp`, team, and organization details. Choose Swift as the default language and choose **Next**. You will then be asked to choose the directory where you want the project to be created. After this, you’re good to go!\n\nHere is how the directory of your project should look.\n\n![watchOS-todo-demo-3](//images.ctfassets.net/1es3ne0caaid/25Ou9lbz16iq6cyswYS82g/b688705957d37f8b03aa292681e8b53b/watchOS-todo-demo-3.png)\n\nThis is quite different from when you are developing just the main app. We have three folders, one for the phone app and two for the watch. \n\nThe `TodoWatchApp WatchKit App` folder is where we design the interface for the watch while the `TodoWatchApp WatchKit Extension` is where we will write the logic and view controllers.\n\n### Building our iOS phone app\n\nSo, we will start with the phone app. The phone app will listen to triggers from Pusher. These triggers will be sent by the app. First, we will need to add the Pusher dependency. Follow these steps to set it up.\n\nRun this command on your main app directory:\n\n```\n    $ pod init\n```\n\nThis will create a `Podfile` where you can insert your dependencies. Paste this into the file:\n\n```\n    # File: ./Podfile\n    target 'TodoWatchApp' do\n      use_frameworks!\n      pod 'PusherSwift'\n    end\n    \n    target 'TodoWatchApp WatchKit App' do\n      use_frameworks!\n    end\n    \n    target 'TodoWatchApp WatchKit Extension' do\n      use_frameworks!\n    end\n```\n\nWe only added the dependency for the main app as the library does not support watchOS yet. You can follow [this GitHub issue](https://github.com/pusher/pusher-websocket-swift/issues/150) to learn the latest on that.\n\nNext, run this command still in the main app directory:\n\n```\n    $ pod install\n```\n\nAfter the installation is complete, close your project -`TodoWatchApp.xcodeproj`. Still in Xcode, open  `TodoWatchApp.xcworkspace` located in the folder of your project. This was generated after you installed your dependencies.\n\nFor now, we will work with the `TodoWatchApp` folder. Now, let us design the interface for the app. All the app needs is a table view to display the todo items. Open the `Main.storyboard` file, delete the default `ViewController` scene. Now, drag the `TableViewController` element and drop on the empty light grey area. You should now have something like this:\n\n![watchOS-todo-demo-4](//images.ctfassets.net/1es3ne0caaid/33yvtekPG86OGuyG02G2eK/a75f51dfaefd8dde9312b8c3ab894333/watchOS-todo-demo-4.png)\n\nSet the controller to be the initial controller. You can do that in the identity inspector pane.\n\nGo to your `ViewController.swift` class and change the class it is extending from `UIViewController` to `UITableViewController`. \n\nThe `UITableViewController` still extends the `UIViewController` class but it is specially configured to table views. Go back to your storyboard, select the **Table View Controller** element, in the identity inspector (third icon at the top right as seen in the image ), change the class to your `ViewController` class like so:\n\n![watchOS-todo-demo-5](//images.ctfassets.net/1es3ne0caaid/3pmsKKDZLGm6aIqUK686OG/82464e5e6de9164d7c177e176f81e5d8/watchOS-todo-demo-5.png)\n\nNext, select the **Table Cell** which is a grandchild of the **Table View Controller** element, in the attributes inspector, enter an identifier of your choice or better still, use **cell**. This is the unique name used to identify the row name in the Swift file, later on.\n\nNext, let us design how each row of the table view will look like. We will just want a label displaying the todo item. Simply drag a label to the **Content View** of the **Table Cell**. Your design should now look like this:\n\n![watchOS-todo-demo-6](//images.ctfassets.net/1es3ne0caaid/2BO9jKltK4GyIUSSQwoAGq/1414d5a8a1adaba6e4da36a0aa4b0b91/watchOS-todo-demo-6.png)\n\nThe additions we just made are reflected in the screenshot above.\nNext, create a `TableCell.swift` file and paste this:\n\n``` swift\n    // File: ./TableCell.swift\n    import Foundation\n    import UIKit\n    \n    class TableCell: UITableViewCell {\n    \n        @IBOutlet weak var textView: UILabel!\n    \n        func setLabel(labelValue:String) {\n            textView.text = labelValue\n        }\n    }\n```\n\nThis class mimics how each row in the table view will look. It has just one `UILabel` as we earlier specified on our storyboard. Now that we have created this file, go back to the `Main.storyboard` , select the cell, in the attributes inspector, replace the default class there with `TableCell` like so:\n\n![watchOS-todo-demo-7](//images.ctfassets.net/1es3ne0caaid/72PkQuszG8W2YI80Mg8Kco/ba2745107363472b9512d5a19551624d/watchOS-todo-demo-7.png)\n\n\u003e Be sure to link the `@IBOutlet` variable from your storyboard to the file.\n\nNow, let us hook things up in our `ViewController` swift file. Paste this in the file:\n\n``` swift\n    // File: ./ViewController.swift\n    import UIKit\n    import PusherSwift\n    \n    class ViewController: UITableViewController {\n        var pusher: Pusher!\n    \n        var itemList = [String]() {\n            didSet {\n                self.tableView.reloadData()\n            }\n        }\n    \n        override func viewDidLoad() {\n            super.viewDidLoad()\n            setupPusher()\n        }\n    \n        override func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -\u003e Int {\n            return itemList.count\n        }\n    \n        override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -\u003e UITableViewCell {\n            // inflate each row\n            let currentItem = itemList[indexPath.row]\n            let todoCell = tableView.dequeueReusableCell(withIdentifier: \"cell\") as! TableCell\n            todoCell.setLabel(labelValue: currentItem)\n            return todoCell\n        }\n    }\n```\n\nLet us go through what we have here: first, we declared a Pusher variable and our list. In the `viewDidLoad` method, we call the `setupPusher` method which we will look at shortly.\n\nThe other two methods are for the table view, to get the size of the table and to get data for each row. Next, paste the `setupPusher` function inside the class like so:\n\n``` swift\n    func setupPusher() {\n        let options = PusherClientOptions(\n            host: .cluster(\"PUSHER_APP_CLUSTER\")\n        )\n    \n        pusher = Pusher(\n            key: \"PUSHER_APP_KEY\",\n            options: options\n        )\n    \n        // subscribe to channel and bind to event\n        let channel = pusher.subscribe(\"todo\")\n    \n        let _ = channel.bind(eventName: \"addItem\", callback: { (data: Any?) -\u003e Void in\n            if let data = data as? [String : AnyObject] {\n                let value = data[\"text\"] as! String\n                self.itemList.append(value)\n            }\n        })\n    \n        pusher.connect()\n    }\n```\n\nThis method initializes Pusher and listens to the `todo` channel for updates.\n\n\u003e Replace the Pusher holder with keys found on your dashboard\n\nWith this, we are done with our phone app. We will now build the watch app.\n\n\n## Building our watchOS app\n\nGo to the `TodoWatchApp WatchKit App` folder, open the `Interface.storyboard`. Delete the controller scenes except for the **Interface Controller Scene.** Now drag a group element library to the watch screen. Set the orientation of the group to horizontal. Next, add a label and a button as children to the group.\n\nWe will use a custom icon for the button. You can download the icon [here](https://www.dropbox.com/s/ravllhdrfh50q4u/add_item.png?dl=0). Open the `Assets.xcassets` in the `TodoWatchApp WatchKit App` folder and import the new image asset. Make sure it is named `add_item`. After importing, go back to the `Interface.storyboard` , select the button, and select the icon as the background in the identity inspector. Clear the title on the button, then set the width and height to fixed using `20` as the value.\n\nNext, we will hookup an action from the add button in the storyboard to our `InterfaceController` file. We use the assistant editor(icon with rings at top right) to split the two files like so:\n\n![watchOS-todo-demo-8](//images.ctfassets.net/1es3ne0caaid/4eTGQoRuVOsWcwoUIcgeIk/759269dbe29723325134cc97ab77f3ea/watchOS-todo-demo-8.png)\n\nAfter that, open the `InterfaceController.swift` file in the `TodoWatchApp WatchKit Extension` folder and make sure your file looks like this:\n\n``` swift\n    // File: ./InterfaceController.swift\n    import WatchKit\n    import Foundation\n    \n    class InterfaceController: WKInterfaceController {\n        @IBAction func addNewItem() {\n            let suggestionsArray = [\"Visit Neo\", \"Write Pusher article\"]\n    \n            presentTextInputController(withSuggestions: suggestionsArray,allowedInputMode: WKTextInputMode.allowEmoji, completion: { (result) -\u003e Void in\n    \n                guard let choice = result else { return }\n                \n                let newItem = choice[0] as! String\n                self.postValue(value: newItem)\n            })\n        }\n    \n        func postValue(value:String){\n            let parameters = [\"value\": value] as [String : Any]\n            let url = URL(string: \"http://127.0.0.1:5000/addItem\")!\n            let session = URLSession.shared\n    \n            var request = URLRequest(url: url)\n            request.httpMethod = \"POST\"\n    \n            do {\n                request.httpBody = try JSONSerialization.data(\n                  withJSONObject: parameters, \n                  options: .prettyPrinted\n                )\n            } catch let error {\n                print(error.localizedDescription)\n            }\n    \n            request.addValue(\"application/json\", forHTTPHeaderField: \"Content-Type\")\n            request.addValue(\"application/json\", forHTTPHeaderField: \"Accept\")\n    \n            let task = session.dataTask(with: request as URLRequest, completionHandler: { data, response, error in\n                guard error == nil else {\n                    return\n                }\n    \n                guard let data = data else {\n                    return\n                }\n    \n                do {\n                    //create json object from data\n                    if let json = try JSONSerialization.jsonObject(with: data, options: .mutableContainers) as? [String: Any] {\n                        print(json)\n                    }\n                } catch let error {\n                    print(error.localizedDescription)\n                }\n            })\n            \n            task.resume()\n        }\n    }\n```\n\nHere, when the add button is clicked, the watch opens the input controller where you can either handwrite your todo, or choose from the suggestions, or dictate it. When you confirm your text, the watch sends the item to the server.\n\nYou can now run both applications on the simulator. You can select the **WatchApp** build so it runs both simulators at once. \n\nWhen you run the app, it should look like this:\n\n![watchOS-todo-demo-1](//images.ctfassets.net/1es3ne0caaid/4EeinSMyO4iM6gc4SII0Ga/5450d9fa9473ac35ceb8487dabba51c5/watchOS-todo-demo-1.gif)\n\n\n## Conclusion\n\nHere, we have been able to successfully build an iOS app to show realtime functionalities between the main app, the watch app and, Pusher. You can still play around this in the [GitHub repository](https://github.com/neoighodaro/todo-watch-app).","metaTitle":"Build a to-do list app for the Apple watch","metaDescription":"This tutorial will guide you through building an app for the Apple watch. Users will be able to create and manage tasks in a simple to-do list app, with the watch and mobile apps communicating in realtime."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4umqdaDyzCCKAIAYIO0gEC","type":"Entry","createdAt":"2018-12-10T19:39:58.351Z","updatedAt":"2020-06-19T14:26:28.893Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Creating an iOS app with user presence using Node.js and Swift","url":"ios-user-presence-node-swift","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/ios-presence-channel-pusher","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6HDXlostEIoyqU6K6WyuM0","type":"Entry","createdAt":"2018-02-12T15:09:53.611Z","updatedAt":"2018-02-26T12:31:12.502Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Online Presence","url":"online-presence","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3WH6Mz2j7iyIaqCeOKIIkQ","type":"Entry","createdAt":"2018-02-12T14:44:51.150Z","updatedAt":"2018-02-12T14:44:51.150Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Node.js","url":"node-js","type":"framework"}}],"reviewDate":"2018-12-10","description":"This tutorial demonstrates how to add an online presence feature to an iOS app. You will use Node for the backend, and build a basic example app using Swift.","prerequisite":"You will need Xcode, Cocoapods and Node installed on your machine. Basic knowledge of iOS development will be helpful.","content":"In this tutorial, we are going to see how you can add user presence to an iOS application using Pusher Channels, Node.js, and Swift. We will create a sample chat application to demonstrate this feature. However, because we are focusing on just user presence, we will not implement the actual chat feature.\n\nIf you are building an application that has a user base, you might need to show your users when their friends are currently online. This comes in handy especially in messenger applications where the current user would like to know which of their friends are available for instant messaging.\n\nHere is a screen recording on how we want the application to work:\n\n![swift-presence-demo-1](//images.ctfassets.net/1es3ne0caaid/1ckGs7NjtccOWCoScc6qKg/c17f54d7165a66443b7e021ecd055e56/swift-presence-demo-1.gif)\n\n## Prerequisites\n\nTo follow along you need the following requirements:\n\n\n1. Xcode installed on your machine. Download [here](https://developer.apple.com/xcode).\n2. Knowledge of the Swift programming language.\n3. Knowledge of the Xcode IDE.\n4. A Pusher Channels app. Create one [here](https://pusher.com).\n5. Cocoapods installed on your machine. [Installation guide](https://guides.cocoapods.org/using/getting-started.html).\n6. Node.js and npm installed on your machine. [Installation guide](https://nodejs.org/en/download/).\n7. Basic knowledge of JavaScript and Node.js.\n\nLet’s get started.\n\n\n## Creating the backend of the application\n\nBefore creating the iOS application, let’s create the backend application in Node.js. This application will have the necessary endpoints the application will need to function properly. To get started, create a new directory for the project. \n\n### Installing the dependencies\n\nIn the root of the project, create a new `package.json` file and paste the following contents into it:\n\n``` json\n    {\n      \"name\": \"presensesample\",\n      \"version\": \"1.0.0\",\n      \"main\": \"index.js\",\n      \"dependencies\": {\n        \"body-parser\": \"^1.18.3\",\n        \"express\": \"^4.16.4\",\n        \"pusher\": \"^2.1.3\"\n      }\n    }\n```\n\nAbove, we have defined some npm dependencies that the backend application will need to function. Amongst the dependencies, we can see the `pusher` library. This is the Pusher JavaScript server SDK.\n\nNext, open your terminal application and `cd` to the root of the project you just created and run the following command:\n\n```\n    $ npm install\n```\n\nThis command will install all the dependencies we defined above in the `package.json` file.\n\n### Creating the main application\n\nNext, create a new file called `index.js` and paste the following code into the file:\n\n``` javascript\n    // File: ./index.js\n    const express = require('express');\n    const bodyParser = require('body-parser');\n    const Pusher = require('pusher');\n    const app = express();\n    \n    let users = {};\n    let currentUser = {};\n    \n    let pusher = new Pusher({\n      appId: 'PUSHER_APP_ID',\n      key: 'PUSHER_APP_KEY',\n      secret: 'PUSHER_APP_SECRET',\n      cluster: 'PUSHER_APP_CLUSTER'\n    });\n    \n    app.use(bodyParser.json());\n    app.use(bodyParser.urlencoded({ extended: false }));\n    \n    // TODO: Add routes here\n    \n    app.listen(process.env.PORT || 5000);\n```\n\n\u003e Replace the `PUSHER_APP_*` placeholders with your Pusher application credentials.\n\nIn the code above, we imported the libraries we need for the backend application. We then instantiated two new variables: `users` and `currentUser`. We will be using these variables as a temporary in-memory store for the data since we are not using a database. \n\nNext, we instantiated the Pusher library using the credentials for our application. We will be using this instance to communicate with the Pusher API. Next, we add the `listen` method which instructs the Express application to start the application on port 5000.\n\nNext, let’s add some routes to the application. In the code above, we added a comment to signify where we will be adding the route definitions. Replace the comment with the following code:\n\n``` javascript\n    // File: ./index.js\n    \n    // [...]\n    \n    app.post('/users', (req, res) =\u003e {\n      const name = req.body.name;\n      const matchedUsers = Object.keys(users).filter(id =\u003e users[id].name === name);\n      \n      if (matchedUsers.length === 0) {\n        const id = generate_random_id();\n        users[id] = currentUser = { id, name };\n      } else {\n        currentUser = users[matchedUsers[0]];\n      }\n      \n      res.json({ currentUser, users });\n    });\n    \n    // [...]\n```\n\nAbove, we have the first route. The route is a `POST` route that creates a new user. It first checks the `users` object to see if a user already exists with the specified name. If a user does not exist, it generates a new user ID using the `generate_random_id`  method (we will create this later) and adds it to the `users`  object. If a user exists, it skips all of that logic. \n\nRegardless of the outcome of the user check, it sets the `currentUser` as the user that was created or matched and then returns the `currentUser` and `users` object as a response.\n\nNext, let’s define the second route. Because we are using presence channels, and presence channels are private channels, we need an endpoint that will authenticate the current user. Below the route above, add the following code:\n\n``` javascript\n    // File: ./index.js\n    \n    // [...]\n    \n    app.post('/pusher/auth/presence', (req, res) =\u003e {\n      let socketId = req.body.socket_id;\n      let channel = req.body.channel_name;\n      \n      let presenceData = {\n        user_id: currentUser.id,\n        user_info: { name: currentUser.name }\n      };\n    \n      let auth = pusher.authenticate(socketId, channel, presenceData);\n      \n      res.send(auth);\n    });\n    \n    // [...]\n```\n\nAbove, we have the Pusher authentication route.  This route gets the expected `socket_id` and `channel_name` and uses that to generate an authentication token. We also supply a `presenceData` object that contains all the information about the user we are authenticating. We then return the token as a response to the client.\n\nFinally, in the first route, we referenced a function `generate_random_id`. Below the route we just defined, paste the following code:\n\n``` javascript\n    // File: ./index.js\n    \n    // [...]\n    \n    function generate_random_id() {\n      let s4 = () =\u003e (((1 + Math.random()) * 0x10000) | 0).toString(16).substring(1);\n      return s4() + s4() + '-' + s4() + '-' + s4() + '-' + s4() + s4() + s4();\n    }\n    \n    // [...] \n```\n\nThe function above just generates a random ID that we can then use as the user ID when creating new users.\n\nLet’s add a final default route. This will catch visits to the backend home. In the same file, add the following:\n\n``` javascript\n    // [...]\n    \n    app.get('/', (req, res) =\u003e res.send('It works!'));\n    \n    // [...]\n```\n\nWith this, we are done with the Node.js backend. You can run your application using the command below:\n\n```\n    $ node index.js\n```\n\nYour app will be available here: http://localhost:5000. \n\n\n## Building the iOS application\n\nLaunch Xcode and create a new sample **Single View App** project. We will call ours `presensesample`. \n\n![swift-presence-xcode-create-project](//images.ctfassets.net/1es3ne0caaid/5laF5rUEzmc4gMCcaUcqqE/41ddeca478eb5805b04fc8d8ce6a5109/swift-presence-xcode-create-project.png)\n\nWhen you are done creating the application, close Xcode. Open your terminal application and `cd` to the root directory of the iOS application and run the following command:\n\n```\n    $ pod init\n```\n\nThis will create a new `Podfile` file in the root directory of your application. Open the file and replace the contents with the following:\n\n```\n    # File: ./Podfile\n    target 'presensesample' do\n      platform :ios, '12.0'\n        \n      use_frameworks!\n      \n      pod 'Alamofire', '~\u003e 4.7.3'\n      pod 'PusherSwift', '~\u003e 5.0'\n      pod 'NotificationBannerSwift', '~\u003e 1.7.3'\n    end\n```\n\nAbove, we have defined the application’s dependencies. To install the dependencies, run the following command:\n\n```\n    $ pod install\n```\n\nThe command above will install all the dependencies in the `Podfile` and also create a new `.xcworkspace` file in the root of the project. Open this file in Xcode to launch the project and not the `.xcodeproj` file.\n\n### Creating the scenes\n\nThe first thing we will do is create the storyboard scenes we need for the application to work. We want the storyboard to look like this:\n\n![swift-presence-storyboard](//images.ctfassets.net/1es3ne0caaid/8giPZenseAOU8OYqsmIUk/f73107482e257a499275a825ac0a724d/swift-presence-storyboard.png)\n\nOpen the main storyboard file and delete all the scenes in the file so it is empty. Next, add a view controller to the scene. \n\n\n\u003e **TIP:** You can use the **command + shift + L** shortcut to bring the objects library.\n\nWith the view controller selected, click on **Editor \u003e Embed In \u003e Navigation Controller**. This will embed the current view controller in a navigation controller. Next, with the navigation view controller selected, open the attributes inspector and select the **Is Initial View Controller** option to set the navigation view controller as the entry point for the storyboard.\n\n![swift-presence-nav-controller](//images.ctfassets.net/1es3ne0caaid/2icSp2cXUky8wyqGKGaGQI/b81ac58e733e6c0b1c6e2b2fed333739/swift-presence-nav-controller.png)\n\nNext, design the view controller as seen in the screenshot below. Later on in the article, we will be connecting the text field and button to the code using an `@IBOutlet` and an `@IBAction`.\n\n![swift-presence-start-chatting-button](//images.ctfassets.net/1es3ne0caaid/6dLkdEmXh6cqgy0sSe0kuo/c9310c02fd5451b4ebcd899991db49c6/swift-presence-start-chatting-button.png)\n\nNext, add the tab bar controller and connect it to the view controller using a manual segue. Since tab bar controllers come with two regular view controllers, delete them and add two table view controllers instead as seen below:\n\n![swift-presence-storyboard-table-view](//images.ctfassets.net/1es3ne0caaid/1C7rwWdvssIA28u66oCk4y/eb31cc12822c9c2246f2c69e18cb801c/swift-presence-storyboard-table-view.png)\n\nWhen you are done creating the scenes, let’s start adding the necessary code. \n\n### Adding code to the created scenes\n\nCreate a new controller class called `LoginViewController` and set it as the custom class for the first view controller attached to the navigation controller. Paste the following code into the file:\n\n``` swift\n    // File: ./presensesample/LoginViewController.swift\n    import UIKit\n    import Alamofire\n    import PusherSwift\n    import NotificationBannerSwift\n    \n    class LoginViewController: UIViewController {\n        var user: User? = nil\n        var users: [User] = []\n    \n        @IBOutlet weak var nameTextField: UITextField!\n        \n        override func viewWillAppear(_ animated: Bool) {\n            super.viewWillAppear(animated)\n            \n            user = nil\n            users = []\n            \n            navigationController?.isNavigationBarHidden = true\n        }\n        \n        @IBAction func startChattingButtonPressed(_ sender: Any) {\n            if nameTextField.text?.isEmpty == false, let name = nameTextField.text {\n                registerUser([\"name\": name.lowercased()]) { successful in\n                    guard successful else {\n                        return StatusBarNotificationBanner(title: \"Failed to login.\", style: .danger).show()\n                    }\n    \n                    self.performSegue(withIdentifier: \"showmain\", sender: self)\n                }\n            }\n        }\n        \n        func registerUser(_ params: [String : String], handler: @escaping(Bool) -\u003e Void) {\n            let url = \"http://127.0.0.1:5000/users\"\n            \n            Alamofire.request(url, method: .post, parameters: params)\n                .validate()\n                .responseJSON { resp in\n                    if resp.result.isSuccess,\n                        let data = resp.result.value as? [String: Any],\n                        let user = data[\"currentUser\"] as? [String: String],\n                        let users = data[\"users\"] as? [String: [String: String]],\n                        let id = user[\"id\"], let name = user[\"name\"]\n                    {\n                        for (uid, user) in users {\n                            if let name = user[\"name\"], id != uid {\n                                self.users.append(User(id: uid, name: name))\n                            }\n                        }\n                        \n                        self.user = User(id: id, name: name)\n                        self.nameTextField.text = nil\n                        \n                        return handler(true)\n                    }\n                    \n                    handler(false)\n            }\n        }\n    \n        override func prepare(for segue: UIStoryboardSegue, sender: Any?) {\n            if let vc = segue.destination as? MainViewController {\n                vc.viewControllers?.forEach {\n                    if let onlineVc = $0 as? OnlineTableViewController {\n                        onlineVc.users = self.users\n                        onlineVc.user = self.user\n                    }\n                }\n            }\n        }\n    }\n```\n\nIn the controller above, we have defined the `users` and `user` properties which will hold the available users and the current user when the user is logged in. We also have the `nameTextField` which is an `@IBOutlet` to the text field in the storyboard view controller, so make sure you connect the outlet if you hadn’t previously done so.\n\nIn the same controller, we have the `startChattingButtonPressed` method which is an `@IBAction` so make sure you connect it to the submit button in the storyboard view controller if you have not already done so. In this method, we call the `registerUser` method to register the user using the API. If the registration is successful, we direct the user to the `showmain` segue.\n\n\u003e The segue between the login view controller and the tab bar controller should be set with an identifier of `showmain`. \n\nIn the `registerUser` method, we send the name to the API and receive a JSON response. We parse it to see if the registration was successful or not.\n\nThe final method in the class is the `prepare` method. This method is automatically called by iOS when a new segue is being loaded. We use this to preset some data to the view controller we are about to load.\n\nNext, create a new file called `MainViewController` and set this as the custom class for the tab bar view controller. In the file, paste the following code:\n\n``` swift\n    // File: ./presensesample/MainViewController.swift\n    import UIKit\n    \n    class MainViewController: UITabBarController {\n    \n        override func viewDidLoad() {\n            super.viewDidLoad()\n            \n            navigationItem.title = \"Who's Online\"\n            navigationItem.hidesBackButton = true\n            navigationController?.isNavigationBarHidden = false\n            \n            // Logout button\n            navigationItem.rightBarButtonItem = UIBarButtonItem(\n                title: \"Logout\",\n                style: .plain,\n                target: self,\n                action: #selector(logoutButtonPressed)\n            )\n        }\n        \n        override func tabBar(_ tabBar: UITabBar, didSelect item: UITabBarItem) {\n            navigationItem.title = item.title\n        }\n        \n        @objc fileprivate func logoutButtonPressed() {\n            viewControllers?.forEach {\n                if let vc = $0 as? OnlineTableViewController {\n                    vc.users = []\n                    vc.pusher.disconnect()\n                }\n            }\n            \n            navigationController?.popViewController(animated: true)\n        }\n    }\n```\n\nIn the controller above, we have a few methods defined. The `viewDidLoad` method sets the title of the controller and other navigation controller specific things. We also define a **Logout** button in this method. The button will trigger the `logoutButtonPressed`  method.\n\nIn the `logoutButtonPressed` method, we try to log the user out by resetting the `users` property in the view controller and also we disconnect the user from the Pusher connection.\n\nNext, create a new controller class named `ChatTableViewController`. Set this class as the custom class for one of the tab bar controllers child controllers. Paste the following code into the file:\n\n``` swift\n    // File: ./presensesample/ChatTableViewController.swift\n    import UIKit\n    \n    class ChatTableViewController: UITableViewController {\n        override func viewDidLoad() {\n            super.viewDidLoad()\n        }\n    \n        override func numberOfSections(in tableView: UITableView) -\u003e Int {\n            return 0\n        }\n    \n        override func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -\u003e Int {\n            return 0\n        }\n    }\n```\n\nThe controller above is just a base controller and we do not intend to add any chat logic to this controller.\n\nCreate a new controller class called `OnlineTableViewController`. Set this controller as the custom class for the second tab bar controller child controller. Paste the following code to the controller class:\n\n``` swift\n    // File: ./presensesample/OnlineTableViewController.swift\n    import UIKit\n    import PusherSwift\n    \n    struct User {\n        let id: String\n        var name: String\n        var online: Bool = false\n        \n        init(id: String, name: String, online: Bool? = false) {\n            self.id = id\n            self.name = name\n            self.online = online!\n        }\n    }\n    \n    class OnlineTableViewController: UITableViewController {\n    \n        var pusher: Pusher!\n        var user: User? = nil\n        var users: [User] = []\n    \n        override func numberOfSections(in tableView: UITableView) -\u003e Int {\n            return 1\n        }\n    \n        override func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -\u003e Int {\n            return users.count\n        }\n    \n        override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -\u003e UITableViewCell {\n            let cell = tableView.dequeueReusableCell(withIdentifier: \"onlineuser\", for: indexPath)\n            let user = users[indexPath.row]\n    \n            cell.textLabel?.text = \"\\(user.name) \\(user.online ? \"[Online]\" : \"\")\"\n    \n            return cell\n        }\n    }\n```\n\nIn the code above, we first defined a `User` struct. We will use this to represent the user resource. We have already referenced this struct in previous controllers we created earlier.\n\nNext, we defined the `OnlineTableViewController` class which is extends the `UITableViewController` class. In this class, we override the usual table view controller methods to provide the table with data. \n\n\u003e You have to set the cell reuse identifier of this table to `onlineuser` in the storyboard. \n\nAbove we also defined some properties:\n\n- `pusher` - this will hold the Pusher SDK instance that we will use to subscribe to Pusher Channels.\n- `users` - this will hold an array of `User` structs.\n- `user` - this is the user struct of the current user.\n\nNext, in the same class, add the following method:\n\n``` swift\n    // File: ./presensesample/OnlineTableViewController.swift\n    \n    // [...]\n    \n    override func viewDidLoad() {\n        super.viewDidLoad()\n        \n        tableView.allowsSelection = false\n        \n        // Create the Pusher instance...\n        pusher = Pusher(\n            key: \"PUSHER_APP_KEY\",\n            options: PusherClientOptions(\n                authMethod: .endpoint(authEndpoint: \"http://127.0.0.1:5000/pusher/auth/presence\"),\n                host: .cluster(\"PUSHER_APP_CLUSTER\")\n            )\n        )\n        \n        // Subscribe to a presence channel...\n        let channel = pusher.subscribeToPresenceChannel(\n            channelName: \"presence-chat\",\n            onMemberAdded: { member in\n                if let info = member.userInfo as? [String: String], let name = info[\"name\"] {\n                    if let index = self.users.firstIndex(where: { $0.id == member.userId }) {\n                        let userModel = self.users[index]\n                        self.users[index] = User(id: userModel.id, name: userModel.name, online: true)\n                    } else {\n                        self.users.append(User(id: member.userId, name: name, online: true))\n                    }\n                    \n                    self.tableView.reloadData()\n                }\n            },\n            onMemberRemoved: { member in\n                if let index = self.users.firstIndex(where: { $0.id == member.userId }) {\n                    let userModel = self.users[index]\n                    self.users[index] = User(id: userModel.id, name: userModel.name, online: false)\n                    self.tableView.reloadData()\n                }\n            }\n        )\n        \n        // Bind to the subscription succeeded event...\n        channel.bind(eventName: \"pusher:subscription_succeeded\") { data in\n            guard let deets = data as? [String: AnyObject],\n                let presence = deets[\"presence\"] as? [String: AnyObject],\n                let ids = presence[\"ids\"] as? NSArray else { return }\n            \n            for userid in ids {\n                guard let uid = userid as? String else { return }\n                \n                if let index = self.users.firstIndex(where: { $0.id == uid }) {\n                    let userModel = self.users[index]\n                    self.users[index] = User(id: uid, name: userModel.name, online: true)\n                }\n            }\n            \n            self.tableView.reloadData()\n        }\n        \n        // Connect to Pusher\n        pusher.connect()\n    }\n    \n    // [...]\n```\n\nIn the `viewDidLoad` method above, we are doing several things. First, we instantiate the Pusher instance. In the options, we specify the authorize endpoint. We use the same URL as the backend we created earlier.\n\nThe next thing we do is subscribe to a presence channel called `presence-chat`. When working with presence channels, the channel name must be prefixed with `presence-`. The `subscribeToPresenceChannel` method has two callbacks that we can add logic to:\n\n- `onMemberAdded` - this [event](https://pusher.com/docs/client_api_guide/client_presence_channels#pusher-member-added) is called when a new user joins the `presence-chat` channel. In this callback, we check for the user that was added and mark them as online in the `users` array.\n- `onMemberRemoved` - this [event](https://pusher.com/docs/client_api_guide/client_presence_channels#pusher-member-removed) is called when a user leaves the `presence-chat` channel. In this callback, we check for the user that left the channel and mark them as offline.\n\nNext, we bind to the `pusher:subscription_succeeded` [event](https://pusher.com/docs/client_api_guide/client_presence_channels#pusher-subscription-succeeded). This event is called when a user successfully subscribes to updates on a channel. The callback on this event returns all the currently subscribed users. In the callback, we use this list of subscribed users to mark them online in the application.\n\nFinally, we use the `connect` method on the `pusher` instance to connect to Pusher. \n\n### Allowing local connections on the iOS app\n\nOne last thing we need to do before we are done with the iOS application is allowing the application load data from arbitrary URLs. By default, iOS does not allow this, and it should not. However, since we are going to be testing locally, we need this turned on temporarily. Open the `info.plist` file and update it as seen below:\n\n![swift-presence-allow-local](//images.ctfassets.net/1es3ne0caaid/383KxDAOo0cyucauWI8osQ/854e200980ad355df6948115bcdf6893/swift-presence-allow-local.png)\n\nNow, our app is ready. You can run the application and you should see the online presence status of other users when they log in.\n\n## Conclusion\n\nIn this tutorial, we learned how to use presence channels in your iOS application using Pusher Channels. \n\nThe source code for the application created in this tutorial is available on [GitHub](https://github.com/neoighodaro/ios-presence-channel-pusher). ","metaTitle":"Creating an iOS app with user presence","metaDescription":"This tutorial demonstrates how to add an online presence feature to an iOS app. You will use Node for the backend, and build a basic example app using Swift."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"34ickDgnSMwWoK8ogaKUmk","type":"Entry","createdAt":"2018-11-26T22:37:46.737Z","updatedAt":"2018-11-26T22:37:46.737Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Getting started with Auto Layout in Swift - Part 6: Understanding layout guides","url":"auto-layout-swift-part-6","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/getting-started-with-autolayout","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SdjzfMUtikUcK2CWI8uyC","type":"Entry","createdAt":"2018-07-31T17:19:35.290Z","updatedAt":"2019-08-28T08:41:23.854Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"no pusher tech","url":"no-pusher-tech","featured":false}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4EeOOWtecUA4ggUugmkuiA","type":"Entry","createdAt":"2018-02-20T16:52:29.977Z","updatedAt":"2018-02-26T12:31:10.457Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"iOS","url":"ios","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6PUXwF09KEc6IGgCSKmcqm","type":"Entry","createdAt":"2018-02-12T14:50:29.767Z","updatedAt":"2018-02-26T12:31:15.525Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Swift","url":"swift","type":"language"}}],"reviewDate":"2018-11-26","description":"This six part series takes an in-depth look at Auto Layout in Swift. You will learn how to use Auto Layout to create responsive interfaces for iOS apps. In part six, learn about layout guides.","prerequisite":"You will need Xcode installed on your machine, and some familiarity with the Xcode interface builder.","content":"In the [previous tutorial](https://pusher.com/tutorials/auto-layout-swift-part-5) of the series, we took a dive into stack views and we learned how using them can make creating layouts a lot easier. In this part, we will round up the series by learning about layout guides and how it can make your applications usable when building with Auto Layout.\n\n## Prerequisites\n\nTo get started, you need the following:\n\n- To have completed [part five](https://pusher.com/tutorials/auto-layout-swift-part-5) of the series.\n- Xcode installed on your machine. Download [here](https://developer.apple.com/xcode).\n- Knowledge of the Xcode interface builder.\n\nLet’s get started.\n\n## Understanding layout guides\n\nIn an earlier part of the series, we mentioned the safe area layout guide. Let’s talk a little about what layout guides are and how you should use them to make sure your application does not fall outside the view area of the user.\n\n### Deprecated layout guide\n\nBefore iOS 11, Apple provided the `topLayoutGuide` and `bottomLayoutGuide`. With these guides, you could constrain your content so it does not get hidden by elements at the top of the view or elements at the bottom.\n\n![autolayout-6-1](//images.ctfassets.net/1es3ne0caaid/443glk5Y1OQ6iKWOoq0WoG/15e7c9084ba02760f1d35b9ee27f3cdc/autolayout-6-1.png)\n\nThese layout guides were important when developing iOS apps pre-iOS 11 era.\n\n### Safe area layout guide\n\nThe `safeAreaLayoutGuide` was introduced in iOS 11 while the top and bottom layout guides were deprecated. The safe area layout guide is a single rectangular guide that encompasses the entire view.\n\nBecause the safe area layout guide is a single guide that is boxed in, it’s a lot easier to work with this layout guide. \n\n![autolayout-6-2](//images.ctfassets.net/1es3ne0caaid/2wmKb8ceuoAQsQq0EyKe6y/b6cd516285560c38f222db1dc8ceab54/autolayout-6-2.png)\n \nAs seen above, the safe area layout guide will box in the areas that are safe to add content to. Adding content outside this area may cause it to be hidden by the content outside the safe area, thus making the content there potentially unusable.\n\nSafe area layout guides work differently for each device. The iPhone X, or later, has a bottom layout guide that considers the on-screen home button. The iPads have a bigger safe area layout guide, and the iPhone 5s, a smaller one.\n\nTo demonstrate how the safe area layout guide helps us keep content in check, let’s create a sample project and play around with it.\n\n## Creating a sample Xcode project\n\nFor the purposes of this chapter, we will be creating a sample Xcode application. We will be playing around with this sample app throughout this chapter.\n\nLaunch Xcode and click **Create a new Xcode project**. Next, select **Single View App** in Xcode.\n\n![autolayout-6-3](//images.ctfassets.net/1es3ne0caaid/4El7UnbSjS0QG88cCicME2/91b538ee41b3284b2b8ee653252039b7/autolayout-6-3.png)\n\nIn the project, open the `Main.storyboard` file. We will be using the storyboard to play around with views and layouts. \n\nNow that we have the sample project, let’s continue.\n\n## Working with the safe area layout guide\n\nOpen the main storyboard and drag a label to the topmost left of the screen. Now drag another label to the top right of the screen as seen below:\n\n![autolayout-6-4](//images.ctfassets.net/1es3ne0caaid/6g9CZV4Q6cMSemgWEuCasI/27d6a91b590ab9819ade13597cd56d2c/autolayout-6-4.gif)\n\nAfter creating the labels, let’s add some constraints to them. We will add the first constraints to the label on the topmost left. We want to make sure it is pinned to the top left of the view.\n\n![autolayout-6-5](//images.ctfassets.net/1es3ne0caaid/2pX8cnJFWEYueAmq4wwOk6/ee809bb67c43b2fc7366bd92d47a16bd/autolayout-6-5.png)\n\nNow for the second label, add the constraints as seen below:\n\n![autolayout-6-6](//images.ctfassets.net/1es3ne0caaid/6hxXiJpkGsagwOg8qC6W0m/dbbb39ff23c627bcbe8987815e7e77e0/autolayout-6-6.png)\n \nNote that in the second example, we clicked the little bottom facing arrow beside the number to reveal a drop-down menu. In this menu, we have the options of setting **Safe Area** or **View**. The safe area refers to the safe area layout guide, while the view is the super view.\n\nNow launch the application in an iPhone X simulator, and then in an iPhone 8 simulator:\n\n![autolayout-6-7](//images.ctfassets.net/1es3ne0caaid/4Ys9HuzwyIo0wg0gOaOQWi/d970a33f77bfa39a10ea4a17410829d8/autolayout-6-7.png)\n\nAs seen above, the label pinned to the top super view overlaps with the status bar in both cases while the one pinned to the safe area layout guide is not.\n\nLet’s do the same thing for the bottom of the screen. Add two new labels one at the bottom left and one at the bottom right of the screen. Next, add the following constraints to the label at the bottom left:\n\n![autolayout-6-8](//images.ctfassets.net/1es3ne0caaid/4AbjEQOz6wwAs060eyCwMa/6d3516140ebe13d9421fe51b09d2f7e8/autolayout-6-8.png)\n\n\u003e Make sure you set it to pin to the super view, and not the safe area.\n\nNext, add the following constraints to the bottom right label:\n\n![autolayout-6-9](//images.ctfassets.net/1es3ne0caaid/5XxrRouB44WcCkscMmqCWs/83668a2638b9bdbd7aef40b9547343fc/autolayout-6-9.png)\n\n\u003e Make sure you pin it to the safe area not the super view,\n\nNow launch the application once more in an iPhone X simulator and an iPhone 8.\n\n![autolayout-6-10](//images.ctfassets.net/1es3ne0caaid/16Iv7d7hCcSgKCKuqeGmge/1ae8220e382fa40ca85529ebc6ee333d/autolayout-6-10.png)\n\nJust like before, the label pinned to the bottom left super view overlaps with the content at the bottom of the screen on the iPhone X. The label on the right pinned to the safe area view does not. \n\nNotice, though, on the iPhone 8 the labels does not seem to overlap any content. In fact, the label seems just fine whether it’s pinned to the safe area guide or not. This is one reason why you should always pin to the safe area layout. Depending on the device the application is being loaded on, there might be overlaps.\n\nLet’s see another reason to add views within the safe area. Open the storyboard and click on **Editor \u003e  Embed In \u003e Navigation Controller** to embed the current view controller inside a navigation controller:\n\n![autolayout-6-11](//images.ctfassets.net/1es3ne0caaid/2HKKvfhVe8Ig8WG8UykQEe/bae4ed0614747113be8591d6738f1407/autolayout-6-11.png)\n \nNow run the application in a simulator. You will see that the label that was pinned to the super view was completely hidden by the navigation bar. However, the other label that was pinned to the safe area readjusted to make sure it is in the viewing area of the application. \n \n![autolayout-6-12](//images.ctfassets.net/1es3ne0caaid/3iZ931mfLOo02WkYCmyWK4/c1b4e2b685279adb6b8c7cb8456128be/autolayout-6-12.png)\n\nUsing the safe area layout guide is invaluable when working with Auto Layout.\n\n## Conclusion\n\nIn this part of the series, we have looked into layout guides and why they are invaluable when building layouts using Auto Layout.\n\nIn this tutorial series, we have taken a deep dive into Auto Layout and how you can use it to create amazing layouts that work across multiple devices. As with everything else, practice makes perfect. Practice using Auto Layout in your projects and you’ll get better at it.\n\nThe source code to the application built in this series is available on [GitHub](https://github.com/neoighodaro/getting-started-with-autolayout).","metaTitle":"Getting started with Auto Layout in Swift - Part 6","metaDescription":"This six part series takes an in-depth look at Auto Layout in Swift. You will learn how to use Auto Layout to create responsive interfaces for iOS apps. In part six, learn about layout guides."}}],"defaultValue":"Swift","queryType":"tag","pages":7,"route":"/tutorials","query":{"tag":"Swift","page":"3"}}},"page":"/tutorials","query":{"tag":"Swift","page":"3"},"buildId":"5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f","isFallback":false,"customServer":true,"ids":[]}</script><script nomodule="" src="_next/static/runtime/polyfills-6cf13c089a5684eabd63.js"></script><script async="" data-next-page="/tutorials" src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/pages/tutorials.js"></script><script async="" data-next-page="/_app" src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/pages/_app.js"></script><script src="_next/static/runtime/webpack-b65cab0b00afd201cbda.js" async=""></script><script src="_next/static/chunks/framework.8f8ac35b6c50c5d44e18.js" async=""></script><script src="_next/static/chunks/commons.5bea89f818e6d7d50d28.js" async=""></script><script src="_next/static/runtime/main-584a95128cf78f0fbea3.js" async=""></script><script src="_next/static/chunks/76f4f697.bba43166686acfbb5f45.js" async=""></script><script src="_next/static/chunks/40541903ab1c1ac841a57dd2b1fba9dfbed8c9ab.d608edb81271263c90db.js" async=""></script><script src="_next/static/chunks/5cad565c69a90ad2361a05d3169ab69cc3ef1b5b.50169a8607cbe0f0091c.js" async=""></script><script src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/_buildManifest.js" async=""></script><script src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/_ssgManifest.js" async=""></script><link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/nprogress@0.2.0/nprogress.css"/></body>
