rgin:0 auto;padding:1rem;}</style><div class="css-1ixj8zf e4ekq4e6"><style data-emotion-css="v8rk0o">.css-v8rk0o{font-family:maison-neue,sans-serif;font-weight:300;line-height:1.6;font-size:18px;color:#300D4F;margin-bottom:1rem;color:#ffffff;font-size:14px !important;margin-bottom:0.5rem;}@media all and (min-width:400px){.css-v8rk0o{font-size:18px;}}@media all and (min-width:760px){.css-v8rk0o{font-size:18px;}}@media all and (min-width:960px){.css-v8rk0o{font-size:18px;}}@media all and (min-width:1200px){.css-v8rk0o{font-size:18px;}}.css-v8rk0o a{color:#300D4F;font-weight:700;-webkit-text-decoration:none;text-decoration:none;border-bottom:2px solid #C223CE;}.css-v8rk0o a:hover{color:white;background-color:#300D4F;border-bottom:none;}</style><p class="css-v8rk0o e4ekq4e7"> <!-- -->2020<!-- --> Pusher Ltd. All rights reserved.</p><p class="css-v8rk0o e4ekq4e7">Pusher Limited is a company registered in England and Wales (No. 07489873) whose registered office is at 160 Old Street, London, EC1V 9BW.</p></div></div></main><footer></footer></div></div><script id="__NEXT_DATA__" type="application/json">{"props":{"pageProps":{"postCount":14,"posts":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3HzlrLfzbqicQUwSmy602E","type":"Entry","createdAt":"2018-07-12T21:17:39.261Z","updatedAt":"2018-07-12T21:17:39.261Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build an Android poll app with push notifications","url":"android-poll-push-notifications","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/kotlin-python-poll","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"644GONySFGieg4wAUc8k0C","type":"Entry","createdAt":"2018-02-12T14:37:53.963Z","updatedAt":"2019-08-28T08:41:31.723Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":4,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Beams","url":"beams","featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3EB32t4KaI0IGIEI6AocoA","type":"Entry","createdAt":"2018-02-20T16:52:11.196Z","updatedAt":"2018-02-26T12:31:10.440Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Android","url":"android","type":"operating system","featured":true}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4KfWHAtURiWQQ6k2A6ueAY","type":"Entry","createdAt":"2018-02-12T14:45:59.264Z","updatedAt":"2018-02-12T14:45:59.264Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Flask","url":"flask","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"54VH6KuDAsYY80KIeQE0OO","type":"Entry","createdAt":"2018-02-12T14:50:20.551Z","updatedAt":"2018-02-26T12:31:15.549Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Kotlin","url":"kotlin","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2SxOuediAgcSuiWyUmkw40","type":"Entry","createdAt":"2018-02-12T15:09:21.130Z","updatedAt":"2018-09-05T15:44:43.776Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Beams","url":"push-notifications","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1qG3Xd9hBSImsOO20kKoiA","type":"Entry","createdAt":"2018-02-05T11:18:51.856Z","updatedAt":"2018-02-12T14:41:11.935Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Python","url":"python","type":"language"}}],"reviewDate":"2018-07-09","description":"Build an Android app in Kotlin, with a backend in Python. Users are able to vote, and see other votes displayed in realtime. They will also receive push notifications when a new vote is cast.","prerequisite":"You will need Android Studio 3+ and virtualenv installed on your machine. Basic knowledge of Android development is required.","content":"The web has become so dynamic that it's weird to have to refresh anything anymore. We expect instant feedback from whatever application we are using and whatever action we are taking on the application.\n\nPolls adopt realtime technologies to give the owners live updates. This has become a major feature in top social media platforms and it is most essential when you need to perform quick surveys. Popular services like Twitter have adopted polls as a part of their services and it works well to gather user sentiments and thoughts.\n\nIn this tutorial, you will learn how to build a realtime poll. We will be using Kotlin, Flask and Pusher Channels. By the time we are done, we will have an application that looks like this:\n\n![pythonball-demo](//images.ctfassets.net/1es3ne0caaid/3R4OATvRi8qEQoqM0mOU24/58776a5c2a0aeb0c3dab6a36358eb149/pythonball-demo.gif)\n\n\n## Prerequisites\n\nIn other to follow this tutorial, you need the following:\n\n\n- Android Studio. Version \u003e= 3.x is recommended. Download [here](https://developer.android.com/studio/).\n- [Flask](http://flask.pocoo.org/) - you will be guided on how to install it in the post.\n- [Virtualenv](#) installed on your machine.\n- Introductory knowledge of Kotlin. Here are some [resources](https://kotlinlang.org/docs/resources.html).\n- [Pusher Beams](https://dash.pusher.com/beams) and [Pusher Channels](https://dashboard.pusher.com/) instance.\n\n\n\n## Setting up your Android application\n\nCreate a new project and follow the wizard to set it up. Name your app `RealtimePolls`. Enter your companys domain name. The company domain affects the package name. We will set the domain to `com.example` and the package name to `com.example.realtimepolls`. \n\nChoose your minimum SDK. API 19 (Android 4.4) is just fine. Continue with the `EmptyActivity` template chosen for you, and finish the wizard.\n\nLets stop here for now and set up our Pusher Beams and Channels application.\n\n\n## Creating your Beams and Channels instance\n\n### Setting up Pusher Channels\n\nLog in to your [Pusher dashboard](https://dashboard.pusher.com). If you dont have an account, create one. Your dashboard should look like this:\n\n![pythonball-new-channels-app](//images.ctfassets.net/1es3ne0caaid/5zBwuAVz6oksgasG0KICwg/7ba6bb2a397765eb2bcfca10c213b7f5/pythonball-new-channels-app.png)\n\nCreate a new Channels app. You can easily do this by clicking the big **Create new Channels app** card at the bottom right. When you create a new app, you are provided with keys. Keep them safe as you will soon need them.\n\n### Getting your FCM key\n\nBefore you can start using Beams, you need an FCM key and a google-services file because Beams relies on Firebase. Go to your [Firebase console](https://console.firebase.google.com/u/0/) and create a new project.\n\nWhen you get to the console, click the **Add project** card to initialize the app creation wizard. Add the name of your project. Read and accept the terms of conditions. After this, you will be directed to the project overview screen. Choose the **Add Firebase to your Android app** option. The next screen will require the package name of your app.\n\nAn easy way to get the package name of your app is from your `AndroidManifest.xml` file. Check the `\u003cmanifest\u003e` tag and copy the value of the `package` attribute. Another place you can find this is your app-module `build.gradle` file. Look out for the `applicationId` value. When you enter the package name and click **Register app.** \n\nNext, download your `google-services.json` file. After you have downloaded the file, you can skip the rest of the process. Add the downloaded file to the app folder of your app `RealtimePolls/app`.\n\n![pythonball-google-services-json](//images.ctfassets.net/1es3ne0caaid/7pwITIZFiEMOQ8WeskYkac/c1c4f8924c16a0243d46dff5471ab371/pythonball-google-services-json.png)\n\nNext, go to your Firebase project settings, under the Cloud messaging tab, copy your server key.\n\n### Setting up Pusher Beams\n\nNext, log in to the new [Pusher dashboard](https://dash.pusher.com/), in here we will create a Pusher Beams instance. You should sign up if you dont have an account yet. Click on the **Beams** button on the sidebar then click **Create**, this will launch a pop up to **Create a new Beams instance** and give it a name.\n\n![pythonball-new-beams](//images.ctfassets.net/1es3ne0caaid/3P9eqXnJi8swOEoykKA40O/f21b1d5b30b63508509218a3f8210ac1/pythonball-new-beams.png)\n\nAs soon as you create the instance, you will be presented with a quickstart guide. Select the **ANDROID** quickstart\n\n![pythonball-beams-quickstart](//images.ctfassets.net/1es3ne0caaid/BafUtlnfiKw4aEKoOsoKi/aade9a902389f94ee252b21823879eb4/pythonball-beams-quickstart.png)\n\nThe next screen requires the FCM key you copied earlier. After you add the FCM key, you can exit the quickstart guide.\n\n\n## Building the Android application\n\n### Adding our dependencies\n\nReopen our project in Android Studio. The next thing we need to do is install the necessary dependencies for our app. Open your app-module `build.gradle` file and add these:\n\n``` gradle\n    // File: ./app/build.gradle\n    dependencies {\n        // other dependencies...\n        implementation 'com.pusher:pusher-java-client:1.5.0'\n        implementation 'com.google.firebase:firebase-messaging:17.0.0'\n        implementation 'com.pusher:push-notifications-android:0.10.0'\n        implementation 'com.pusher:pusher-java-client:1.5.0'    \n        implementation \"com.squareup.retrofit2:retrofit:2.4.0\"\n        implementation \"com.squareup.retrofit2:converter-scalars:2.4.0\"\n        implementation \"com.squareup.retrofit2:converter-gson:2.3.0\"\n    }\n    apply plugin: 'com.google.gms.google-services'\n```\nAnd in the project `build.gradle` file add this:\n\n``` gradle\n    // File: ./build.gradle\n    dependencies {\n        // add other dependencies...\n        classpath 'com.google.gms:google-services:4.0.0'\n    }\n```\n\nAfter adding the dependencies, sync your Gradle files so that the dependencies are imported. \n\n### Developing the logic for our Android application\n\nPusher Beams makes use of a service to notify the app when there is a remote message. Create a new service named `NotificationsMessagingService` and paste this:\n\n``` kotlin\n    // File: ./app/src/main/java/com/example/realtimepolls/NotificationsMessagingService.kt\n    import android.app.NotificationChannel\n    import android.app.NotificationManager\n    import android.app.PendingIntent\n    import android.content.Intent\n    import android.os.Build\n    import android.support.v4.app.NotificationCompat\n    import android.support.v4.app.NotificationManagerCompat\n    import com.google.firebase.messaging.RemoteMessage\n    import com.pusher.pushnotifications.fcm.MessagingService\n    \n    class NotificationsMessagingService : MessagingService() {\n    \n        override fun onMessageReceived(remoteMessage: RemoteMessage) {\n            val notificationId = 10\n            val channelId  = \"polls\"\n            lateinit var channel:NotificationChannel\n            val intent = Intent(this, MainActivity::class.java)\n            intent.flags = Intent.FLAG_ACTIVITY_NEW_TASK or Intent.FLAG_ACTIVITY_CLEAR_TASK\n            val pendingIntent = PendingIntent.getActivity(this, 0, intent, 0)\n            val mBuilder = NotificationCompat.Builder(this, channelId)\n                    .setSmallIcon(R.mipmap.ic_launcher)\n                    .setContentTitle(remoteMessage.notification!!.title!!)\n                    .setContentText(remoteMessage.notification!!.body!!)\n                    .setContentIntent(pendingIntent)\n                    .setPriority(NotificationCompat.PRIORITY_DEFAULT)\n                    .setAutoCancel(true)\n    \n            if (Build.VERSION.SDK_INT \u003e= Build.VERSION_CODES.O) {\n                val notificationManager = applicationContext.getSystemService(NotificationManager::class.java)\n                val name = getString(R.string.channel_name)\n                val description = getString(R.string.channel_description)\n                val importance = NotificationManager.IMPORTANCE_DEFAULT\n                channel = NotificationChannel(\"world-cup\", name, importance)\n                channel.description = description\n                notificationManager!!.createNotificationChannel(channel)\n                notificationManager.notify(notificationId, mBuilder.build())\n    \n            } else {\n                val notificationManager =  NotificationManagerCompat.from(this)\n                notificationManager.notify(notificationId, mBuilder.build())\n            }\n        }\n    }\n```\n\nThe method `onMessageReceived` is called when a push notification is received on the device. The message received is then used to display a notification for the user. \n\nAdd the following to your `string.xml` file:\n\n``` xml\n    // File: ./app/src/main/res/values/strings.xml\n    \u003cstring name=\"channel_name\"\u003ePolls\u003c/string\u003e\n    \u003cstring name=\"channel_description\"\u003eTo receive updates about polls\u003c/string\u003e\n```\n\nAdd the service to your `AndroidManifest.xml` file like so:\n\n``` xml\n    // File: ./app/src/main/AndroidManifest.xml\n    \u003capplication\n              ...\u003e\n    \n        [...]\n        \n        \u003cservice android:name=\".NotificationsMessagingService\"\u003e\n            \u003cintent-filter android:priority=\"1\"\u003e\n                \u003caction android:name=\"com.google.firebase.MESSAGING_EVENT\" /\u003e\n            \u003c/intent-filter\u003e\n        \u003c/service\u003e\n        \n    \u003c/application\u003e\n```\n\nCreate an interface named `ApiService` and paste the following:\n\n``` kotlin\n    // File: ./app/src/main/java/com/example/realtimepolls/ApiService.kt\n    import okhttp3.RequestBody\n    import retrofit2.Call\n    import retrofit2.http.Body\n    import retrofit2.http.GET\n    import retrofit2.http.POST\n    \n    interface ApiService {\n    \n        @GET(\"/generate\")\n        fun generatePolls(): Call\u003cString\u003e\n    \n        @POST(\"/update\")\n        fun updatePolls(@Body  body: RequestBody):Call\u003cString\u003e\n    \n    }\n```\n\nThis interface contains the endpoints to be accessed during the course of this tutorial. There are two endpoints, the first one is to get the question and options from the server while the second is to send the option selected by the user to the server.\n\nSince internet connection is required for some functionalities, you need to request for the internet permissions. Add this to your `AndroidManifest.xml` file:\n\n``` xml\n    // File: ./app/src/main/AndroidManifest.xml\n    \u003cmanifest xmlns:android=\"http://schemas.android.com/apk/res/android\"\n        package=\"com.example.realtimepolls\"\u003e\n    \n        \u003cuses-permission android:name=\"android.permission.INTERNET\"/\u003e\n        \n        [...]\n    \n    \u003c/manifest\u003e\n```\n\nNext, lets design the layout of the app. The app will contain radio buttons so as to ensure that only one option is chosen. Open your `activity_main.xml` file and paste this:\n\n``` xml\n    // File: ./app/src/main/res/layout/activity_main.xml\n    \u003c?xml version=\"1.0\" encoding=\"utf-8\"?\u003e\n    \u003cScrollView xmlns:android=\"http://schemas.android.com/apk/res/android\"\n        xmlns:app=\"http://schemas.android.com/apk/res-auto\"\n        xmlns:tools=\"http://schemas.android.com/tools\"\n        android:layout_width=\"match_parent\"\n        android:layout_height=\"match_parent\"\u003e\n    \n        \u003candroid.support.constraint.ConstraintLayout\n            android:layout_width=\"match_parent\"\n            android:layout_height=\"wrap_content\"\n            tools:context=\".MainActivity\"\u003e\n    \n            \u003cTextView\n                android:id=\"@+id/poll_title\"\n                android:layout_width=\"0dp\"\n                android:layout_height=\"wrap_content\"\n                android:layout_margin=\"10dp\"\n                android:textSize=\"20sp\"\n                app:layout_constraintLeft_toLeftOf=\"parent\"\n                app:layout_constraintRight_toRightOf=\"parent\"\n                app:layout_constraintTop_toTopOf=\"parent\" /\u003e\n    \n            \u003cRadioGroup xmlns:android=\"http://schemas.android.com/apk/res/android\"\n                android:id=\"@+id/radio_group\"\n                android:layout_width=\"wrap_content\"\n                android:layout_height=\"wrap_content\"\n                android:layout_margin=\"20dp\"\n                android:orientation=\"vertical\"\n                app:layout_constraintLeft_toLeftOf=\"parent\"\n                app:layout_constraintTop_toBottomOf=\"@id/poll_title\"\u003e\n    \n                \u003cRadioButton\n                    android:id=\"@+id/choice_1\"\n                    android:layout_width=\"wrap_content\"\n                    android:layout_height=\"wrap_content\" /\u003e\n    \n                \u003candroid.support.v4.widget.ContentLoadingProgressBar\n                    android:id=\"@+id/progress_choice_1\"\n                    style=\"@style/Base.Widget.AppCompat.ProgressBar.Horizontal\"\n                    android:layout_width=\"200dp\"\n                    android:layout_height=\"50dp\"\n                    android:layout_marginStart=\"10dp\"\n                    android:max=\"100\" /\u003e\n    \n                \u003cRadioButton\n                    android:id=\"@+id/choice_2\"\n                    android:layout_width=\"wrap_content\"\n                    android:layout_height=\"wrap_content\" /\u003e\n    \n                \u003candroid.support.v4.widget.ContentLoadingProgressBar\n                    android:id=\"@+id/progress_choice_2\"\n                    style=\"@style/Base.Widget.AppCompat.ProgressBar.Horizontal\"\n                    android:layout_width=\"200dp\"\n                    android:layout_height=\"50dp\"\n                    android:layout_marginStart=\"10dp\"\n                    android:max=\"100\" /\u003e\n    \n                \u003cRadioButton\n                    android:id=\"@+id/choice_3\"\n                    android:layout_width=\"wrap_content\"\n                    android:layout_height=\"wrap_content\" /\u003e\n    \n                \u003candroid.support.v4.widget.ContentLoadingProgressBar\n                    android:id=\"@+id/progress_choice_3\"\n                    style=\"@style/Base.Widget.AppCompat.ProgressBar.Horizontal\"\n                    android:layout_width=\"200dp\"\n                    android:layout_height=\"50dp\"\n                    android:layout_marginStart=\"10dp\"\n                    android:max=\"100\" /\u003e\n    \n            \u003c/RadioGroup\u003e\n    \n            \u003cButton\n                android:id=\"@+id/vote\"\n                android:layout_width=\"match_parent\"\n                android:layout_height=\"wrap_content\"\n                android:layout_margin=\"10dp\"\n                android:text=\"Vote\"\n                android:textAllCaps=\"false\"\n                app:layout_constraintLeft_toLeftOf=\"parent\"\n                app:layout_constraintRight_toRightOf=\"parent\"\n                app:layout_constraintTop_toBottomOf=\"@+id/radio_group\" /\u003e\n    \n        \u003c/android.support.constraint.ConstraintLayout\u003e\n    \n    \u003c/ScrollView\u003e\n```\n\nThe layout contains radio buttons with a progress bar below each of them. The progress bar will give a visual feedback of the vote count.\n\nGo to your `MainActivity` file and add this:\n\n``` kotlin\n    // File: ./app/src/main/java/com/example/realtimepolls/MainActivity.kt\n    import android.os.Bundle\n    import android.util.Log\n    import android.widget.Toast\n    import android.support.v7.app.AppCompatActivity\n    import com.pusher.client.Pusher\n    import com.pusher.client.PusherOptions\n    import com.pusher.pushnotifications.PushNotifications\n    import kotlinx.android.synthetic.main.activity_main.*\n    import okhttp3.MediaType\n    import okhttp3.OkHttpClient\n    import okhttp3.RequestBody\n    import org.json.JSONObject\n    import retrofit2.Call\n    import retrofit2.Callback\n    import retrofit2.Response\n    import retrofit2.Retrofit\n    import retrofit2.converter.scalars.ScalarsConverterFactory\n    \n    class MainActivity : AppCompatActivity() {\n    \n        private val apiService: ApiService by lazy {\n            Retrofit.Builder()\n                    .baseUrl(\"http://10.0.2.2:5000/\")\n                    .addConverterFactory(ScalarsConverterFactory.create())\n                    .client(OkHttpClient.Builder().build())\n                    .build().create(ApiService::class.java)\n        }\n    \n        val tag = \"MainActivity\"\n    \n        override fun onCreate(savedInstanceState: Bundle?) {\n            super.onCreate(savedInstanceState)\n            setContentView(R.layout.activity_main)\n            generatePolls()\n            setupPusher()\n            setupBeams()\n            setupClickListener()\n        }\n    }\n```\n\nAbove, the class variables `apiService` and `tag` are declared. The first is to be used to make API calls to the local server while the second will be used for logging. In the `onCreate` method, there are some other custom methods called. Lets create them.\n\nFirst is the `generatePolls` method. Paste the function in your `MainActivity` class:\n\n``` kotlin\n    private fun generatePolls() {\n        apiService.generatePolls().enqueue(object : Callback\u003cString\u003e {\n            override fun onFailure(call: Call\u003cString\u003e?, t: Throwable?) {\n    \n            }\n            \n            override fun onResponse(call: Call\u003cString\u003e?, response: Response\u003cString\u003e?) {\n                val jsonObject = JSONObject(response!!.body())\n                poll_title.text = jsonObject.getString(\"title\")\n                choice_1.text = jsonObject.getString(\"choice1\")            \n                choice_2.text = jsonObject.getString(\"choice2\")\n                choice_3.text = jsonObject.getString(\"choice3\")\n            }\n        })\n    }\n```\n\nThis method makes a network call to the server to get the poll question and options and populate the questions and options to the layout. \n\nNext, is the `setupPusher` method. Add the following to the `MainActivity` class:\n\n``` kotlin\n    private fun setupPusher() {\n        val options = PusherOptions()\n        options.setCluster(PUSHER_APP_CLUSTER)\n        val pusher = Pusher(PUSHER_API_KEY, options)\n        val channel = pusher.subscribe(\"polls\")\n    \n        channel.bind(\"vote\") { channelName, eventName, data -\u003e\n            Log.d(tag, data)\n            val jsonObject = JSONObject(data)\n    \n            runOnUiThread {\n                progress_choice_1.progress = jsonObject.getInt(\"1\")\n                progress_choice_2.progress = jsonObject.getInt(\"2\")\n                progress_choice_3.progress = jsonObject.getInt(\"3\")\n            }\n        }\n        \n        pusher.connect()\n    }\n```\n\u003e Replace the `PUSHER_KEY_*` placeholders with the keys from your Pusher Channels dashboard.\n\nThis method subscribes to the `polls` channel and listens to the `vote` event. Here, what is expected from the Pusher event is the score in percent of each option of the poll. The results are then populated to their respective progress-bars on the UI thread.\n\nNext, create the `setupBeams` function and add it to the same class:\n\n``` kotlin\n    private fun setupBeams() {\n        PushNotifications.start(applicationContext, \"PUSHER_BEAMS_INSTANCE_ID\")\n        PushNotifications.subscribe(\"polls-update\")\n    }\n```\n\nThis method above initializes Pusher Beams and subscribes to the `polls-update` event.\n\n\n\u003e Replace `PUSHER_BEAMS_INSTANCE_ID` with the instance ID from your Beams dashboard.\n\nFinally, create the `setupClickListener` and add it to the class:\n\n``` kotlin\n    private fun setupClickListener() {\n        vote.setOnClickListener {\n            val checkedButton = radio_group.checkedRadioButtonId\n            if (checkedButton == -1) {\n                Toast.makeText(this, \"Please select an option\", Toast.LENGTH_SHORT).show()\n            } else {\n                Log.d(tag, checkedButton.toString())\n                val selectedId = when (checkedButton) {\n                    R.id.choice_1 -\u003e 1\n                    R.id.choice_2 -\u003e 2\n                    R.id.choice_3 -\u003e 3\n                    else -\u003e -1\n                }\n    \n                val jsonObject = JSONObject()\n                jsonObject.put(\"option\", selectedId)\n    \n                val body = RequestBody.create(MediaType.parse(\"application/json\"), jsonObject.toString())\n    \n                apiService.updatePolls(body).enqueue(object : Callback\u003cString\u003e {\n                    override fun onFailure(call: Call\u003cString\u003e?, t: Throwable?) {\n                        Log.d(tag, t?.localizedMessage)\n                    }\n    \n                    override fun onResponse(call: Call\u003cString\u003e?, response: Response\u003cString\u003e?) {\n                        Log.d(tag, response?.body())\n                    }\n                })\n            }\n        }\n    }\n```\n\nThis method above contains the click listener added to the vote button. The user must choose an option for the vote to be recorded. Based on the choice of the user, a unique ID is sent to the server to update the poll and trigger a Pusher event.\n\nThats all for the Android application. Lets build a simple Python backend.\n\n\n## Building your backend\n\nLets create our project folder, and activate a virtual environment in it. Run the commands below:\n\n```\n    $ mkdir pypolls\n    $ cd pypolls\n    $ virtualenv .venv\n    $ source .venv/bin/activate # Linux based systems\n    $ \\path\\to\\env\\Scripts\\activate # Windows users\n```\n\nNow that we have the virtual environment setup, we can install Flask within it with this command:\n\n```\n    $ pip install flask\n```\nNext, run the following command to set the Flask environment to development (on Linux based machines):\n\n```\n    $ export FLASK_ENV=development\n```\nIf you are on Windows, the environment variable syntax depends on command line interpreter. On Command Prompt:\n\n```\n    C:\\path\\to\\app\u003eset FLASK_APP=app.py\n```\n\nAnd on PowerShell:\n\n```\n    PS C:\\path\\to\\app\u003e $env:FLASK_APP = \"app.py\"\n```\n\nNow we need to install some of the other dependencies:\n\n```\n    $ pip install pusher pusher_push_notifications\n    $ pip install --ignore-installed pyopenssl\n```\n\nWhen the installation is complete, create the main and only Python file called `app.py` and paste the following code:\n\n``` python\n    // File: ./app.py\n    # Imports\n    from flask import Flask, jsonify, request, json\n    from pusher import Pusher\n    from pusher_push_notifications import PushNotifications\n    \n    app = Flask(__name__)\n    pn_client = PushNotifications(\n        instance_id='YOUR_INSTANCE_ID_HERE',\n        secret_key='YOUR_SECRET_KEY_HERE',\n    )\n    \n    pusher = Pusher(app_id=u'PUSHER_APP_ID', key=u'PUSHER_APP_KEY', secret=u'PUSHER_SECRET', cluster=u'PUSHER_CLUSTER')\n    \n    # Variables to hold scores of polls\n    choice1 = 0\n    choice2 = 0\n    choice3 = 0\n    \n    # Route to send poll question\n    @app.route('/generate')\n    def send_poll_details():\n        return jsonify({'title':'Who will win the 2018 World Cup','choice1': 'Germany', 'choice2':'Brazil', 'choice3':'Spain'})\n        \n    @app.route('/update', methods=['POST'])\n    def update_poll():\n        global choice1, choice2, choice3\n        \n        req_data = request.get_json()\n        \n        user_choice = req_data['option']\n        \n        if user_choice == 1:\n            choice1 += 1\n        elif user_choice == 2:\n            choice2 += 1\n        elif user_choice == 3:\n            choice3 += 1\n        else:\n            print(\"User choose a wrong option\")\n        \n        total = 0.0\n        total = float(choice1 + choice2 + choice3)\n        \n        choice1_percent = (choice1/total) * 100\n        choice2_percent = (choice2/total) * 100\n        choice3_percent = (choice3/total) * 100\n        \n        pn_client.publish(\n        interests=['polls-update'],\n        publish_body={\n            'fcm': {\n                'notification': {\n                    'title': 'Polls update',\n                    'body': 'There are currently ' + str(int(round(total))) + 'vote(s) in the polls. Have you casted your vote?',\n                },\n            },\n        },\n        )\n        \n        pusher.trigger(u'polls', u'vote', {u'1': choice1_percent, '2':choice2_percent, '3':choice3_percent})\n        \n        return 'success', 200\n```\n\n\u003e Replace the `PUSHER_APP_*` keys with the credentials from your Pusher dashboard.\n\nThis is the only file needed for your Flask application. This snippet contains two endpoints to send out the poll question and to give current results.\n\nRun your Python app using this command:\n\n```\n    $ flask run\n```\n\nNow run your Android application in Android Studio and you should see something like this:\n\n![pythonball-demo](//images.ctfassets.net/1es3ne0caaid/3R4OATvRi8qEQoqM0mOU24/58776a5c2a0aeb0c3dab6a36358eb149/pythonball-demo.gif)\n\n## Conclusion\n\nIn this post, you have learned briefly about Flask and how to use it to develop RESTful APIs. You have also explored Pushers realtime technologies both on the client and server side. Feel free to check out the final [GitHub repo](https://github.com/neoighodaro/kotlin-python-poll) and play around with the application.","metaTitle":"Build an Android poll app with push notifications","metaDescription":"Use Kotlin to build an Android app, supported by a Python backend. Users are able to vote, and see other votes displayed in realtime. They will also receive push notifications when a new vote is cast."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6DAliEpVCgYEcIgOk4aYOu","type":"Entry","createdAt":"2018-07-06T09:15:49.491Z","updatedAt":"2018-07-06T09:15:49.491Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a live voting app with Ionic","url":"live-graph-ionic","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5Q1I2HN8pqGKG2wYcCeaO2","type":"Entry","createdAt":"2018-02-12T17:24:27.398Z","updatedAt":"2018-02-14T10:09:48.043Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christian Nwamba","url":"christian-nwamba","email":"nwambachristian@gmail.com","twitter":"https://twitter.com/codebeast","github":"https://github.com/christiannwamba","guestWriter":true}}],"projectRepo":"https://github.com/HackAfro/ionic-graph-app","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1SM1spRqTeua46Ew0W6UwC","type":"Entry","createdAt":"2018-02-12T14:40:52.995Z","updatedAt":"2018-02-12T14:40:52.995Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Angular","url":"angular","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2ogfivX0dSCcEuKkCOwAKe","type":"Entry","createdAt":"2018-02-12T14:50:39.667Z","updatedAt":"2018-02-26T12:31:15.529Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Cordova","url":"cordova","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5JtEWXIwaAaOM4KOWMO0qK","type":"Entry","createdAt":"2018-02-12T15:10:44.896Z","updatedAt":"2018-02-26T12:31:11.752Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Data Visualization","url":"data-visualization","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4E7t0TtVFSAyeeA0ckeYe0","type":"Entry","createdAt":"2018-02-12T15:09:41.899Z","updatedAt":"2018-02-26T12:31:11.510Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Chart","url":"realtime-chart","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6i1HPBqlwsE24YEKwqQyuq","type":"Entry","createdAt":"2018-02-12T15:09:31.050Z","updatedAt":"2018-02-26T12:31:11.548Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Graph","url":"realtime-graph","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1gcZ0mVsoeagKg0G0MOEgy","type":"Entry","createdAt":"2018-02-12T14:51:28.935Z","updatedAt":"2018-02-26T12:31:14.813Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"TypeScript","url":"typescript","type":"language"}}],"reviewDate":"2018-07-02","description":"Build a live voting app using Ionic, with a Node backend. The app replicates the behaviour of a Twitter poll: users can vote for who they expect to win the World Cup.","prerequisite":"You should have Node and npm installed on your machine. Familiarity with Ionic development will be helpful.","content":"\u003e **Data visualization** is viewed by many disciplines as a modern equivalent of visual communication. It involves the creation and study of the visual representation of data. \n\u003e - [Wikipedia](https://en.wikipedia.org/wiki/Data_visualization)\n\n An important advantage of data visualization is how it enables users to more effectively see connections as they are occurring between operating conditions and business performance. Adding realtime functionality using [Pusher](https://pusher.com) improves this experience as data changes are witnessed in realtime.\n\nWell be creating an application that will present data about how football fans predict who wins the current running World Cup. Using our application, users will complete a poll and then see the data from the polls in realtime.\n\nHeres a screenshot of the final product:\n\n![ionic-live-graph-demo](//images.ctfassets.net/1es3ne0caaid/6bQMCuvpks8wsgkOWwAGgq/f7e885b2d2af1dbd1a5dbbbe711d93a3/ionic-live-graph-demo.png)\n\n## Prerequisites\n\nTo follow this tutorial a basic understanding of [Angular](https://angular.io), Ionic and Node.js is required. Please ensure that you have Node and npm installed before you begin.\n\nIf you have no prior knowledge of Ionic, kindly follow the tutorial [here](https://ionicframework.com/docs/intro/tutorial/). Come back and finish the tutorial when youre done.\n\nWell be using these tools to build our application:\n\n- [Express](https://expressjs.com/) \n- [Node](https://nodejs.org/en/)\n- [Pusher](https://pusher.com)\n- [Ionic](https://ionicframework.com)\n- [Chart.js](http://www.chartjs.org/)\n\nWell be sending data to the server and using [Pushers](https://pusher.com) pub/sub pattern, well listen to and receive data in realtime. To make use of Pusher youll have to create an account [here](https://pusher.com). \n\nAfter account creation, visit the dashboard. Click **Create new Channels app**, fill out the details, click **Create my app**, and make a note of the details on the **App Keys** tab.\n\n\nLets build!\n\n## Setup and folder structure\n\nWell initialize our project using the Ionic CLI (command line interface). First, install the CLI by running `npm install -g ionic` in your terminal. [NPM](https://www.npmjs.com/) is a package manager used for installing packages. It will be available on your PC if you have [Node](https://nodejs.org/) installed.\n\nTo create a new Ionic project called `chat-app` using the CLI, open a terminal and run:   \n\n```\n    ionic start ionic-polls blank\n```\n\nThe command is simply telling the CLI to create a new project called `ionic-polls` without a template. \n\nFollow the prompt and integrate your app with Cordova to target IOS and Android.\n\n![ionic-geofence-integrate-cordova](//images.ctfassets.net/1es3ne0caaid/1IUUkS2jWEsGkaoC0ImGA0/a4419e24c715605029dc1edc7a6a60d5/ionic-geofence-integrate-cordova.png)\n\n Type **Y** to integrate Cordova into the application. The next prompt will ask if you want to integrate Ionic pro into the application. If you have a pro account type **Y** and **N** if you dont.\n\nThe Ionic team provides three ready made starter templates. You can check out the rest of the templates [here](https://ionicframework.com/getting-started).\n\nOpen the newly created folder, your folder structure should look something like this:\n\n```\n    chat-app/\n      resources/\n      node_modules/\n      src/\n        app/\n          app.component.html\n          app.module.ts\n          app.scss\n          ...\n```\n\nOpen a terminal inside the project folder and start the application by running `ionic serve`. A browser window should pop up and you should see a page like this.\n\n\n![ionic-chat-sentiment-ionic-serve](//images.ctfassets.net/1es3ne0caaid/cZJy8Cc9WgkoiOEKI44uA/8a6010cf3b3515127f486c76c18f33de/ionic-chat-sentiment-ionic-serve.png)\n\n## Installing dependencies\n\nNext, run the following commands in the root folder of the project to install dependencies.\n\n```\n    // install depencies required to build the server\n    npm install express body-parser dotenv pusher\n    \n    // front-end dependencies\n    npm install ng2-charts pusher-js\n```\n\nStart the Ionic development server by running `ionic serve` in a terminal in the root folder of your project.\n\n\n## Building our server\n\nWell build our server using [Express](https://expressjs.com). Express is a fast, unopinionated, minimalist web framework for [Node.js](https://nodejs.org/en/).\n\nCreate a file called `server.js` in the root of the project and update it with the code snippet below\n\n``` javascript\n    // server.js\n    \n    require('dotenv').config();\n    const express = require('express');\n    const bodyParser = require('body-parser');\n    const Pusher = require('pusher');\n    \n    const app = express();\n    const port = process.env.PORT || 4000;\n    const pusher = new Pusher({\n      appId: process.env.PUSHER_APP_ID,\n      key: process.env.PUSHER_KEY,\n      secret: process.env.PUSHER_SECRET,\n      cluster: process.env.PUSHER_CLUSTER,\n    });\n    \n    app.use(bodyParser.json());\n    app.use(bodyParser.urlencoded({extended: false}));\n    app.use((req, res, next) =\u003e {\n      res.header('Access-Control-Allow-Origin', '*');\n      res.header(\n        'Access-Control-Allow-Headers',\n        'Origin, X-Requested-With, Content-Type, Accept'\n      );\n      next();\n    });\n    \n    app.listen(port, () =\u003e {\n      console.log(`Server started on port ${port}`);\n    });\n```\n\nThe calls to our endpoint will be coming in from a different origin. Therefore, we need to make sure we include the CORS headers (`Access-Control-Allow-Origin`). If you are unfamiliar with the concept of CORS headers, you can find more information [here](https://developer.mozilla.org/en-US/docs/Web/HTTP/Access_control_CORS).\n\nThis is a standard Node application configuration, nothing specific to our app.\n\nCreate a Pusher account and a new Pusher Channels app if you havent done so yet and get your `appId`, `key` and `secret`. Create a file in the root folder of the project and name it `.env`. Copy the the following snippet into the `.env` file and ensure to replace the placeholder values with your Pusher credentials.\n\n```\n    // .env\n    \n    // Replace the placeholder values with your actual pusher credentials\n    PUSHER_APP_ID=PUSHER_APP_ID\n    PUSHER_KEY=PUSHER_KEY\n    PUSHER_SECRET=PUSHER_SECRET\n    PUSHER_CLUSTER=PUSHER_CLUSTER\n```\n\nWell make use of the `dotenv` library to load the variables contained in the `.env` file into the Node environment. The `dotenv` library should be initalized as early as possible in the application.\n\n\n### Sending votes\n\nTo let users send requests to the server, well create a route to handle incoming requests. Update your `server.js`  file with the code below. \n\n``` javascript\n    // server.js\n    require('dotenv').config();\n    ...\n    \n    app.use((req, res, next) =\u003e {\n      res.header('Access-Control-Allow-Origin', '*');\n      ...\n    });\n    \n    \n    app.post('/vote', (req, res) =\u003e {\n      const {body} = req;\n      const data = {\n        ...body,\n        // set the selected property of the body to true\n        selected: true,\n      };\n      // trigger a new-entry event on the vote-channel\n      pusher.trigger('vote-channel', 'new-entry', data);\n      res.json(data);\n    });\n    \n     ...\n```\n\n- We added a POST route(`/vote`) to handle incoming requests.\n- Using [object destructuring](https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/Destructuring_assignment), we got the body of the request.\n-  The trigger is achieved using the `trigger` method which takes the trigger identifier(`vote-channel`), an event name (`new-entry`), and a payload.\n- The payload being sent contains the body of the request sent in. The `selected` property of the payload is set to `true`.\n\nStart the server by running `node server` in a terminal in the root folder of your project.\n\n\n## Home view\n\nThe home view of the project will house both the polling area and the area where the data is visualized. Well present the user with options and a submit button to place vote.\n\nOpen the `home.html` file and replace it with the content below. The `home.html` file is in the `src/pages/home/` directory.\n\n``` html\n    \u003c!-- src/pages/home/home.html --\u003e\n    \n    \u003cion-header\u003e\n      \u003cion-navbar\u003e\n        \u003cion-title\u003e\n          Vote\n        \u003c/ion-title\u003e\n      \u003c/ion-navbar\u003e\n    \u003c/ion-header\u003e\n    \u003cion-content\u003e\n      \u003cdiv padding\u003e\n        \u003ch1 class=\"header\"\u003eWho will win the world cup?\u003c/h1\u003e\n        \u003cp class=\"sub-header\"\u003e* Place vote to see results\u003c/p\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"vote-area\" *ngIf=\"!voted\"\u003e\n        \u003cdiv class=\"options\"\u003e\n          \u003cbutton ion-button full class=\"option\" color=\"light\" [ngClass]=\"{active: selectedOption === option}\" *ngFor=\"let option of optionsArray\"\n            (click)=\"selectOption(option)\"\u003e{{options[option].name}}\u003c/button\u003e\n        \u003c/div\u003e\n        \u003cdiv\u003e\n          \u003cbutton ion-button block class=\"submit\" (click)=\"vote()\"\u003eSubmit Vote!\u003c/button\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"result-area\" *ngIf=\"voted\"\u003e\n        \u003c!-- Charts area --\u003e\n      \u003c/div\u003e\n    \u003c/ion-content\u003e\n```\n\n\n\n- In the code snippet above, we looped through `optionsArray` to create a view based on the players information. \n- The `vote` method will make use of the `HttpClient`  to send the users selection as a request to the server.\n- An option is active if the current `selectedOption` is equal to the options name.\n\nVariables used will be defined in the components TypeScript file.\n\n### Styling\n\nReplace the contents of `home.scss` with the following:\n\n``` scss\n    // src/pages/home/home.scss\n    \n    page-home {\n      .toolbar-background {\n        background: #1cd8d2; /* fallback for old browsers */\n        background: linear-gradient(to right, #93edc7, #1cd8d2);\n      }\n      .toolbar-title {\n        color: white;\n      }\n      .header {\n        font-size: 35px;\n        line-height: 1.1;\n      }\n       .sub-header{\n        margin: 0;\n        opacity: 0.5;\n        font-size: 13px;\n        font-weight: bold;\n      }\n      .options {\n        margin-top: 1.5rem;\n        padding: 0 17px 5px;\n        .option {\n          margin: 15px 0;\n          padding-top: 32px;\n          padding-bottom: 32px;\n          opacity: 0.6;\n          font-size: 17px;\n          font-weight: bold;\n          box-shadow: 0 1px 1px 0 rgba(0, 0, 0, 0.2);\n          \u0026.active {\n            border-left-width: 5px;\n            border-left-style: solid;\n            border-image: linear-gradient(to right, #93edc7, #1cd8d2) 1 100%;\n            box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.2);\n            opacity: 0.9;\n          }\n        }\n      }\n      .submit {\n        background: #1cd8d2; /* fallback for old browsers */\n        background: linear-gradient(to right, #93edc7, #1cd8d2);\n        border-radius: 0;\n        padding-top: 35px;\n        padding-bottom: 35px;\n        margin-bottom: 0;\n        font-size: 19px;\n        font-weight: bold;\n      }\n      .response{\n        @extend .submit;\n        margin-top: 5rem;\n      }\n    }\n```\n\n\nThese styles are meant to add a bit of life to our application. It also helps distinguish between states during application use. \n\n### Home component\n\nIn the HTML snippet, we made reference to some variables that werent yet defined, well create the variables here with the logic behind our application. \n\n``` typescript\n    // src/pages/home/home.ts\n    \n    import { Component, OnInit } from '@angular/core';\n    import { NavController } from 'ionic-angular';\n    import { HttpClient } from '@angular/common/http';\n    \n    @Component({\n      selector: 'page-home',\n      templateUrl: 'home.html',\n    })\n    export class HomePage implements OnInit {\n      constructor(\n        public navCtrl: NavController,\n        private http: HttpClient,\n      ) {}\n      options = {\n        germany: { name: 'Germany', votes: 0 },\n        spain: { name: 'Spain', votes: 0 },\n        france: { name: 'France', votes: 0 },\n        nigeria: { name: 'Nigeria', votes: 0 },\n      };\n      optionsArray = Object.keys(this.options);\n      chartData = this.optionsArray.map((val) =\u003e this.options[val].votes);\n      selectedOption = '';\n      chartType = 'doughnut';\n      voted = false;\n      selectOption(option) {\n        this.selectedOption = this.selectedOption !== option ? option : '';\n      }\n      computeData(option) {\n        this.options = {\n          ...this.options,\n          [option]: {\n            ...this.options[option],\n            votes: ++this.options[option].votes,\n          },\n        };\n        this.chartData = this.optionsArray.map((val) =\u003e this.options[val].votes);\n      }\n      vote() {\n        if (this.selectedOption) {\n          this.http\n            .post('http://localhost:4000/vote', { option: this.selectedOption })\n            .subscribe((res) =\u003e {\n              this.voted = true;\n            });\n        }\n      }\n      ngOnInit() {\n        \n      }\n    }\n```\n\n\n\n- `vote`: this method makes use of the native HttpClient service to make requests to our server. A request is sent, only if the user has made a selection. When a response is returned the `voted` property is set to true.\n\n\n- `computeData`: when a response is returned, this function takes the `option`  and increments the votes for the selected option.  \n\n\n- `selectOption`: this method will be used to set the `selectedOption` property to the `option`  param passed it. \n\nTo make use of the HttpClient service, well need to import the `HttpClientModule` into the `app.module.ts` file. Update your app module file as follows:\n\n``` typescript\n    // src/app/app.module.ts\n    \n    ...\n    import { HomePage } from '../pages/home/home';\n    import {HttpClient, HttpClientModule} from '@angular/common/http';\n    \n    @NgModule({\n      ...\n      imports: [\n        ...\n        HttpClientModule,\n      ],\n      ...\n      providers: [\n        ...\n        HttpClient\n      ]\n    })\n    export class AppModule {}\n```\n\n\nLets check how our application looks at this point. Make sure the server(`node server`) and Ionic dev server(`ionic serve`) are both running.\n\n![ionic-live-graph-homepage](//images.ctfassets.net/1es3ne0caaid/vW6xfrCOECykEMyOSum28/7eb2e7362cf2b6de7a182149e1503994/ionic-live-graph-homepage.png)\n\n\n## Introducing Pusher\n\nSo far we have an application that allows users be a part of the polling process but data updates arent happening in realtime.  Lets create a provider that will make it easier to include Pusher in our components.\n\nWell create a Pusher provider to be used application wide. The Ionic CLI can aid in the provider creation. Open a terminal in your projects root folder and run the following command.\n\n```\n    ionic generate provider pusher\n```\n\nThis command simply tells the CLI to generate a service named `pusher`. Now open the `pusher.ts` file in the `src/providers/pusher/` directory and update it with the code below.\n\n``` typescript\n    // src/providers/pusher/pusher.ts\n    \n    import { Injectable } from '@angular/core';\n    import Pusher from 'pusher-js';\n    \n    @Injectable()\n    export class PusherProvider {\n      constructor() {\n        const pusher = new Pusher('PUSHER_KEY', {\n          cluster: 'PUSHER_CLUSTER',\n        });\n        this.channel = pusher.subscribe('vote-channel');\n      }\n      channel;\n      public init() {\n        return this.channel;\n      }\n    }\n```\n\n- First, we initialize Pusher in the constructor. \n- The  `init` method returns the Pusher property we created. \n\n\n\u003e **Note**: ensure you replace the `PUSHER_KEY` and `PUSHER_CLUSTER` string with your actual credentials.\n  \n\nTo make the service available application wide, import it into the `app.module.ts` file.\n\n``` typescript\n    // app.module.ts\n    ...\n    import {HttpClient, HttpClientModule} from '@angular/common/http';\n    import { PusherProvider } from '../providers/pusher/pusher';\n    \n    @NgModule({\n      ...\n      providers: [\n        ...\n        PusherProvider,\n      ]\n    })\n    export class AppModule {}\n```\n\nThe next step is to include the provider in the `home.ts` file. Using the `PusherProvider`, well listen for `vote` events from the server and update our app in real time according to votes placed by users.\n \nOpen the `home.ts` file and update the `ngOnInit` lifecycle to listen for Pusher events.\n \n``` typescript\n    // src/pages/home/home.ts\n    ...\n    import { PusherProvider } from '../../providers/pusher/pusher';\n    \n    @Component({\n      selector: 'page-home',\n      templateUrl: 'home.html',\n    })\n    export class HomePage implements OnInit {\n      constructor(\n        ...\n        private pusher: PusherProvider\n      ) {}\n      ...\n      \n      ngOnInit() {\n        const channel = this.pusher.init();\n        channel.bind('new-entry', (data) =\u003e {\n          this.computeData(data.option);\n        });\n    }\n```\n\nNow our application should receive vote updates in realtime. Lets include a chart component to visualize the data in the application.\n\n## Charts component\n\nTo visualize the data in our application, well be making use of [ng2-charts](https://valor-software.com/ng2-charts/) to create charts and present the data in a graphical format. Lets make use of the components provided by the [ng2-charts](https://valor-software.com/ng2-charts/) library. Update the `home.html` file to include the `canvas` provided by [ng2-charts](https://valor-software.com/ng2-charts/).\n\nOpen the `home.html` file and update it with the contents below:\n\n``` html\n    // src/pages/home/home.html\n    \n    ...\n    \n    \u003cion-content\u003e\n      ...\n      \u003cdiv class=\"result-area\" *ngIf=\"voted\"\u003e\n        \u003ccanvas baseChart [data]=\"chartData\" [labels]=\"optionsArray\" [chartType]=\"chartType\"\u003e\u003c/canvas\u003e\n        \u003cdiv ion-button block class=\"response\"\u003eThank you for voting!\u003c/div\u003e\n      \u003c/div\u003e\n    \u003c/ion-content\u003e\n```\n\nTo make use of the `ng2-charts` package, well have to import the `ChartsModule` into our module file.\n\nUpdate the `app.module.ts` file like so:\n\n``` typescript\n    // src/app/app.module.ts\n    ...\n    import {HttpClient, HttpClientModule} from '@angular/common/http';\n    import {ChartsModule} from 'ng2-charts';\n    \n    @NgModule({\n      ...\n      imports: [\n        ...\n        HttpClientModule,\n        ChartsModule\n      ],\n      ...\n    })\n    ...\n```\n\n At this point, your application should have realtime updates when votes are placed. Ensure that the server is running alongside the Ionic development server. If not, run `node server` and `ionic serve` in two separate terminals. Both terminals should be opened in the root folder of your project.\n  \nTo test the realtime functionality of the application, open two browsers side-by-side and engage the application. Data updates should be in realtime.\n\n\n## Testing on mobile devices\n\nTo test the application on your mobile device, download the [IonicDevApp](https://ionicframework.com/docs/pro/devapp/) on your mobile device. Make sure your computer and your mobile device are connected to the same network. When you open the IonicDevApp, you should see Ionic apps running on your network listed. \n\n\n\u003e **Note:** Both the server(`node server`), ngrok for proxying our server and the Ionic dev server(`ionic serve`) must be running to get the application working. Run the commands in separate terminal sessions if you havent done so already.\n\n![ionic-geofence-in-app](//images.ctfassets.net/1es3ne0caaid/4Tto6qC43SyoeuuCwWk6kC/10b2fcce68e32c25cb8da5cfe7e635ad/ionic-geofence-in-app.png)\n\nTo view the application, click on it and you should see a similar view to what was in the browser. Sending messages to the server might have worked in the browser but localhost doesnt exist on your phone, so well need to create a proxy to be able to send messages from mobile. \n\n\n## Using Ngrok as a proxy\n\nTo create a proxy for our server, well download Ngrok. Visit the [download](https://ngrok.com/download) page on the Ngrok website. Download the client for your OS. Unzip it and run the following command in the folder where Ngrok can be found:\n\n```\n    ./ngrok http 4000\n```\n\n![ionic-geofence-ngrok](//images.ctfassets.net/1es3ne0caaid/5xPwNwr4DmMCA6k0MaaIqo/d569c60667d1da3659409f57b9a52da5/ionic-geofence-ngrok.png)\n\nCopy the forwarding url with `https` and place it in the `home.ts` file that previously had `http://localhost:4000/vote`. Please do not copy mine from the screenshot above. \n\n``` typescript\n    // src/pages/home/home.ts\n    ...\n    export class ChatComponent implements OnInit {\n      ...\n      vote() {\n        ...\n        this.http\n            .post('\u003cNGROK_URL\u003e/vote', data)\n            .subscribe((res) =\u003e {\n              this.voted = true;\n            });\n      }\n      ...\n    }\n    ...\n```\n\n\u003e Ensure to include the forwarding URL you copied where the placeholder string is\n\nNow you should be receiving messages sent from the phone on the browser. Preferably you can test it with two mobile devices connected to the same network.\n\n\n![ionic-live-graph-demo](//images.ctfassets.net/1es3ne0caaid/6bQMCuvpks8wsgkOWwAGgq/f7e885b2d2af1dbd1a5dbbbe711d93a3/ionic-live-graph-demo.png)\n\n\n\u003e **Note:** Both the server(`node server`), ngrok for proxying our server and the Ionic dev server(`ionic serve`) must be running to get the application working. Run the commands in separate terminal sessions if you havent done so already.\n\nTo build your application to deploy on either the AppStore or PlayStore, follow the instructions found [here](https://ionicframework.com/docs/pro/package/).\n\n\n## Conclusion\n\nUsing Pusher,  weve built out an application using the pub/sub pattern to recieve realtime updates. With the help of Chart.js, our data was well presented using charts. You can check out the repo containing the demo on [GitHub](https://github.com/HackAfro/ionic-graph-app).","metaTitle":"Build a live voting app with Ionic","metaDescription":"Use Ionic and Node to create a live voting app, similar to a Twitter poll. Users can vote for who they expect to win the World Cup."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6BwM9O7lhCQWKSg4eCw6ym","type":"Entry","createdAt":"2018-06-27T11:31:30.790Z","updatedAt":"2020-06-19T12:41:52.435Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a live polling web app with Python","url":"live-poll-python","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/python-realtime-poll-pusher","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5JtEWXIwaAaOM4KOWMO0qK","type":"Entry","createdAt":"2018-02-12T15:10:44.896Z","updatedAt":"2018-02-26T12:31:11.752Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Data Visualization","url":"data-visualization","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4KfWHAtURiWQQ6k2A6ueAY","type":"Entry","createdAt":"2018-02-12T14:45:59.264Z","updatedAt":"2018-02-12T14:45:59.264Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Flask","url":"flask","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1qG3Xd9hBSImsOO20kKoiA","type":"Entry","createdAt":"2018-02-05T11:18:51.856Z","updatedAt":"2018-02-12T14:41:11.935Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Python","url":"python","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6i1HPBqlwsE24YEKwqQyuq","type":"Entry","createdAt":"2018-02-12T15:09:31.050Z","updatedAt":"2018-02-26T12:31:11.548Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Graph","url":"realtime-graph","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4E7t0TtVFSAyeeA0ckeYe0","type":"Entry","createdAt":"2018-02-12T15:09:41.899Z","updatedAt":"2018-02-26T12:31:11.510Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Chart","url":"realtime-chart","type":"use case"}}],"reviewDate":"2018-06-26","description":"Build a live voting application using Flask. Users will be able to vote for their favorite programming language. Results will appear in an admin dashboard and be displayed on a live graph.","prerequisite":"You will need Python 3+ installed on your machine. A basic knowledge of Python and JavaScript will be helpful.","content":"In this article, we will build a voting poll application that keeps track of each vote in a poll and broadcasts recent updates to all subscribed clients. We will be broadcasting the updates in realtime using Pusher and this will ensure that every connected user knows when a new vote has been made. There will also be an admin section that displays a [chart](https://canvasjs.com/). This chart will update in realtime and show voting statistics.\n\nHere is what the final application will look like:\n\n![python-realtime-poll-demo](//images.ctfassets.net/1es3ne0caaid/1hgCPLcEbyUOUcuYOiI4Q8/745fb422b6dfa795a19a1158cf6c4162/python-realtime-poll-demo.gif)\n\nThe top-left screen shows a browser window that loads the voting application and sends realtime updates. To show that these updates are propagated across every other connected client, we can see that the bottom-left screen also updates as a vote is cast on the former screen. Finally, the admin screen by the right displays the chart and delivers realtime voting statistics. There is also a database that stores the status of votes of each poll member.\n\nWe will build the backend server for this application using a Python framework called [Flask.](http://flask.pocoo.org/) We will use this framework to develop a simple backend API that can respond to the requests we will be sending from our JavaScript frontend.\n\n## Prerequisites\n\nTo follow along with this tutorial, a basic knowledge of Python, Flask, and JavaScript (ES6 syntax) is required. You will also need the following installed:\n\n\n1. [Python (\u003e= v3.x)](https://www.python.org/)\n2. [virtualenv](https://virtualenv.pypa.io/en/stable/)\n3. [Flask](http://flask.pocoo.org/)\n\nVirtualenv is great for creating isolated Python environments, so we can install dependencies in an isolated environment, and not pollute our global packages directory.\n\nLets install `virtualenv` with this command:\n\n```\n    $ pip install virtualenv\n```\n\n## Setting up the app environment\n\nLets create our project folder, and activate a virtual environment in it. Run the commands below:\n\n```\n    $ mkdir python-poll-pusher\n    $ cd python-poll-pusher\n    $ virtualenv .venv\n    $ source .venv/bin/activate # Linux based systems\n    $ \\path\\to\\env\\Scripts\\activate # Windows users\n```\n\nNow that we have the virtual environment setup, we can install Flask within it with this command:\n\n```\n    $ pip install flask\n```\n\nLets install two more packages that will ensure that the application works correctly:\n\n```\n    $ pip install -U flask-cors\n    $ pip install simplejson\n```\n\nBefore we do anything else, we need to install the Pusher library as we will need that for realtime updates.\n\n\n## Setting up Pusher\n\nThe first step will be to get a Pusher Channels application. We will need the application credentials for our realtime features to work.\n\nGo to the [Pusher website](https://pusher.com) and create an account. After creating an account, you should create a new application. Follow the application creation wizard and then you should be given your application credentials, we will use this later in the article.\n\n![python-realtime-poll-keys](//images.ctfassets.net/1es3ne0caaid/752Tb8m156CeqWw2q6eKyS/2a61be62a4ff148b8fe9e7c60a94a5db/python-realtime-poll-keys.png)\n\nWe also need to install the Pusher Python Library to send events to Pusher. Install this using the command below:\n\n```\n    $ pip install pusher\n```\n\n## File and folder structure\n\nWe dont need to create so many files and folders for this application since its a simple one. Heres the file/folder structure:\n\n```\n     python-poll-pusher\n            app.py\n            dbsetup.py\n            static\n            templates\n```\n\nThe `static` folder will contain the static files to be used as is defined by [Flask standards](http://flask.pocoo.org/docs/0.12/tutorial/folders/). The `templates` folder will contain the HTML templates. In our application, `app.py` is the main entry point and will contain our server-side code. To keep things modular, we will write all the code that we need to interact with the database in `dbsetup.py`.\n\nCreate the `app.py` and `dbsetup.py` files, and then the `static` and `templates` folders.\n\n\n## Building the backend\n\nIn the `dbsetup.py` file, we will write all the code that is needed for creating a database and interacting with it. Open the `dbsetup.py` file and paste the following:\n\n``` python\n    import sqlite3, json\n    from sqlite3 import Error\n    \n    def create_connection(database):\n        try:\n            conn = sqlite3.connect(database, isolation_level=None, check_same_thread = False)\n            conn.row_factory = lambda c, r: dict(zip([col[0] for col in c.description], r))\n    \n            return conn\n        except Error as e:\n            print(e)\n    \n    def create_table(c):\n        sql = \"\"\" \n            CREATE TABLE IF NOT EXISTS items (\n                id integer PRIMARY KEY,\n                name varchar(225) NOT NULL,\n                votes integer NOT NULL Default 0\n            ); \n        \"\"\"\n        c.execute(sql)\n    \n    def create_item(c, item):\n        sql = ''' INSERT INTO items(name)\n                  VALUES (?) '''\n        c.execute(sql, item)\n    \n    def update_item(c, item):\n        sql = ''' UPDATE items\n                  SET votes = votes+1 \n                  WHERE name = ? '''\n        c.execute(sql, item)\n    \n    def select_all_items(c, name):\n        sql = ''' SELECT * FROM items '''\n        c.execute(sql)\n    \n        rows = c.fetchall()\n        rows.append({'name' : name})\n        return json.dumps(rows)\n    \n    def main():\n        database = \"./pythonsqlite.db\"\n        conn = create_connection(database)\n        create_table(conn)\n        create_item(conn, [\"Go\"])\n        create_item(conn, [\"Python\"])\n        create_item(conn, [\"PHP\"])\n        create_item(conn, [\"Ruby\"])\n        print(\"Connection established!\")\n    \n    if __name__ == '__main__':\n        main()\n```\n\nNext, run the `dbsetup.py` file so that it creates a new SQLite database for us. We can run it with this command:\n\n```\n    $ python dbsetup.py\n```\n\nWe should see this text logged to the terminal  Connection established!  and there should be a new file  `pythonsqlite.db`  added to the projects root directory.\n\nNext, lets open up the `app.py` file and start writing the backend code that will handle incoming requests. Here, we are going to register three routes: the first two will handle the `GET` requests that return the home and admin pages respectively. The last route will handle the `POST` requests that attempt to update the status of a particular vote member, both on the users page and on the admins page. \n\nIn this file, we will instantiate a fresh instance of Pusher and use it to broadcast data through a channel that we will shortly define within the application. We will also import some of the database handling methods we defined in `dbsetup.py` so that we can use them here.\n\nOpen the `app.py`  file and paste the following code:\n\n``` python\n    from flask import Flask, render_template, request, jsonify, make_response\n    from dbsetup import create_connection, select_all_items, update_item\n    from flask_cors import CORS, cross_origin\n    from pusher import Pusher\n    import simplejson\n    \n    app = Flask(__name__)\n    cors = CORS(app)\n    app.config['CORS_HEADERS'] = 'Content-Type'\n    \n    # configure pusher object\n    pusher = Pusher(\n    app_id='PUSHER_APP_ID',\n    key='PUSHER_APP_KEY',\n    secret='PUSHER_APP_SECRET',\n    cluster='PUSHER_APP_CLUSTER',\n    ssl=True)\n    \n    database = \"./pythonsqlite.db\"\n    conn = create_connection(database)\n    c = conn.cursor()\n    \n    def main():\n        global conn, c\n    \n    @app.route('/')\n    def index():\n        return render_template('index.html')\n    \n    @app.route('/admin')\n    def admin():\n        return render_template('admin.html')\n    \n    @app.route('/vote', methods=['POST'])\n    def vote():\n        data = simplejson.loads(request.data)\n        update_item(c, [data['member']])\n        output = select_all_items(c, [data['member']])\n        pusher.trigger(u'poll', u'vote', output)\n        return request.data\n    \n    if __name__ == '__main__':\n        main()\n        app.run(debug=True)\n```\n\nFirst, we imported the required modules and objects, then we initialized a Flask app. Next, we ensured that the backend server can receive requests from a client on another computer, then we initialized and configured Pusher. We also registered some routes and defined the functions that will handle them.\n\n\n\u003e Replace the `PUSHER_APP_*`  keys with the values on your Pusher dashboard.\n\nWith the `pusher` object instantiated, we can trigger events on whatever channels we define.\nIn the `/vote` route, we trigger a vote event on the poll channel. The trigger method has the following syntax:\n\n```\n    pusher.trigger(\"a_channel\", \"an_event\", {key: \"data to pass with event\"})\n```\n\nYou can find the docs for the Pusher Python library [here](https://github.com/pusher/pusher-http-python), to get more information on configuring and using Pusher in Python.\n\nThe first two routes we defined will return our applications view by rendering the `index.html` and `admin.html` templates. However, we are yet to create these files so there is nothing to render, lets create the app view in the next step and start using the frontend to communicate with our Python backend API.\n\n\n## Setting up the app view\n\nWe need to create two files in the `templates` directory. These files will be named `index.html` and `admin.html`, this is where the view for our code will live. The `index` page will render the view that displays the voting page for users to interact with while the `admin` page will display the chart that will update in realtime when a new vote is cast.\n\nIn the `./templates/index.html` file, you can paste this code:\n\n``` html\n    \u003c!DOCTYPE html\u003e\n    \u003chtml\u003e\n    \u003chead\u003e\n      \u003cmeta charset=\"utf-8\"\u003e\n      \u003ctitle\u003ePython Poll\u003c/title\u003e\n      \u003clink rel=\"stylesheet\" type=\"text/css\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css\"\u003e\n      \u003cstyle type=\"text/css\"\u003e\n        .poll-member h1 {\n          cursor: pointer\n        }\n        .percentageBarParent{\n          height: 22px;\n          width: 100%;\n          border: 1px solid black;\n        }\n        .percentageBar {\n          height: 20px;\n          width: 0%;\n        }\n      \u003c/style\u003e\n    \u003c/head\u003e\n    \u003cbody\u003e\n      \u003cdiv class=\"main\"\u003e\n        \u003cdiv class=\"container\"\u003e\n          \u003ch1\u003eWhat's your preferred language?\u003c/h1\u003e\n          \u003cdiv class=\"col-md-12\"\u003e\n            \u003cdiv class=\"row\"\u003e\n              \u003cdiv class=\"col-md-6\"\u003e\n                \u003cdiv class=\"poll-member Go\"\u003e\n                  \u003ch1\u003eGo\u0026nbsp;\u0026nbsp;\u003c/h1\u003e\n                  \u003cdiv class=\"percentageBarParent\"\u003e\n                  \u003cdiv class=\"percentageBar\" id=\"Go\"\u003e\u003c/div\u003e\n                \u003c/div\u003e\n                \u003c/div\u003e\n              \u003c/div\u003e\n              \u003cdiv class=\"col-md-6\"\u003e\n                \u003cdiv class=\"poll-member \"\u003e\n                  \u003ch1\u003ePython\u0026nbsp;\u0026nbsp;\u003c/h1\u003e \n                  \u003cdiv class=\"percentageBarParent\"\u003e\n                  \u003cdiv class=\"percentageBar\" id=\"Python\"\u003e\u003c/div\u003e\n                  \u003c/div\u003e\n                \u003c/div\u003e\n              \u003c/div\u003e\n            \u003c/div\u003e\n            \u003cdiv class=\"row\"\u003e\n              \u003cdiv class=\"col-md-6\"\u003e\n                \u003cdiv class=\"poll-member PHP\"\u003e\n                  \u003ch1\u003ePHP\u0026nbsp;\u0026nbsp;\u003c/h1\u003e \n                  \u003cdiv class=\"percentageBarParent\"\u003e\n                  \u003cdiv class=\"percentageBar\" id=\"PHP\"\u003e\u003c/div\u003e\n                  \u003c/div\u003e\n                \u003c/div\u003e\n              \u003c/div\u003e\n              \u003cdiv class=\"col-md-6\"\u003e\n                \u003cdiv class=\"poll-member Ruby\"\u003e\n                  \u003ch1\u003eRuby\u0026nbsp;\u0026nbsp;\u003c/h1\u003e \n                  \u003cdiv class=\"percentageBarParent\"\u003e\n                  \u003cdiv class=\"percentageBar\" id=\"Ruby\"\u003e\u003c/div\u003e\n                  \u003c/div\u003e\n                \u003c/div\u003e\n              \u003c/div\u003e\n            \u003c/div\u003e\n          \u003c/div\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n    \n      \u003cscript src=\"https://cdnjs.cloudflare.com/ajax/libs/axios/0.16.2/axios.js\"\u003e\u003c/script\u003e\n      \u003cscript type=\"text/javascript\" src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js\"\u003e\u003c/script\u003e\n        \u003cscript type=\"text/javascript\" src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/js/bootstrap.min.js\"\u003e\u003c/script\u003e\n        \u003cscript src=\"https://js.pusher.com/4.0/pusher.min.js\"\u003e\u003c/script\u003e\n      \u003cscript type=\"text/javascript\" src=\"{{ url_for('static', filename='app.js') }}\" defer\u003e\u003c/script\u003e\n    \u003c/body\u003e\n    \u003c/html\u003e\n```\n\nNext, lets copy and paste in this code into the `./templates/admin.html` file:\n\n``` html\n    \u003c!DOCTYPE html\u003e\n    \u003chtml\u003e\n    \u003chead\u003e\n      \u003cmeta charset=\"utf-8\"\u003e\n      \u003ctitle\u003ePython Poll Admin\u003c/title\u003e\n      \u003clink rel=\"stylesheet\" type=\"text/css\" href=\"https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css\"\u003e\n    \u003c/head\u003e\n    \u003cbody\u003e\n    \n      \u003cdiv class=\"main\"\u003e\n        \u003cdiv class=\"container\"\u003e\n          \u003ch1\u003eChart\u003c/h1\u003e\n          \u003cdiv id=\"chartContainer\" style=\"height: 300px; width: 100%;\"\u003e\u003c/div\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n    \n      \u003cscript src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/3.3.1/jquery.min.js\"\u003e\u003c/script\u003e\n      \u003cscript src=\"https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/js/bootstrap.min.js\"\u003e\u003c/script\u003e\n      \u003cscript src=\"https://js.pusher.com/4.0/pusher.min.js\"\u003e\u003c/script\u003e\n      \u003cscript src=\"https://cdnjs.cloudflare.com/ajax/libs/canvasjs/1.7.0/canvasjs.js\"\u003e\u003c/script\u003e\n      \u003cscript src=\"{{ url_for('static', filename='admin.js') }}\" defer\u003e\u003c/script\u003e\n    \u003c/body\u003e\n    \u003c/html\u003e\n```\n\nThats all for the `index.html` and `admin.html` files, we have described how they should be rendered on the DOM but our application lacks all forms of interactivity. \n\nIn the next section, we will write the scripts that will send the `POST` requests to our Python backend server.\n\n## Communicating with the backend\n\nCreate two more files in the `static` folder, one for the `index` page and the other for the `admin` page. These files are the scripts that will define how our application interacts with click events, communicates with the backend server for realtime updates and display a progress bar.\n\nLets create the following files in the `static` folder:\n\n\n- app.js\n- admin.js\n\nIn the `./static/app.js` file, we can paste the following:\n\n``` javascript\n    var pollMembers = document.querySelectorAll('.poll-member')\n    \n    var members = ['Go', 'Python', 'PHP', 'Ruby']\n    \n    // Sets up click events for all the cards on the DOM\n    pollMembers.forEach((pollMember, index) =\u003e {\n      pollMember.addEventListener('click', (event) =\u003e {\n        handlePoll(members[index])\n      }, true)\n    })\n    \n    // Sends a POST request to the server using axios\n    var handlePoll = function(member) {\n      axios.post('http://localhost:5000/vote', {member}).then((r) =\u003e console.log(r))\n    }\n    \n    // Configure Pusher instance\n    const pusher = new Pusher('PUSHER_APP_KEY', {\n      cluster: 'PUSHER_APP_CLUSTER',\n      encrypted: true\n    });\n    \n    // Subscribe to poll trigger\n    var channel = pusher.subscribe('poll');\n    \n    // Listen to vote event\n    channel.bind('vote', function(data) {\n      for (i = 0; i \u003c (data.length - 1); i++) { \n        var total = data[0].votes + data[1].votes + data[2].votes + data[3].votes\n        document.getElementById(data[i].name).style.width = calculatePercentage(total, data[i].votes)\n        document.getElementById(data[i].name).style.background = \"#388e3c\" \n      }\n    });\n    \n    let calculatePercentage = function(total, amount) {\n      return (amount / total) * 100 + \"%\"\n    }\n```\n\n\u003e Replace the `PUSHER_APP_*`  keys with the keys on your Pusher dashboard.\n\nFirst, we registered click events on all the members of the poll, then we configure [Axios](https://www.npmjs.com/package/axios) to send a POST request whenever a user votes for a member of the poll. Next, we configured a Pusher instance to communicate with the Pusher service. Next, we register a listener for the events Pusher sends. Finally, we bind the events we are listening to on the channel we created. \n\nNext, open the `admin.js` file and paste in this code:\n\n``` javascript\n    var dataPoints = [\n      { label: \"Go\", y: 0 },\n      { label: \"Python\", y: 0 },\n      { label: \"PHP\", y: 0 },\n      { label: \"Ruby\", y: 0 },\n    ]\n    \n    var chartContainer = document.querySelector('#chartContainer');\n    \n    if (chartContainer) {\n      var chart = new CanvasJS.Chart(\"chartContainer\", {\n        animationEnabled: true,\n        theme: \"theme2\",\n        data: [\n          {\n            type: \"column\",\n            dataPoints: dataPoints\n          }\n        ]\n      });\n      \n      chart.render();\n    }\n    \n    Pusher.logToConsole = true;\n    \n    // Configure Pusher instance\n    const pusher = new Pusher('PUSHER_APP_KEY', {\n      cluster: 'PUSHER_APP_CLUSTER',\n      encrypted: true\n    });\n    \n    // Subscribe to poll trigger\n    var channel = pusher.subscribe('poll');\n    \n    // Listen to vote event\n    channel.bind('vote', function(data) {\n      dataPoints = dataPoints.map(dataPoint =\u003e {\n        if(dataPoint.label == data[4].name[0]) {\n          dataPoint.y += 10;\n        }\n    \n        return dataPoint\n      });\n    \n      // Re-render chart\n      chart.render()\n    });\n```\n\n\u003e Replace the `PUSHER_APP_*`  keys with the keys on your Pusher dashboard.\n\nJust as we did in the previous code, here we also receive Pusher events and use the received data to update the [chart](https://canvasjs.com/) on the admins page.\n\nOur application is good to go! Now we can run the app using this command:\n\n```\n    $ flask run\n```\n\nNow if we visit [127.0.0.1:5000](http://127.0.0.1:5000) and [127.0.0.1:5000/admin](http://127.0.0.1:5000/admin) we should see our app:\n\n![python-realtime-poll-demo](//images.ctfassets.net/1es3ne0caaid/1hgCPLcEbyUOUcuYOiI4Q8/745fb422b6dfa795a19a1158cf6c4162/python-realtime-poll-demo.gif)\n\n## Conclusion\n\nIn this tutorial, we have learned how to build a Python Flask project from scratch and add realtime functionality to it using Pusher and vanilla JavaScript. The entire code for this tutorial is [available on GitHub](https://github.com/neoighodaro/python-realtime-poll-pusher).","metaTitle":"Build a live polling web app with Python","metaDescription":"Use Flask and JavaScript to create a live voting application. Users will vote for their favorite programming language, and admins can view a results graph that automatically updates as results come in."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6uu3Ys68yQ6sIMwae8OAQU","type":"Entry","createdAt":"2018-06-28T09:38:54.858Z","updatedAt":"2018-06-28T09:38:54.858Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a live poll app with Ionic","url":"live-poll-ionic","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5Q1I2HN8pqGKG2wYcCeaO2","type":"Entry","createdAt":"2018-02-12T17:24:27.398Z","updatedAt":"2018-02-14T10:09:48.043Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christian Nwamba","url":"christian-nwamba","email":"nwambachristian@gmail.com","twitter":"https://twitter.com/codebeast","github":"https://github.com/christiannwamba","guestWriter":true}}],"projectRepo":"https://github.com/FeezyHendrix/ionic-realtime-pollapp","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1SM1spRqTeua46Ew0W6UwC","type":"Entry","createdAt":"2018-02-12T14:40:52.995Z","updatedAt":"2018-02-12T14:40:52.995Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Angular","url":"angular","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1gcZ0mVsoeagKg0G0MOEgy","type":"Entry","createdAt":"2018-02-12T14:51:28.935Z","updatedAt":"2018-02-26T12:31:14.813Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"TypeScript","url":"typescript","type":"language"}}],"reviewDate":"2018-06-26","description":"Use Ionic to create a live poll application for mobile. The app features a Node.js backend. It allows users to vote on their favorite premier league player, and see other users' votes displayed in realtime.","prerequisite":"You will need Node and npm installed on your machine. A basic knowledge of Angular, Ionic and Node will be helpful.","content":"## Introduction\n\nAn electronic polling system allows users cast their votes with ease without the hassle and stress of visiting a polling booth. This makes it easily accessible as it can be used by users anywhere in the world. Adding realtime functionality to the application improves the user experience as votes are seen in realtime.\n\nWell build a realtime polling application using Pusher, Node, and Ionic.\nUsing our application, users will get to vote for their favorite soccer player in the English Premier League.\nHeres a demo of the final product:\n\n![ionic-poll-demo](//images.ctfassets.net/1es3ne0caaid/2tcCbmsgRyI2YYqEcuOEO2/d09a686ee51a5db0fc1706672a373556/ionic-poll-demo.gif)\n\nWell send our votes to the server and with the help of Pusher, update our polls in realtime. To make use of Pusher, youll have to create an account [here.](https://pusher.com/)\n\nLets build!\n\n## Prerequisites\n\nTo follow this tutorial a basic understanding of [Angular](https://angular.io), [Ionic](https://ionicframework.com) and [Node.js](https://nodejs.org/en/) is required. Please ensure that you have Node and npm installed before you begin.\nIf you have no prior knowledge of Ionic, kindly follow the [tutorial here.](https://ionicframework.com/docs/intro/tutorial/) Come back and finish the tutorial when youre done.\n\nWell be using these tools to build out our application:\n\n1. [Express](https://expressjs.com/)\n2. [Node](https://nodejs.org/en/)\n3. [Pusher](https://pusher.com/)\n4. [Ionic](https://ionicframework.com)\n\n\n## Setup and folder structure\n\nTo get started, we will use the CLI (command line interface) provided by the Ionic team to initialize our project.\n\nFirst, install the CLI by running `npm install -g ionic cordova`. [NPM](https://www.npmjs.com/) is a package manager used for installing packages. It will be available on your PC if you have [Node](https://nodejs.org/en/) installed.\n\nTo create a new Ionic App using the CLI, open a terminal and run:\n\n```\n    ionic start pollApp blank\n```\n\nThe command is merely telling the CLI to create a new app called `pollApp` and it should make use of the blank starter template.  While the setup is running you might get a prompt `\"Would you like to integrate your new app with Cordova to target native iOS and Android?\"`. If you want to start running or testing the application on a mobile device or emulator as you build, then choose yes by typing `Y` and pressing Enter on your keyboard, else type `N` and continue. You might get another prompt   `\"If you would like to integrate ionic pro ?\"` ,  we wouldnt be needing that in this tutorial so just type `N` and if you would like extend the project into production with your team choose `Y`.\n\nOpen the newly created `pollApp`. Your folder structure should look something like this:\n\n```\n    pollApp /\n      node_modules /\n      src / \n        app /\n          assets /\n          pages /\n            home /\n              home.html\n              home.ts\n              home.scss\n```\n\nOpen a terminal inside the app folder and start the application by running: \n\n```\n    ionic serve \n```\n\nAutomatically your default browser should open, and you should see the screenshot below if everything went well.\n\n![ionic-chat-sentiment-ionic-serve](//images.ctfassets.net/1es3ne0caaid/cZJy8Cc9WgkoiOEKI44uA/8a6010cf3b3515127f486c76c18f33de/ionic-chat-sentiment-ionic-serve.png)\n\n## Building our server\n\nNow that we have our Ionic application running lets build our server.\nTo do this, well need to install Express. Express is a fast, unopinionated, minimalist web framework for Node.js. Well use this to receive requests from our Ionic application.\n\nRun `npm install express` on a terminal inside the root folder of your project to install Express.\nCreate a file called `server.js` in the root of the project and update it with the code snippet below:\n\n``` javascript\n    //pollApp/server.js\n    \n    require('dotenv').config();\n    const express = require('express');\n    const bodyParser = require('body-parser');\n    const Pusher = require('pusher');\n    \n    const app = express();\n    const port = process.env.PORT || 4000;\n    const pusher = new Pusher({\n      appId: process.env.PUSHER_APP_ID,\n      key: process.env.PUSHER_KEY,\n      secret: process.env.PUSHER_SECRET,\n      cluster: process.env.CLUSTER,\n      encrypted: true,\n    });\n    \n    app.use(bodyParser.json());\n    app.use(bodyParser.urlencoded({ extended: false }));\n    app.use((req, res, next) =\u003e {\n      res.header('Access-Control-Allow-Origin', '*');\n      res.header(\n        'Access-Control-Allow-Headers',\n        'Origin, X-Requested-With, Content-Type, Accept'\n      );\n      next();\n    });\n    \n    app.listen(port, () =\u003e {\n      console.log(`Server started on port ${port}`);\n    });\n```\n\nWe referenced three packages that we are yet to install, `body-parser`, `pusher` and `dotenv`. Install these packages by running this code in your terminal:\n\n```\n    npm i body-parser pusher dotenv\n```\n\nThe `body-parser` package is used to parse incoming request bodies in a middleware before your handlers, available under the `req.body` property.\n\nThe calls to our endpoint will be coming in from a different origin. Therefore we need to make sure we include the CORS headers (Access-Control-Allow-Origin). If you are unfamiliar with the concept of CORS headers, you can find more information [here](https://developer.mozilla.org/en-US/docs/Web/HTTP/Access_control_CORS).\n\nThis is a standard Node application configuration, nothing specific to our app.\nAlso, youll notice that we installed Pusher library as a dependency. [Create a Pusher account](https://pusher.com/) and a new Pusher Channels app and get your `appId`, `key` and `secret`.\n\nThe last package, `dotenv` is a zero-dependency module that loads environment variables from a `.env` file into `process.env`.\n\nWe use this package to make sure we dont add sensitive information like our `appId` and `secret` directly into our code. To get these values loaded into our environment, we'll create a `.env` file in the root of our project.\n\nYour `.env file` should look something like the snippet below. Well add our Pusher `appId`, `key` and `secret` provided here.\n\n```\n    // pollApp/.env\n    PUSHER_APP_ID=\u003cAPP_ID\u003e\n    PUSHER_KEY=\u003cPUSHER_KEY\u003e\n    PUSHER_SECRET=\u003cPUSHER_SECRET\u003e\n    CLUSTER=\u003cCLUSTER\u003e\n```\n\nIf you noticed, we added the `dotenv` package at the start of our file. This is done because we need to make the variables available throughout the file.\nPlease ensure you replace the following placeholder values above with your unique Pusher `appId`, `key` and `secret` from your Pusher dashboard.\n\n## Send votes\n\nTo enable users send requests to the server, well create a route to handle incoming requests. Update your `server.js` file with the code below:\n\n``` javascript\n    // pollApp/server.js\n    require('dotenv').config();\n    ...\n    app.post('/vote', (req, res) =\u003e {\n      const { body } = req;\n      const { player } = body;\n      pusher.trigger('vote-channel', 'vote', {\n        player,\n      });\n      res.json({ player });\n    });\n    \n     ...\n```\n\nWe created a POST `/vote` route which, when hit, triggers a Pusher event.\nWe used object destructuring to get the body of the request and also the player info sent by the user.\n\nThe trigger is achieved using the `trigger``()` method which takes the trigger identifier (vote-channel), an event name (vote), and a payload.\nThe payload can be any value, but in this case, we have a JS object. This object contains the name of the player being voted for.\n\nWe still go ahead to respond with an object containing the voted player string so we can update the frontend with the data.\n\n## Building the app UI\n\nOpen `home.html` and replace it with the content below:\n \n``` html\n    //../src/pages/home/home.html\n    \n    \u003cion-header\u003e\n      \u003cion-navbar\u003e\n        \u003cion-title\u003e\n        Poll APP  \n        \u003c/ion-title\u003e\n      \u003c/ion-navbar\u003e\n    \u003c/ion-header\u003e\n    \u003cion-content padding\u003e\n      \u003cion-list no-lines\u003e\n        \u003cion-item *ngFor=\"let player of playerData\"\u003e\n          \u003cion-row\u003e\n            \u003cion-col col-6\u003e\n                \u003cion-avatar\u003e\n                    \u003cimg [src]=\"player.image\" [alt]=\"player.name\" [ngClass]=\"getVoteClasses(player.shortName)\"\u003e\n                    \u003ch4\u003e{{player.name}}\u003c/h4\u003e\n                    \u003cp\u003e{{player.goals}} goals\u003c/p\u003e\n                    \u003cp\u003e{{player.assists}} assists\u003c/p\u003e\n                  \u003c/ion-avatar\u003e\n            \u003c/ion-col\u003e\n            \u003cion-col col-6\u003e\n                  \u003ch1\u003eVoteCount\u003c/h1\u003e\n                  \u003ch1 text-center\u003e{{voteCount[player.shortName]}}\u003c/h1\u003e\n            \u003c/ion-col\u003e\n          \u003c/ion-row\u003e \n          \u003cbutton ion-button full round (click)=\"sendVotes(player)\"\u003eVote\u003c/button\u003e\n        \u003c/ion-item\u003e\n      \u003c/ion-list\u003e\n    \u003c/ion-content\u003e\n```    \n\nIn the code snippet above, we looped through `playerData``[]` to create a view based on the players information.\nThere are some undefined variables in the code snippet above, dont panic yet, well define them in our `home.ts` file below.\n\n## Home.ts\n\nIn the HTML snippet, we referred to some variables that werent yet defined. Well create the variables here with the logic behind our application, and import the HttpClient service.\n\n\n\n``` typescript    \n    // ../src/pages/home/home.ts\n    import { HttpClient } from '@angular/common/http';\n    import { Component } from '@angular/core';\n    import { NavController } from 'ionic-angular';\n    \n    @Component({\n      selector: 'page-home',\n      templateUrl: 'home.html'\n    })\n    export class HomePage {\n      constructor(public navCtrl: NavController, public http : HttpClient) {\n      }\n      event: string = 'vote';\n      vote: string = '';\n      ifVoted : boolean = false;\n      voteCount = {\n        salah: 0,\n        kane: 0,\n        eriksen: 0,\n        kevin: 0,\n      };\n      playerData = [\n        {\n          name: 'Mo. Salah',\n          goals: 30,\n          assists: 12,\n          shortName: 'salah',\n          image: 'https://platform-static-files.s3.amazonaws.com/premierleague/photos/players/250x250/p118748.png'\n        },\n        {\n          name: 'Christian Eriksen',\n          goals: 8,\n          assists: 13,\n          shortName: 'eriksen',\n          image: 'https://platform-static-files.s3.amazonaws.com/premierleague/photos/players/250x250/p80607.png',\n        },\n        {\n          name: 'Harry Kane',\n          goals: 26,\n          assists: 5,\n          shortName: 'kane',\n          image:\n            'https://platform-static-files.s3.amazonaws.com/premierleague/photos/players/40x40/p78830.png',\n        },\n        {\n          name: \"Kevin De'bruyne\",\n          goals: 10,\n          assists: 17,\n          shortName: 'kevin',\n          image: 'https://platform-static-files.s3.amazonaws.com/premierleague/photos/players/40x40/p61366.png',\n        },\n    ];\n      sendVotes(player){\n        this.http.post('http://localhost:4000/vote', { player }).subscribe((res : any) =\u003e {\n          this.vote  = res.player;\n          this.ifVoted = true;\n        })\n      }\n      getVoteClasses(player){\n        return {\n          elect : this.ifVoted \u0026\u0026 this.vote === player,\n          lost : this.ifVoted \u0026\u0026 this.vote !== player\n        }\n      }\n      ionViewDidLoad(){}\n    }\n```\n\n`send``Votes``()`: this method makes use of the native HttpClient service to make requests to our server. It sends the name of the player being voted for in a POST request to the server. When a response is returned, it sets the voted property to true signifying that the user has placed a vote. Also, it sets the vote property to the name of the player being voted.\n\n`getVoteClasses``()`: this method sets classNames on each player element based on if a player was voted for or not.\n\nTo make use of the HttpClient service, well need to import the HttpClientModule into the `app.module.ts` file. Update your app module file as follows:\n\n``` typescript\n    // src/app/app.module.ts\n    \n    import { BrowserModule } from '@angular/platform-browser';\n    import { ErrorHandler, NgModule } from '@angular/core';\n    import { IonicApp, IonicErrorHandler, IonicModule } from 'ionic-angular';\n    import { SplashScreen } from '@ionic-native/splash-screen';\n    import { StatusBar } from '@ionic-native/status-bar';\n    import { HttpClientModule } from '@angular/common/http';\n    import { MyApp } from './app.component';\n    import { HomePage } from '../pages/home/home';\n    @NgModule({\n    declarations: [\n    MyApp,\n    HomePage\n    ],\n    imports: [\n    BrowserModule,\n    IonicModule.forRoot(MyApp),\n    HttpClientModule\n    ],\n    bootstrap: [IonicApp],\n    ....\n```\n\nBy now our application should look like this:\n\n![ionic-poll-vote-page-1](//images.ctfassets.net/1es3ne0caaid/5KahXsHznU6giAomgwaaA4/3f95e4a7ead72f9b9a6f2f4d1196ca62/ionic-poll-vote-page-1.png)\n\n## Introducing Pusher\n\nSo far we have an application that enables users to cast votes, but we have no way of keeping track of how others voted in realtime. We also have no way of visualizing the polling data. To solve both of these problems, well include the Pusher library.\n\nOpen `index.html` file in the src folder of the project and paste the cdn into it:\n\n``` html\n    ...\n    \n      \u003cscript src=\"https://js.pusher.com/4.1/pusher.min.js\"\u003e\u003c/script\u003e\n      \u003c!-- add to homescreen for ios --\u003e \n    ...\n```\n\nNow that Pusher has been made available in our project, well create a Pusher Provider to be used in our application. The Ionic CLI can aid in the provider creation. Open a terminal in your projects root folder and run the following command:\n\n```\n    ionic generate provider pusher-service\n```\n\nThis command simply tells the CLI to generate a provider named pusher. Now open the `pusher-service.ts` file and update it with the code below:\n\n``` typescript\n    // ../src/providers/pusher-service/pusher-service.ts\n    \n    import { HttpClient } from '@angular/common/http';\n    import { Injectable } from '@angular/core';\n    declare const Pusher: any;\n    @Injectable()\n    export class PusherServiceProvider {\n    channel;\n    constructor(public http: HttpClient) {\n    var pusher = new Pusher('\u003cPUSHER_KEY\u003e', {\n    cluster: 'eu',\n    encrypted: true,\n    });\n    this.channel = pusher.subscribe('vote-channel');\n    }\n    public init(){\n      return this.channel;\n      }\n    }\n```\n\nFirst, we initialize Pusher in the constructor.\nThe `init()` method returns the Pusher property we created.\nEnsure you replace the `PUSHER_KEY` string with your actual Pusher key.\n\nTo make the provider available in the application, import it into the `app.``module.ts` file.\n\n``` typescript\n    // ../src/app/app.module.ts\n    \n    import { PusherServiceProvider } from '../providers/pusher-service/pusher-service';\n    ....\n    providers: [\n    StatusBar,\n    SplashScreen,\n    {provide: ErrorHandler, useClass: IonicErrorHandler},\n    PusherServiceProvider\n    ]\n    ....\n```\n\nWell make use of this provider in our home page, by binding to the vote event and increment the votes of the voted player returned in the event. This will be done in the `ionViewDidLoad` lifecycle.\n\n``` typescript\n    // ../src/pages/home/home.ts\n    \n    import { PusherServiceProvider } from '../../providers/pusher-service/pusher-service';\n    \n    constructor(public navCtrl: NavController, public http : HttpClient, private pusher : PusherServiceProvider) {}\n    \n    ...\n    \n    ionViewDidLoad(){\n      const channel = this.pusher.init();\n      channel.bind(this.event, ({ player }) =\u003e {\n        this.voteCount[player.shortName] += 1\n      });\n    }\n```\n\nAt this point, your application should have realtime updates when votes are placed. Ensure that the server is running alongside the Ionic development server. If not, run `node server` and `ionic serve` in two separate terminals. Both terminals should be opened in the root folder of your project. \n\n\n## Testing on Mobile Devices\n\nTo test the application on your mobile device, download the [IonicDevApp](https://ionicframework.com/docs/pro/devapp/) on your mobile device. Make sure your computer and your mobile device are connected to the same network. When you open the IonicDevApp, you should see Ionic apps running on your network listed. \n\n\n\u003e **Note:** Both the server(`node server`), ngrok for proxying our server and the Ionic dev server(`ionic serve`) must be running to get the application working. Run the commands in separate terminal sessions if you havent done so already.\n\n![ionic-chat-sentiment-in-app](//images.ctfassets.net/1es3ne0caaid/2OsgMWFKTC8ksw2wMCqEkI/983d5ae2843c21149a10fb468393ba2b/ionic-chat-sentiment-in-app.png)\n\nTo view the application, click on it and you should see a similar view to what was in the browser. Sending messages to the server might have worked in the browser but localhost doesnt exist on your phone, so well need to create a proxy to be able to send messages from mobile. \n\nYou can also run the following code in your terminal if the above method doesnt work for you.\n\n```\n    ionic cordova run \u003ctarget_platform\u003e\n```\n\n- `target_platform` : either `ios` or `android`\n\n## Using Ngrok as a proxy\n\nTo create a proxy for our server, well download Ngrok. Visit the [download](https://ngrok.com/download) page on the Ngrok website. Download the client for your OS. Unzip it and run the following command in the folder where Ngrok can be found:\n\n```\n    ./ngrok http 4000\n```\n\n![ionic-chat-sentiment-ngrok](//images.ctfassets.net/1es3ne0caaid/5pf60lqM9i8YmkC4eG8iY8/d31c9d7421f2bef8b8595d98e98e4082/ionic-chat-sentiment-ngrok.png)\n\nCopy the forwarding url with `https` and place it in the `home.ts` file that previously had `http://localhost:4000/``vote`. Please do not copy mine from the screenshot above. \n\n``` typescript\n    // src/pages/home/home.ts\n    ...\n    @Component({\n      selector: 'page-home',\n      templateUrl: 'home.html'\n    })\n    export class HomePage {\n      ...\n       sendVotes(player){\n          this.http.post('\u003cNGROK_URL\u003e/vote', { player }).subscribe((res : any) =\u003e {\n          this.vote  = res.player;\n          this.ifVoted = true;\n        });\n      }\n      ...\n    }\n    ...\n```\n\n\u003e Ensure to include the forwarding URL you copied where the placeholder string is\n\nNow you should be seeing realtime update of the votes, both on the phone and the browser.\n\n\n![ionic-poll-demo](//images.ctfassets.net/1es3ne0caaid/2tcCbmsgRyI2YYqEcuOEO2/d09a686ee51a5db0fc1706672a373556/ionic-poll-demo.gif)\n\n\n\u003e **Note:** Both the server(`node server`), ngrok for proxying our server and the Ionic dev server(`ionic serve`) must be running to get the application working. Run the commands in separate terminal sessions if you havent done so already.\n\nTo build your application to deploy on either the AppStore or PlayStore, follow the instructions found [here](https://ionicframework.com/docs/pro/package/).\n\n\n## Conclusion\n\nUsing Pusher, weve built out an application using the pub/sub pattern without having to set up a WebSocket server. This shows how powerful Pusher is and how easy it is to set up. You can find the demo for this article on [GitHub.](https://github.com/FeezyHendrix/ionic-realtime-pollapp)","metaTitle":"Build a live poll app with Ionic","metaDescription":"Build a voting app with Ionic, using Node for the backend. Users can vote on their favorite premier league player, and see other users' votes displayed in realtime."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6MHAvSM6MokiagYk8YQueO","type":"Entry","createdAt":"2018-06-22T15:29:42.470Z","updatedAt":"2018-06-22T15:29:42.470Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a voting system with Ember.js","url":"voting-system-emberjs","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5Q1I2HN8pqGKG2wYcCeaO2","type":"Entry","createdAt":"2018-02-12T17:24:27.398Z","updatedAt":"2018-02-14T10:09:48.043Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christian Nwamba","url":"christian-nwamba","email":"nwambachristian@gmail.com","twitter":"https://twitter.com/codebeast","github":"https://github.com/christiannwamba","guestWriter":true}}],"projectRepo":"https://github.com/yemiwebby/realtime-voting-ember","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"oh5mPeR5cGqMImEyKoWwQ","type":"Entry","createdAt":"2018-02-12T14:46:47.672Z","updatedAt":"2018-02-12T14:46:47.672Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Ember.js","url":"ember-js","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3WH6Mz2j7iyIaqCeOKIIkQ","type":"Entry","createdAt":"2018-02-12T14:44:51.150Z","updatedAt":"2018-02-12T14:44:51.150Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Node.js","url":"node-js","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4E7t0TtVFSAyeeA0ckeYe0","type":"Entry","createdAt":"2018-02-12T15:09:41.899Z","updatedAt":"2018-02-26T12:31:11.510Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Chart","url":"realtime-chart","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6i1HPBqlwsE24YEKwqQyuq","type":"Entry","createdAt":"2018-02-12T15:09:31.050Z","updatedAt":"2018-02-26T12:31:11.548Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Graph","url":"realtime-graph","type":"use case"}}],"reviewDate":"2018-06-20","description":"Build a polling app using Ember.js and Node.js. Users will be able to vote for which football team has the best supporters, and see results displayed on a chart in realtime.","prerequisite":"You will need Node and npm installed on your machine.","content":"## Introduction\n\nIn this tutorial, we are going to build a basic voting app with Ember.js that displays the live results of votes on a doughnut chart as they come in. Pusher will be used here to respond to votes and dynamically update the chart in all browsers in realtime. The results will be instant as depicted in the image below:\n\n![emberjs-voting-demo](//images.ctfassets.net/1es3ne0caaid/319dqAqENGE0WYm6sGcwiU/cffaa09b1f739350e687f055291cb777/emberjs-voting-demo.gif)\n\nA simple realtime voting application like this can improve the conventional way of casting and collecting votes, and also facilitate the live update of results for everyone involved to see. This improves transparency and makes the experience more interactive.\n\nIn this realtime demo, users will be able to vote for the fans that they consider the best by clicking on the image. This will immediately increase the points of such country as it will be displayed on the chart.\n\n## Prerequisites\n\nIt is important that you have [Node.js](https://nodejs.org/en/) and [npm](https://www.npmjs.com/) installed on your machine, [learn how to install Node.js and npm here](https://docs.npmjs.com/getting-started/installing-node). A quick run-down of the core technologies we will be using include:\n\n\n- [Ember.js](https://www.emberjs.com/): a productive frontend framework built for ambitious web developers.\n- [Pusher](https://pusher.com/): a [Node.js](http://nodejs.org/) client to interact with the Pusher REST API\n- [Express](https://expressjs.com/): Node utility for handling HTTP requests via routes\n- [Axios](https://www.npmjs.com/package/axios): a promise-based HTTP client that works both in the browser and in a Node.js environment.\n- [Body Parser](https://github.com/expressjs/body-parser): attaches the request payload on Expresss `req`, hence `req.body` stores this payload for each request.\n- [CanvasJS](https://canvasjs.com/): a responsive HTML5 Charting library for data visualization.\n\n\n## Getting started\n\nThe most convenient and recommended way of getting an Ember app up and running with a single command is by using the Ember CLI tool. You can install it with npm, open your terminal and type this command:\n\n```\n     npm install -g ember-cli\n```\n\nThe [Ember CLI](https://github.com/ember-cli/ember-cli) will give us access to the `ember` command that will be used to set up our project easily.\n\n\n## Creating the voting application\n\nWe can now proceed to create our application by using the `ember new` command. Open up the terminal on your machine and run a command that will create an application named `realtime-voting-ember` in your project directory:\n\n```\n     ember new realtime-voting-ember\n```\n\nNext, change directory into the newly created project and start the development server:\n\n```\n     // change directory\n     cd realtime-voting-ember\n     \n     // start the development server\n     ember serve\n```\n\nThis will start the development server on http://localhost:4200. Open your favorite browser and navigate to that URL, you should see a default welcome page by Ember.js as shown below:\n\n![ember-start-page](//images.ctfassets.net/1es3ne0caaid/2Z3q7KbdrOEWGs8McO8gyC/86f812aacd929bbcd81e6a45873f91b6/ember-start-page.png)\n\n\n## Installing server dependencies\n\nRun the following command to install the dependencies required for this project using:\n\n```\n    npm install --save axios pusher pusher-js\n    npm install --save body-parser cors dotenv express \n    npm install --save canvasjs babelify babel-preset-es2015 babel-preset-stage-1\n    ember install ember-browserify\n```\n\n## Pusher account setup\n\nHead over to Pusher and [sign up for a free account](https://dashboard.pusher.com/accounts/sign_up). \n\n![ember-pusher-signup](//images.ctfassets.net/1es3ne0caaid/2NeDEhMbuwYsECuqGyWCUE/646d9e28c3c1ecc1517361035836a8ec/ember-pusher-signup.png)\n\nCreate a new app by selecting **Channels apps** on the sidebar and clicking **Create Channels app** button on the bottom of the sidebar:\n\n![ember-pusher-create-app](//images.ctfassets.net/1es3ne0caaid/5YHrOKL5AIQk6E2OiCIeMW/473f43255c5a820f1523387487884757/ember-pusher-create-app.png)\n\nConfigure an app by providing basic information requested in the form presented. You can also choose the environment you intend to integrate Pusher with to be provided with some boilerplate code:\n\n![ember-pusher-config-app](//images.ctfassets.net/1es3ne0caaid/38gUNRKzT2UuEcQIq2GgS6/d7ef41d5c3f3afb724373fd976caef4c/ember-pusher-config-app.png)\n\nYou can retrieve your keys from the **App Keys** tab:\n\n![ember-pusher-app-keys](//images.ctfassets.net/1es3ne0caaid/1QiXDu6gpy0CKQCosqgcgE/1ffc23a7859704465b1a9357c07151b5/ember-pusher-app-keys.png)\n\n## Setting the environment variables\n\nCreate a `.env` file in the root directory of your application and add your application credentials as obtained from your Pusher dashboard as follows:\n\n```\n      PUSHER_APP_ID=YOUR_APP_ID\n      PUSHER_APP_KEY=YOUR_APP_KEY\n      PUSHER_APP_SECRET=YOUR_APP_SECRET_KEY\n      PUSHER_APP_CLUSTER=CLUSTER\n```\n\nWe will make use of the variables specified in the above snippet later in our project. And do ensure that you replace `YOUR_APP_ID`, `YOUR_APP_KEY`, `YOUR_APP_SECRET_KEY` and `CLUSTER` placeholders with the appropriate credentials.\n\n\n## Setting up the server\n\nAs stated earlier, a user will only be able to vote by clicking on an image of the chosen option from the list of fans displayed. \n\nThis click event will result in a process that needs to be managed by a server. So well use Express and Node to set up a simple server that will receive the `id` of the country selected and publish it to a designated channel alongside the points required to update the chart.\n\nSince the Ember application that we configured earlier is running on a different domain, well enable CORS in order to ensure communication between the Express server and Ember.\n\nCreate an `app.js`  file in the root directory of your application and add the following code snippet to it to set up the server:\n\n\n``` javascript\n    // app.js\n    \n      const express = require('express');\n      const bodyParser = require('body-parser');\n      const cors = require('cors');\n      const Pusher = require('pusher');\n      require('dotenv').config();\n      \n      const app = express();\n      \n      app.use(cors());\n      app.use(bodyParser.urlencoded({extended: false}));\n      app.use(bodyParser.json());\n      \n      const pusher = new Pusher({\n          appId: process.env.PUSHER_APP_ID,\n          key: process.env.PUSHER_APP_KEY,\n          secret: process.env.PUSHER_APP_SECRET,\n          cluster: process.env.PUSHER_APP_CLUSTER,\n          encrypted: true\n      });\n      app.set('port', process.env.PORT || 3000);\n      app.post('/vote', (req, res) =\u003e {\n          const payload = {\n              fans: req.body.id,\n              points: 10\n          }\n          pusher.trigger('poll', 'vote', payload);\n          res.send(payload)\n      })\n      app.listen(app.get('port'), () =\u003e {\n          console.log(\"Listening on port \" + app.get('port'));\n      })\n```    \n\nHere we loaded all the necessary middlewares for the Express server and then configured Pusher using the credentials we added to our environment variables earlier. \n\nFurthermore, we also created the `/vote` endpoint that will receive and process the selected `id` sent in from our Ember app. Finally, we triggered an event named `vote` to Pusher Channels on a channel called `poll`. The `poll` channel holds a payload that contains the `id`  of the country selected by the user and the points to update the chart.\n\nOpen another terminal and run the command below to start the server on http://localhost:3000\n\n```\n    node app.js\n```\n\nThis will log a message to the console as shown by the image below:\n\n![ember-chat-node-running](//images.ctfassets.net/1es3ne0caaid/62I54alxBYyYcsmw84GWmu/7721512f40ceb9cc32d9cf290869915f/ember-chat-node-running.png)\n\n## Defining your routes\n\nRoutes in Ember.js plays a significant role in mapping an endpoint to a particular template file. To create a route for our application, well make use of Ember generators to automate the process. Run the command below in the terminal from the root directory of your project:\n\n```\n    ember generate route realtime\n```\n\nThis will create a route file `./app/routes/realtime.js` and a template file for the route at `./app/templates/realtime.hbs`. The route file is the route handler and defines the data that should be loaded and what should happen when the template file is rendered.\n\nIn our case, we need to pass a dataset that will contain the `imgSrc`, `fans` and the `id` of each country to the template file. Now, open the `./app/routes/realtime.js` and update it with the following content:\n\n``` javascript\n    // ./app/routes/realtime.js\n    \n        import Route from '@ember/routing/route';\n      const dataset = [\n          {\n              \"imgSrc\":\"http://res.cloudinary.com/yemiwebby-com-ng/image/upload/v1529405850/nigerians_jojjpz.jpg\",\n              \"id\":\"Nigeria\",\n              \"fans\":\"Nigerian fans\"\n          },\n          {\n              \"imgSrc\":\"http://res.cloudinary.com/yemiwebby-com-ng/image/upload/v1529405850/mexican-fans_wzfkd9.jpg\",\n              \"id\":\"Mexico\",\n              \"fans\":\"Mexican fans\"\n          },\n          {\n              \"imgSrc\":\"http://res.cloudinary.com/yemiwebby-com-ng/image/upload/v1529405850/brazil_ik4yu5.jpg\",\n              \"id\":\"Brazil\",\n              \"fans\":\"Brazilian fans\"\n          },\n          {\n              \"imgSrc\":\"http://res.cloudinary.com/yemiwebby-com-ng/image/upload/v1529405850/argentina_n5e2ye.jpg\",\n              \"id\":\"Argetina\",\n              \"fans\":\"Argentina fans\"\n          }\n      ]\n      export default Route.extend({\n          model() {\n              return dataset;\n          }\n      });\n```    \n\nHere, we declared a `dataset` that will be rendered on the `/realtime` endpoint. Next, well update the template file. Locate `./app/templates/realtime.hbs` and paste the code below in it:\n\n\n``` html\n    {{!-- ./app/templates/realtime.hbs --}}\n    \n      \u003cdiv class=\"main\"\u003e\n         \u003cdiv class=\"container\"\u003e\n           \u003ch1\u003e\u003cb\u003eVote :\u003c/b\u003e Which 2018 world cup team has the best fans\u003c/h1\u003e\n             {{#voting-app poll=model}}{{/voting-app}}\n         \u003c/div\u003e\n      \u003c/div\u003e\n```\n\nWe passed the model to a component named `voting-app`. This will allow us to easily loop over the `dataset`  and attach a click event to the images. Well create this component in the next section.\n\n\n## Creating the UI components\n\nEmber supports the usage and sharing of UI elements on multiple pages. We will leverage this and generate the `voting-app` component for our application. \n\nThe Ember component generally consists of two parts: a JavaScript component file that defines behavior and a Handlebars template that defines the markup for the components UI.\n\n\n## Voting app component\n\nRun the command below to generate the `voting-app`  component:\n\n```\n     ember generate component voting-app\n```\n\n### Voting app component file\n\nAdd the following content to `./app/components/voting-app.js`:\n\n``` javascript\n    // ./app/components/voting-app.js\n      \n      import Component from '@ember/component';\n      import CanvasJS from 'npm:canvasjs';\n      import axios from 'npm:axios';\n      import Pusher from 'npm:pusher-js';\n      \n      let dataPoints = [\n          {label: 'Nigeria', y:1},\n          {label: 'Mexico', y:1},\n          {label: 'Brazil', y:1},\n          {label: 'Argetina', y:1}\n      ]\n      \n      export default Component.extend({\n          init() {\n              this._super(...arguments);\n              const chart = new CanvasJS.Chart('vote-chart-container', {\n                  animationEnabled: true,\n                  theme: 'theme1',\n                  title: {\n                      text: 'The best fans'\n                  },\n                  data: [\n                      {\n                          type: 'doughnut',\n                          dataPoints: dataPoints\n                      }\n                  ]\n              });\n              chart.render();\n              \n              // Initialize Pusher\n              var pusher = new Pusher('YOUR_APP_KEY', { // update your APP_KEY\n                  cluster: 'CLUSTER',\n                  encrypted: true\n              });\n              \n              var channel = pusher.subscribe('poll');\n              channel.bind('vote', (data) =\u003e {\n                  dataPoints = dataPoints.map(x =\u003e {\n                      if (x.label == data.fans) {\n                          x.y += data.points;\n                          return x;\n                      } else {\n                          return x;\n                      }\n                  });\n                  chart.render()\n              });        \n          },\n          \n          actions: {\n              getElement(e) {\n                  var id = e.target.id;\n                  axios.post('http://localhost:3000/vote', {id});\n              }\n          }  \n      });\n```\n\nFirst, we imported the required modules for this component and we added a `dataPoints`  array that will serve as the initial data source for the chart. The objects in the array have a uniform structure of `label`, which stores the name of the country and `y` which stores the points.\n\nIn addition, we used the `Chart` constructor function to create a chart by passing the basic configuration for the chart which includes the `dataPoints`. The chart is rendered by calling `render()` on the constructor function instance.\n\nNext, we initialize Pusher with the `APP_KEY` and `CLUSTER` as obtained from our Pusher account dashboard and then proceeded to use the `subscribe()` method from Pusher to subscribe to the created `poll` channel, update the `dataPoints` variable and re-render the chart with `render()`.\n\nFinally, the `getElement()` action was used to get the `id` of the item clicked and `POST` it to the server. This action will be triggered once a user clicks on an image in the template file for this component.\n\nDont forget to replace the `YOUR_APP_KEY` and `CLUSTER` placeholder with the appropriate details from your Pusher account dashboard.\n\n### Voting app component template\n\nEarlier, we passed the `dataset` from the template file for the `realtime` route to this component\nin  `./app/templates/realtime.hbs`. So here, well loop over each item in the `dataset`. Open `./app/templates/components/voting-app.hbs` and edit as shown below:\n\n``` html\n    {{!-- ./app/templates/components/voting-app.hbs --}}\n     \n      \u003cdiv class=\"col-md-12\"\u003e\n        \u003cdiv class=\"row\"\u003e\n           {{#each poll as |data|}}            \n          \u003cdiv class=\"col-md-3\"\u003e\n            \u003cdiv class=\"poll-device\"\u003e\n                \u003cimg onclick={{action 'getElement'}} src=\"{{data.imgSrc}}\" alt=\"\" id=\"{{data.id}}\"\u003e\n                \u003cp class=\"text-center caption\"\u003e{{data.fans}}\u003c/p\u003e\n            \u003c/div\u003e\n          \u003c/div\u003e\n          {{/each}}\n        \u003c/div\u003e\n      \u003c/div\u003e  \n```\n\n\n## Update the app template\n\nUpdate the application template file by adding the container where the chart will be rendered:\n\n``` html\n    {{!-- ./app/templates/application.hbs --}}\n      \n      \u003cdiv id=\"vote-chart-container\"\u003e\u003c/div\u003e\n      {{outlet}}\n```\n\n## Stylesheet\n\nTo add some minimal styling to the page, open `./app/styles/app.css` and paste this code in it:\n\n``` css\n    // ./app/styles/app.css\n    \n      @import url('https://fonts.googleapis.com/css?family=Spectral');\n      *, *:before, *:after {\n        box-sizing: border-box;\n      }\n      html, body {\n        font-family: 'Spectral', serif;\n        height: 100%;\n      }\n      .main {\n        height: 100%;\n        background-color: #FAFAFA;\n      }\n      .container {\n        padding-top: 20px;\n        padding-bottom: 20px;\n      }\n      h1 {\n        text-align: center;\n      }\n      .caption {\n          font-weight: bold;\n      }\n      .poll-device {\n        border: 1px solid #EDEDED;\n      }\n      .poll-device p {\n        padding: 10px;\n      }\n      .poll-device.selected {\n        background: #EDEDED;\n      }\n      .poll-device:hover {\n        background: #EDEDED;\n        cursor: pointer;\n      }\n      \n      .poll-device img {\n        width: 100%;\n        height: auto;\n        display: block;\n        margin: 0 auto;\n      }\n      #vote-chart-container {\n        height: 300px;\n         width: 100%;\n         background: #f5f5f5;\n      }\n```\n\n\n## Updating the index page\n\nOpen the `./app/index.html` file and include the CDN file for Bootstrap to enhance the styling and layout of our application. Open up the file and add update as shown below:\n\n``` html\n     \u003c!-- ./app/index.html --\u003e\n     \n      \u003c!DOCTYPE html\u003e\n      \u003chtml\u003e\n        \u003chead\u003e\n          \u003cmeta charset=\"utf-8\"\u003e\n          \u003cmeta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\"\u003e\n          \u003ctitle\u003eRealtimeVotingEmber\u003c/title\u003e\n          \u003cmeta name=\"description\" content=\"\"\u003e\n          \u003cmeta name=\"viewport\" content=\"width=device-width, initial-scale=1\"\u003e\n          {{content-for \"head\"}}\n          \u003clink integrity=\"\" rel=\"stylesheet\" href=\"{{rootURL}}assets/vendor.css\"\u003e\n          \u003clink integrity=\"\" rel=\"stylesheet\" href=\"{{rootURL}}assets/realtime-voting-ember.css\"\u003e\n          \n          \u003c!-- add the CDN file --\u003e\n          \u003clink rel=\"stylesheet\" href=\"https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css\"\u003e\n      \n          {{content-for \"head-footer\"}}\n        \u003c/head\u003e\n        \u003cbody\u003e\n          {{content-for \"body\"}}\n          \u003cscript src=\"{{rootURL}}assets/vendor.js\"\u003e\u003c/script\u003e\n          \u003cscript src=\"{{rootURL}}assets/realtime-voting-ember.js\"\u003e\u003c/script\u003e\n          {{content-for \"body-footer\"}}\n        \u003c/body\u003e\n      \u003c/html\u003e\n```\n\n\n## Testing the application\n\nStop the Ember development server if it is currently running by typing `Ctrl + C`  in the terminal and restart it with `ember serve`. In addition, dont forget to also run `node app.js` from another terminal to start the Express server in case you havent done that. Next, open http://localhost:4200/realtime to test the application.\n\n![emberjs-voting-homepage](//images.ctfassets.net/1es3ne0caaid/5KhOVacUi4666eOGEaYy/8ed019fade1ddc0417a0d56cd76d0303/emberjs-voting-homepage.png)\n\nIf you wish to access the application without adding the `/realtime` path, you can edit the applications router file `./app/router.js` by updating the path as shown below:\n\n``` javascript\n    // ./app/router.js\n    \n      import EmberRouter from '@ember/routing/router';\n      import config from './config/environment';\n      const Router = EmberRouter.extend({\n        location: config.locationType,\n        rootURL: config.rootURL\n      });\n      Router.map(function() {\n        // edit the path\n        this.route('realtime', { path: '/'});\n      });\n      export default Router;\n```\n\nNow if you visit http://localhost:4200, you should still see the application fully functioning.\n\n\n## Conclusion\n\nAlthough a voting system definitely has more features than what we have implemented here, I anticipate that with the basic knowledge gained from this tutorial you can easily get more creative and improve your existing project or new ones.\n\nI hope you found this tutorial useful. The complete source code can be found here on [GitHub](https://github.com/yemiwebby/realtime-voting-ember).","metaTitle":"Build a voting system with Ember.js","metaDescription":"Use Ember.js and Node.js to create a simple voting application. Users will pick their favorite World Cup team supporters, and be able to see poll results in realtime."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3LONnVN1tuoo6IQ8kuKG8c","type":"Entry","createdAt":"2018-05-29T09:13:51.830Z","updatedAt":"2018-05-29T09:13:51.830Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a polling app with Ruby on Rails and PostgreSQL","url":"polling-ruby-rails-postgresql","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5Q1I2HN8pqGKG2wYcCeaO2","type":"Entry","createdAt":"2018-02-12T17:24:27.398Z","updatedAt":"2018-02-14T10:09:48.043Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christian Nwamba","url":"christian-nwamba","email":"nwambachristian@gmail.com","twitter":"https://twitter.com/codebeast","github":"https://github.com/christiannwamba","guestWriter":true}}],"projectRepo":"https://github.com/yemiwebby/pusher-rails-chart","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5JtEWXIwaAaOM4KOWMO0qK","type":"Entry","createdAt":"2018-02-12T15:10:44.896Z","updatedAt":"2018-02-26T12:31:11.752Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Data Visualization","url":"data-visualization","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4E7t0TtVFSAyeeA0ckeYe0","type":"Entry","createdAt":"2018-02-12T15:09:41.899Z","updatedAt":"2018-02-26T12:31:11.510Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Chart","url":"realtime-chart","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6i1HPBqlwsE24YEKwqQyuq","type":"Entry","createdAt":"2018-02-12T15:09:31.050Z","updatedAt":"2018-02-26T12:31:11.548Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Graph","url":"realtime-graph","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6S5luSjtlYk4i8eM8AyO4I","type":"Entry","createdAt":"2018-02-02T13:39:48.331Z","updatedAt":"2018-02-12T14:41:19.307Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Ruby","url":"ruby","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"hPWAADkjVmomS68E6seqQ","type":"Entry","createdAt":"2018-02-12T14:48:52.613Z","updatedAt":"2018-02-12T14:48:52.613Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Ruby on Rails","url":"ruby-on-rails","type":"framework"}}],"reviewDate":"2018-05-28","description":"Build a web app with Ruby on Rails, featuring a live poll and chart. User responses are communicated instantly, with the chart updating in realtime.","prerequisite":"You will need Ruby, Rails and PostgreSQL installed on your machine. A basic understanding of Ruby and CoffeeScript will be helpful.","content":"## Introduction\n\nOne of the effective means of interpreting and representing data for users of web applications, is by using data visualization tools such as charts, plots and graphs. This among other things aids simplification of data and presents it in a comprehensible and interactive form.\n\nWith the increase in popularity of the web and its technologies, representing numerical data with graphs in realtime has become inevitable.\n\nTogether in this post, well build a basic poll application where users will be able to provide an answer to a question by selecting one of the options given, then we will proceed to represent these numbers on a pie chart. For a more user-friendly experience, Pusher will be use to update this chart in realtime without the need to refresh the page. This is what the finished application will look like:\n\n![realtime-chart-ruby-demo-1](//images.ctfassets.net/1es3ne0caaid/4nf1sbvChWeA6ukiYE6Kq2/abb514a11cc0b99773d73602745c01c8/realtime-chart-ruby-demo-1.gif)\n\nThe application depicted by the image above will be build with Ruby on Rails and the database powered by PostgreSQL. In addition we will make use of a library called [Chartkick](https://github.com/ankane/chartkick); a tool used for creating beautiful JavaScript charts.\n\n## Prerequisites\n\nA basic understanding of Ruby, CoffeeScript and PostgresSQL will help you with this tutorial. Ensure that you have PostgreSQL installed on your machine. Kindly check the documentation of [PostgreSQL](https://www.postgresql.org/download/), [Ruby](https://www.ruby-lang.org/en/downloads/) and [Rails](http://guides.rubyonrails.org/getting_started.html) for further installations procedures.\n\n## Preparing the application\n\nIt is important that you have both Ruby and Rails installed. Run the following code in your terminal to confirm the successful installation of both Ruby and Rails:\n\n```\n    $ ruby -v // 2.1 or above\n    \n    $ rails -v // 4.2 or above\n```\n\nNext, we will run a command that will create a new Rails application in a folder called `pusher-rails-chart`. Run the command below in your terminal:\n\n```\n    $ rails new pusher-rails-chart -T --database=postgresql\n```\n\nThe `-T` flag was added to prevent generation of the default testing suite as we wont be writing any tests.\n\nGo ahead and change directory into the newly created folder:\n\n```\n    $ cd pusher-rails-chart\n```\n\nIn the root directory of this application, open the `Gemfile` and add the following gems:\n\n```\n    # Gemfile\n    \n    gem 'bootstrap', '~\u003e 4.1.0'\n    gem 'jquery-rails'\n    gem 'pusher'\n    gem 'figaro'\n    gem 'chartkick'\n```\n\nand install them:\n\n```\n    $ bundle install\n```\n\n## Database setup\n\nTo set up the database for our application, create a database called `pusher-rails-chart_development`. Check out this [article](https://www.digitalocean.com/community/tutorials/how-to-setup-ruby-on-rails-with-postgres) on how to create a Postgres database and an associated user and password.\n\nIn your `database.yml` file, under the `development` key, add the following code:\n\n``` yaml\n    # config/database.yml\n    \n    ...\n    development:\n      \u003c\u003c: *default\n      database: pusher-rails-chart_development // add this line if it isn't already there\n      username: database_user // add this line\n      password: user_password // add this line\n    ...  \n```\n\nThe username and password in the code above should have access to the `pusher-rails-chart_development` database. Next, run the following code to setup the database:\n\n```\n    # setup database\n    $ rails db:setup\n```\n\n## Starting the development server\n\nStart the development server by running `rails s` from the terminal and visit http://localhost:3000 in your browser. Your page should look like this:\n\n![youre-on-rails](//images.ctfassets.net/1es3ne0caaid/3sOsgC4S80gOKM8Waqkgui/87c5c9e64375cbb2f8f2516223f4e515/youre-on-rails.png)\n\n## Sign up for a free Pusher account\n\nHead over to Pusher and [sign up for a free account](https://dashboard.pusher.com/accounts/sign_up). \n\n![ruby-create-pusher-account](//images.ctfassets.net/1es3ne0caaid/6uDbn5kOyIOiKyeSeOKqeS/6136b86bc4d9b6ceff7fcf8458375a19/ruby-create-pusher-account.png)\n\nCreate a new app by selecting **Channels apps** on the sidebar and clicking **Create Channels app** button on the bottom of the sidebar:\n\n![ruby-channels-dashboard](//images.ctfassets.net/1es3ne0caaid/5IDtPf0HhC4aKg66gguYq8/183e0b23ec44fdfc23a38921e11fe9e3/ruby-channels-dashboard.png)\n\nConfigure an app by providing basic information requested in the form presented. You can also choose the environment you intend to integrate Pusher with, to be provided with boilerplate code:\n\n![ruby-new-pusher-app](//images.ctfassets.net/1es3ne0caaid/3qnLmThfu8ue2ImYKKSw8a/2e4f39b15b8787e0f6d3b4f2ea022d95/ruby-new-pusher-app.png)\n\nYou can retrieve your keys from the **App Keys** tab:\n\n![ruby-app-keys](//images.ctfassets.net/1es3ne0caaid/1XHWE3GCeoCeqsMCO8uM2q/52523148497d08f6e9ba4f52fe0750f6/ruby-app-keys.png)\n\nNow that you have your Pusher API keys, head over to `config/initializers` directory. Create a `pusher.rb` file and add the following code:\n\n``` ruby\n    # config/initializers/pusher.rb\n    \n    require 'pusher'\n    \n    Pusher.app_id = ENV[\"PUSHER_APP_ID\"]\n    Pusher.key = ENV[\"PUSHER_KEY\"]\n    Pusher.secret = ENV[\"PUSHER_SECRET\"]\n    Pusher.cluster = ENV[\"PUSHER_CLUSTER\"]\n    Pusher.logger = Rails.logger\n    Pusher.encrypted = true\n```\n\nIn your terminal, run:\n\n```\n    $ figaro install\n```\n\nThe command above will generate an `application.yml` file. Locate the newly generate file in `config/application.yml` and add the Pusher credentials obtained from your dashboard:\n\n``` yaml\n    # config/application.yml\n    \n    PUSHER_APP_ID: 'YOUR_APP_ID'\n    PUSHER_KEY: 'YOUR_APP_KEY'\n    PUSHER_SECRET: 'YOUR_APP_SECRET'\n    PUSHER_CLUSTER: 'CLUSTER'\n```\n\n## Setting up the model and controller\n\nNext, we will go ahead and generate a model and controller for our application. In your terminal, while in the projects directory, run the following code:\n\n```\n    // generate poll model and controller\n    $ rails g scaffold poll name:string laptop:binary\n    \n    // run database migrations\n    $ rails db:migrate\n```\n\n## Setting up the homepage\n\nAt the moment, if you restart the development server and reload the browser page, the welcome page will still be displayed. This is because we have not instructed Rails to show the homepage whenever we navigate to the root `URL` of our site. \n\nTo tell Rails where the actual homepage is, update the route file with the following code:\n\n``` ruby\n    # config/routes.rb\n    \n    ...\n    root 'polls#index'\n    ...\n```    \n\nIn your `application.js` file, add the following code just before the last line:\n\n``` javascript\n    // app/assets/javascripts/application.js\n    \n    ...\n    //= require jquery3 # add this line\n    //= require popper # add this line\n    //= require bootstrap # add this line\n    //= require Chart.bundle # add this line\n    //= require chartkick # add this line\n    //= require_tree .\n    ...\n```\n\n## Styling\n\nTo set up a basic styling for our application, go ahead and rename your `application.css` file to `application.scss`, import Bootstrap and add some custom styles:\n\n``` scss\n    // app/assets/stylesheets/application.scss\n    \n     @import \"bootstrap\";\n     @import url('https://fonts.googleapis.com/css?family=Josefin+Sans');\n     body {\n      font-family: 'Josefin Sans', sans-serif;\n     }\n     .navbar-brand {\n       color: #FFF !important;\n       \u0026:hover {\n         background-color: unset;\n       }\n     }\n     .form-control {\n       outline: none !important;\n       \n       \u0026:focus {\n        border-color: transparent !important;\n        border: 1px !important;\n       }\n     }\n```    \n\nAt the moment, if we restart the development server and reload the application, we would notice changes in the fonts of our page, this is because we have successfully included Bootstrap.\n\nIf you encounter any errors relating to `application.html.erb` , in `config/boot.rb`, change the ExecJS runtime from Duktape to Node.\n\n``` ruby\n    # config/boot.rb\n    \n    ENV['EXECJS_RUNTIME'] = 'Node'\n```\n\n## Setting up the polls table\n\nTo set up the view, replace the code in your `index.html.erb` file with the following:\n\n``` html\n    \u003c%# app/views/polls/index.html.erb %\u003e\n    \n    \u003cdiv class=\"container-fluid\"\u003e\n      \u003cdiv class=\"row\"\u003e\n        \u003cdiv class=\"col-5 offset-1\"\u003e\n          \u003cp id=\"notice\"\u003e\u003c%= notice %\u003e\u003c/p\u003e\n          \u003ch1\u003ePolls\u003c/h1\u003e\n          \u003ctable id='poll-table' class=\"table table-hover\"\u003e\n            \u003cthead class=\"thead-light\"\u003e\n              \u003ctr\u003e\n                \u003cth\u003eName\u003c/th\u003e\n                \u003cth\u003eLaptop\u003c/th\u003e\n                \u003cth colspan=\"3\"\u003e\u003c/th\u003e\n              \u003c/tr\u003e\n            \u003c/thead\u003e\n            \u003ctbody\u003e\n              \u003c% @polls.each do |poll| %\u003e\n                \u003ctr\u003e\n                  \u003ctd\u003e\u003c%= poll.name %\u003e\u003c/td\u003e\n                  \u003ctd\u003e\u003c%= poll.laptop %\u003e\u003c/td\u003e\n                  \u003ctd\u003e\u003c%= link_to 'Show', poll %\u003e\u003c/td\u003e\n                  \u003ctd\u003e\u003c%= link_to 'Edit', edit_poll_path(poll) %\u003e\u003c/td\u003e\n                  \u003ctd\u003e\u003c%= link_to 'Destroy', poll, method: :delete, data: { confirm: 'Are you sure?' } %\u003e\u003c/td\u003e\n                \u003c/tr\u003e\n              \u003c% end %\u003e\n            \u003c/tbody\u003e\n          \u003c/table\u003e\n        \u003c/div\u003e\n        \n        \u003cdiv class=\"col-6\"\u003e\n          \u003c%= pie_chart [[\"Yes\", Poll.where(laptop: \"yes\").count], [\"No\", Poll.where(laptop: \"no\").count]], id:'polls'%\u003e \n        \u003c/div\u003e\n      \u003c/div\u003e\n      \n       \u003cdiv class=\"row\"\u003e\n        \u003cdiv class='col-11 offset-1 col-sm-4'\u003e\n          \u003ch3\u003eNew poll\u003c/h3\u003e\n          \u003c%= render 'form', poll: @poll %\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n    \u003c/div\u003e\n```\n\nHere, we have used Bootstrap to style our table and within the second column of the first row we included a line to render the pie chart for this application on the homepage. [Chartkick](https://www.chartkick.com/) helps simplify the use of chart. You can check [here](https://github.com/ankane/chartkick) for more information.\n\nNext, update the index method of the polls controller, to include the following:\n\n``` ruby\n    # app/controllers/polls_controller.rb\n    \n    class PollsController \u003c ApplicationController\n     ...\n      def index\n        @polls = Poll.all\n        @poll = Poll.new # add this line\n      end\n      ...\n    end\n```    \n\nThe code above adds Bootstrap styling to the table on our landing page. It also instantiates a new poll record in the `index` method of the polls controller. If you reload the landing page, you should see the form.\n\n## Submitting a new poll\n\nCurrently adding a new poll record redirects us to a different page. We would like to handle that with AJAX instead. To achieve this, add `remote: true` and `format: :json` to your form. Replace the code in `_form.html.erb` with the following: \n\n``` html\n    // app/views/polls/_form.html.erb\n    \n    \u003c%= form_with(model: poll, remote: true, format: :json, id: 'add_poll') do |form| %\u003e\n      \u003c% if poll.errors.any? %\u003e\n        \u003cdiv id=\"error_explanation\"\u003e\n          \u003ch2\u003e\u003c%= pluralize(poll.errors.count, \"error\") %\u003e prohibited this poll from being saved:\u003c/h2\u003e\n          \u003cul\u003e\n          \u003c% poll.errors.full_messages.each do |message| %\u003e\n            \u003cli\u003e\u003c%= message %\u003e\u003c/li\u003e\n          \u003c% end %\u003e\n          \u003c/ul\u003e\n        \u003c/div\u003e\n      \u003c% end %\u003e\n      \u003cdiv class=\"field\"\u003e\n        \u003c%= form.label :name %\u003e\n        \u003c%= form.text_field :name, id: :poll_name, class: \"form-control\" %\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"field\"\u003e\n        \u003c%= form.label :laptop, \"Do you own a laptop? \" %\u003e\u003cbr /\u003e\n        \u003c%= form.label :laptop, \"Yes\", :value =\u003e \"true\" %\u003e\n        \u003c%= form.radio_button :laptop, 'yes', :checked =\u003e true %\u003e\n        \u003c%= form.label :laptop, \"No\", :value =\u003e \"false\" %\u003e\n        \u003c%= form.radio_button :laptop, 'no'%\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"actions\"\u003e\n        \u003c%= form.submit 'submit', class: \"btn btn-success\"%\u003e\n      \u003c/div\u003e\n    \u003c% end %\u003e\n```    \n\nNow that our form uses AJAX, well handle updating the page with CoffeeScript. Open `app/assets/javascripts/polls.coffee` and paste in the code below:\n\n``` coffeescript\n    # app/assets/javascripts/polls.coffee\n    \n    submitPoll = (data) -\u003e\n      $('#poll-table tbody').append \"\"\"\n      \u003ctr\u003e\n          \u003ctd\u003e#{data.name}\u003c/td\u003e\n          \u003ctd\u003e#{data.laptop}\u003c/td\u003e\n          \u003ctd\u003e\u003ca href=\"/polls/#{data.id}\"\u003eShow\u003c/a\u003e\u003c/td\u003e\n          \u003ctd\u003e\u003ca href=\"/polls/#{data.id}/edit\"\u003eEdit\u003c/a\u003e\u003c/td\u003e\n          \u003ctd\u003e\u003ca data-confirm=\"Are you sure?\" rel=\"nofollow\" data-method=\"delete\" href=\"/polls/#{data.id}\"\u003eDestroy\u003c/a\u003e\u003c/td\u003e\n      \u003c/tr\u003e\n      \"\"\"\n      return\n    \n    $ -\u003e\n      $('#add_poll').on 'ajax:success', (data) -\u003e\n        $('#add_poll')[0].reset()\n        submitPoll data.detail[0]\n        return\n      return\n```\n\nIn the above code, we added an `ajax:success` event listener to our form courtesy of [jQuery-ujs](https://github.com/rails/jquery-ujs/wiki/ajax). Whenever we add a new response, we get our newly added answer and update our page with it.\n\nLets reload our page and add a few responses, they should pop up on the page as soon as we hit submit.\n\nIf the `laptop` column shows undefined or returns a null value, this has to do with mass assignment protection from the controller, you can read more about this [here](http://guides.rubyonrails.org/action_controller_overview.html#strong-parameters). To fix this, navigate to `app/controllers/polls_controller.rb` and within the `poll_param` method, add `:laptop` to the list of params.\n\n## Realtime service with Pusher\n\nTo update our table realtime, we will publish the new poll record to a channel and subscribe to it on the frontend of our app. In the poll model, well add an `after_commit` callback which will call a method that will publish the new poll. We'll use `after_commit` so data is published whenever we add or update a poll. \n\nAdd the following code to the poll model:\n\n``` ruby\n    # app/models/poll.rb\n    \n    class Poll \u003c ApplicationRecord\n        after_commit :notify_pusher, on: [:create, :update]\n        def notify_pusher\n        Pusher.trigger('poll', 'new', self.as_json)\n        end  \n    end\n```    \n\nHere, our initialized Pusher client triggers an event called `new` through a channel named `poll`.\n\n## Updating the frontend of the application\n\nThe client side of our application needs to listen for those changes that are being pushed by the server and update the view with it. To do this, well rename our `polls.coffee` file to `polls.coffee.erb` so we can add some Ruby code in it.\n\nUpdate the file with the following code:\n\n``` coffeescript\n    # app/assets/javascripts/polls.coffee.erb\n    \n    submitPoll = (data) -\u003e\n      $('#poll-table tbody').append \"\"\"\n      \u003ctr\u003e\n          \u003ctd\u003e#{data.name}\u003c/td\u003e\n          \u003ctd\u003e#{data.laptop}\u003c/td\u003e\n          \u003ctd\u003e\u003ca href=\"/polls/#{data.id}\"\u003eShow\u003c/a\u003e\u003c/td\u003e\n          \u003ctd\u003e\u003ca href=\"/polls/#{data.id}/edit\"\u003eEdit\u003c/a\u003e\u003c/td\u003e\n          \u003ctd\u003e\u003ca data-confirm=\"Are you sure?\" rel=\"nofollow\" data-method=\"delete\" href=\"/polls/#{data.id}\"\u003eDelete\u003c/a\u003e\u003c/td\u003e\n      \u003c/tr\u003e\n      \"\"\"\n      return\n    $ -\u003e\n    updateChart = (data) -\u003e\n      chart = Chartkick.charts['polls'].getChartObject()\n      if data.laptop == 'yes'\n        chart.data.datasets[0].data[0]++\n        chart.update()\n      else\n        chart.data.datasets[0].data[1]++\n        chart.update()\n    \n    $ -\u003e\n      $('#add_poll').on 'ajax:success', (data) -\u003e\n        $('#add_poll')[0].reset()\n        return\n      pusher = new Pusher('\u003c%= ENV[\"PUSHER_KEY\"] %\u003e',\n        cluster: '\u003c%= ENV[\"PUSHER_CLUSTER\"] %\u003e'\n        encrypted: true)\n      channel = pusher.subscribe('poll')\n      channel.bind 'new', (data) -\u003e\n          submitPoll data\n          updateChart data\n          return\n      return\n```    \n\nIn the code above, we created two methods:\n\n1.  `submitPoll()`: updates the poll table with new records upon submission of the polls form.\n\n2.  `updateChart()`: updates the datasets of our pie chart without the need to refresh the page.\n\nLastly, add the Pusher library inside the head tag in the `application.html.erb` file just before the `javascript_include_tag`:\n\n``` html\n    \u003c%# . app/views/layouts/application.html.erb %\u003e\n    \n    \u003chead\u003e\n        \u003ctitle\u003ePoll\u003c/title\u003e\n        \u003c%= csrf_meta_tags %\u003e\n        \u003c%= csp_meta_tag %\u003e\n        \u003c%= stylesheet_link_tag    'application', media: 'all', 'data-turbolinks-track': 'reload' %\u003e\n        \u003cscript src=\"https://js.pusher.com/4.1/pusher.min.js\"\u003e\u003c/script\u003e\n        \u003c%= javascript_include_tag 'application', 'data-turbolinks-track': 'reload' %\u003e\n      \u003c/head\u003e\n```\n\n## Testing the application\n\nRestart the development server if it is currently running. Check your page on http://localhost:3000:\n\n![realtime-chart-ruby-demo-2](//images.ctfassets.net/1es3ne0caaid/4P5ZUY6cGscOIge6IWWGoI/c6b065bec773b431ea99fbc41ddd89b1/realtime-chart-ruby-demo-2.gif)\n\n## Conclusion\n\nRealtime and visual analysis in web applications has come to stay and can only get better. As shown by this post, you can now go ahead and give your users the best of visual realtime update in Ruby on Rails applications.\n\nI hope you found this tutorial helpful. Feel free to download the complete source can be found on [GitHub](https://github.com/yemiwebby/pusher-rails-chart).","metaTitle":"Build a polling app with Ruby on Rails and PostgreSQL","metaDescription":"Use Ruby on Rails and PostgreSQL to create a live polling application. Chartkick provides the graph visuals, which update in realtime."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5kd5NYciXeUsoa4ogwCWu6","type":"Entry","createdAt":"2018-05-29T09:13:20.560Z","updatedAt":"2018-05-29T09:13:20.560Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a voting application with Go and Vue.js","url":"voting-app-go-vuejs","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5q8aOMtjzOkUkOqeCOkaws","type":"Entry","createdAt":"2018-02-15T16:09:28.291Z","updatedAt":"2018-02-15T16:09:28.291Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Neo Ighodaro","url":"neo-ighodaro","email":"neo@creativitykills.co","twitter":"https://twitter.com/NeoIghodaro","github":"https://github.com/neoighodaro","guestWriter":true}}],"projectRepo":"https://github.com/neoighodaro/realtime-poll-go-pusher","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"3FPNv1ezQkGsyYyW8eUSiy","type":"Entry","createdAt":"2018-02-12T14:52:13.587Z","updatedAt":"2018-02-26T12:31:14.749Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Go","url":"go","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"2tOJiaXqzKIqosUKm22cMk","type":"Entry","createdAt":"2018-02-12T14:43:50.931Z","updatedAt":"2018-02-12T14:43:50.931Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Vue.js","url":"vue-js","type":"framework"}}],"reviewDate":"2018-05-28","description":"Build a polling app using Echo, the Go framework, Vue.js and SQLite. Users are able to vote on their favorite JavaScript frameworks. Results update instantly.","prerequisite":"You will need Go installed on your machine, and a basic knowledge of Go and JavaScript.","content":"Digital polls are a great way for an online crowd to express their opinions towards a set of items on the list. In the past, to participate in voting, voters would have to physically be present at the elected place of vote so that they can cast their ballots. Such a drag right?\n\n## What we will be building\n\nIn this tutorial we will demonstrate how to build a realtime voting poll application. We will write the backend API for handling the HTTP requests and saving updates to the database ([SQLite](https://www.sqlite.org/)) in [Go](https://golang.org/). \n\nWe will be using the Go framework, [Echo](https://echo.labstack.com/), to keep boilerplate to a minimum. You can think of Echo to be to Go what [Laravel](https://laravel.com/) is to PHP. If you have prior experience using web frameworks to create routes and handle HTTP requests, the code in this tutorial should look somewhat familiar.\n\nFor the frontend section of this project, well use [Vue.js](https://vuejs.org/). With its reactive properties, Vue.js will re-render the DOM whenever there is an update to the `upvotes` or `downvotes` of a vote member. Well also require a bit of [jQuery](https://jquery.com/) to handle some functionality.\n\nTo make things work in realtime, well integrate Pusher into the application. [Pusher](https://pusher.com) makes it very easy to create realtime applications.\n\nWhen we are done with our application, heres what we will have:\n\n![voting-app-go-demo-1](//images.ctfassets.net/1es3ne0caaid/6aAc9s9C9OkIMCIs88o2Sg/47c72b8039deedd5e655159bb8d61b86/voting-app-go-demo-1.gif)\n\n## Requirements\n\nTo follow along with this article, you will need the following:\n\n- An IDE of your choice like [Visual Studio Code](https://code.visualstudio.com/).\n- [Go](https://golang.org/doc/install) (version \u003e= 0.10.x) installed on your computer. [Heres how you can install Go](https://golang.org/doc/install#install).\n- Basic knowledge of the Go programming language.\n- Basic knowledge of JavaScript (ES6).\n- Basic knowledge of Vue.js and jQuery.\n\nOnce you have all the above requirements, we can proceed.\n\n## Setting up our codebase\n\nTo get started create a new directory in our `$GOPATH` and launching that directory with an IDE. We can do this by running the commands below:\n\n```\n    $ cd $GOPATH/src\n    $ mkdir gopoll \n    $ cd gopoll\n```\n\nThe directory above will be our project directory. Next create our first `.go` file where our main function will go, we will call it `poll.go`.\n\nLets import some useful Go packages that well be using within our project. For a start, we have to fetch the Echo and SQLite packages from GitHub. Run the following commands to pull in the packages:\n\n```\n    $ go get github.com/labstack/echo\n    $ go get github.com/labstack/echo/middleware\n    $ go get github.com/mattn/go-sqlite3\n```\n\n\u003e   If you use Windows and you encounter the error cc.exe: sorry, unimplemented: 64-bit mode not compiled in , then you need a Windows gcc port, such as https://sourceforge.net/projects/mingw-w64/. Also see this [GitHub issue](https://github.com/mattn/go-sqlite3/issues/297).\n\nOpen the `poll.go` file and paste in the following code:\n\n``` go\n    package main\n    \n    import (\n        // \"database/sql\"\n        \"github.com/labstack/echo\"\n        \"github.com/labstack/echo/middleware\"\n        // _ \"github.com/mattn/go-sqlite3\"\n    )\n```\n\nAbove we also imported the `database/sql` library but we dont have to use `go get` because this is a part of the standard Go library.\n\n## Setting up the routes and database\n\nTo enable Go to run our application, we need a `main` function, so lets create that before we think of creating the routes and setting up the database.\n\nOpen the `poll.go` file and in there add the following code to the file:\n\n``` go\n    func main() {\n        e := echo.New()\n    \n        // Middleware\n        e.Use(middleware.Logger())\n        e.Use(middleware.Recover())\n    \n        // Define the HTTP routes\n        e.GET(\"/polls\", func(c echo.Context) error { \n            return c.JSON(200, \"GET Polls\") \n        })\n    \n        e.PUT(\"/polls\", func(c echo.Context) error { \n            return c.JSON(200, \"PUT Polls\") \n        })\n    \n        e.PUT(\"/polls/:id\", func(c echo.Context) error {\n            return c.JSON(200, \"UPDATE Poll \" + c.Param(\"id\"))    \n        })\n    \n        // Start server\n        e.Logger.Fatal(e.Start(\":9000\"))\n    }\n```\n\nAwesome, weve created some basic routes and even if they dont do more than echo static text, they should be able to handle matching URL requests.\n\nWe included the final line because we want to instruct Go to start the application using Echos `Start` method. This will start Gos standard HTTP server and listen for requests on the port `9000`.\n\nWe can test the routes in our application as it is now by compiling it down, running it and making requests to the port `9000` of our local host with [Postman](https://www.getpostman.com/).\n\n```\n    $ go run poll.go\n```\n\nNow we can head over to Postman and point the address to `localhost:9000/polls` with a `GET` HTTP verb. To try the PUT request, we can use an address such as `localhost:9000/polls/3`.\nAssuming that everything works as we planned, you should get the following screens:\n\n### GET request\n\n![voting-app-go-GET](//images.ctfassets.net/1es3ne0caaid/50FAIAIQzukai0yCaIwGie/bc264375d33faa2621d121dc0ab0f5b8/voting-app-go-GET.png)\n\n### PUT request\n\n![voting-app-go-PUT](//images.ctfassets.net/1es3ne0caaid/3kGQb7GcHmCasScCWA0Ica/cebb54775deb8809c8651c9a435edaf1/voting-app-go-PUT.png)\n\nIn the `poll.go` file, we will write some code to initialize a database with a filename of `Storage.db` on application run. The `Sql` driver can create this file for us if it doesnt already exist. After the database has been created, we will run a function to migrate and seed the database for us if it hasnt already been migrated and seeded.\n\nOpen the `poll.go` file and add the following functions to the file:\n\n``` go\n    func initDB(filepath string) *sql.DB {\n            db, err := sql.Open(\"sqlite3\", filepath)\n    \n            if err != nil {\n                panic(err)\n            }\n    \n            if db == nil {\n                panic(\"db nil\")\n            }\n            \n            return db\n    }\n    \n    func migrate(db *sql.DB) {\n        sql := `\n            CREATE TABLE IF NOT EXISTS polls(\n                    id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,\n                    name VARCHAR NOT NULL,\n                    topic VARCHAR NOT NULL,\n                    src VARCHAR NOT NULL,\n                    upvotes INTEGER NOT NULL,\n                    downvotes INTEGER NOT NULL,\n                    UNIQUE(name)\n            );\n        \n            INSERT OR IGNORE INTO polls(name, topic, src, upvotes, downvotes) VALUES('Angular','Awesome Angular', 'https://cdn.colorlib.com/wp/wp-content/uploads/sites/2/angular-logo.png', 1, 0);\n        \n            INSERT OR IGNORE INTO polls(name, topic, src, upvotes, downvotes) VALUES('Vue', 'Voguish Vue','https://upload.wikimedia.org/wikipedia/commons/thumb/5/53/Vue.js_Logo.svg/400px-Vue.js_Logo.svg.png', 1, 0);\n        \n            INSERT OR IGNORE INTO polls(name, topic, src, upvotes, downvotes) VALUES('React','Remarkable React','https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/React-icon.svg/1200px-React-icon.svg.png', 1, 0);\n        \n            INSERT OR IGNORE INTO polls(name, topic, src, upvotes, downvotes) VALUES('Ember','Excellent Ember','https://cdn-images-1.medium.com/max/741/1*9oD6P0dEfPYp3Vkk2UTzCg.png', 1, 0);\n        \n            INSERT OR IGNORE INTO polls(name, topic, src, upvotes, downvotes) VALUES('Knockout','Knightly Knockout','https://images.g2crowd.com/uploads/product/image/social_landscape/social_landscape_1489710848/knockout-js.png', 1, 0);\n       `\n        _, err := db.Exec(sql)\n    \n        if err != nil {\n                panic(err)\n        }\n    }\n```\n\nThe first function, `initDB` is pretty straightforward with its task, it makes an attempt to open a database file, or creates it when it doesnt exist. In a case where it is unable to read the database file or create it, the program exits because the database is crucial to the logic of the application.\n\nThe `migrate` function, does exactly what its name suggests. It runs an SQL statement against the database to ensure that the `polls` table is created if it isnt already created, and seeded with some initial values for this example.\n\nFor our example, we will be seeding the database with some values for a few JavaScript frameworks. Each framework will have a column for registering the state of `upvotes` and `downvotes`. Like the `initDB` function, if the `migrate` function fails to migrate and seed the database, the program will return an error.\n\nNext open the `poll.go` file and add the following into the `main` function right after the middleware definitions:\n\n``` go\n    // [...]\n    \n    // Initialize the database\n    db := initDB(\"storage.db\")\n    migrate(db)\n    \n    // [...]\n```\n\nNext, uncomment the imports in the `poll.go` file. Now we can test to see if our application works. Run the following command to build and run the application:\n\n```\n    $ go run poll.go\n```\n\nIf we look at the project directory, there should be a `storage.db` file there. This means that our code executed correctly.\n\nGreat, now lets create the handlers.\n\n## Creating the handlers\n\nWeve already created the endpoints with which the frontend can interact with the backend. Now we need to build the backend logic that will handle the received requests on specific routes. We can achieve this by registering several handler functions of our own.\n\nLets begin by creating and navigating into a new directory called `handlers`:\n\n```\n    $ mkdir handlers\n    $ cd handlers\n```\n\nLets create a new `handlers.go` file in this `handlers` directory and paste the following code into the file:\n\n``` go\n    package handlers\n    \n    import (\n        \"database/sql\"\n        \"net/http\"\n        \"strconv\"\n        \"github.com/labstack/echo\"\n    )\n```\n\nNext, open the `poll.go` file and import the `handlers.go` package in there:\n\n``` go\n    import (\n        // [...]\n    \n        \"gopoll/handlers\"\n    \n        // [...]\n    )\n```\n\nIn the same file, replace the route definitions from earlier with the ones below:\n\n``` go\n    // [...]\n    \n    // Define the HTTP routes\n    e.File(\"/\", \"public/index.html\")\n    e.GET(\"/polls\", handlers.GetPolls(db))\n    e.PUT(\"/poll/:index\", handlers.UpdatePoll(db))\n    \n    // [...]\n```\n\nYou may have noticed that we included an extra route above:\n\n```\n    e.File(\"/\", \"public/index.html\")\n```\n\nThis is the route that will process requests sent to the `/` endpoint. We need this route to serve a static `HTML` file that we are yet to create, this file will hold our client-side code and live in the public directory.\n\nNow back to the `handlers.go` file. In order for us to return arbitrary JSON as responses in our handler, we need to register a map just below our import statements:\n\n```\n    type H map[string]interface{}\n```\n\nThis maps strings as keys and anything else as values. In Go, the \"interface\" keyword represents anything from a primitive datatype to a user defined type or struct.\n\nLets create our handlers. We will make it so they receive an instance of the database well be passing from the routes. Theyll also need to implement the [Echo.HandlerFunc](https://godoc.org/github.com/labstack/echo#HandlerFunc) interface so they can be used by the routes. \n\nOpen the `handlers.go` file and paste the following:\n\n``` go\n    func GetPolls(db *sql.DB) echo.HandlerFunc {\n        return func(c echo.Context) error {\n            return c.JSON(http.StatusOK, models.GetPolls(db))\n        }\n    }\n    \n    func UpdatePoll(db *sql.DB) echo.HandlerFunc {\n        return func(c echo.Context) error {\n            var poll models.Poll\n    \n            c.Bind(\u0026poll)\n    \n            index, _ := strconv.Atoi(c.Param(\"index\"))\n    \n            id, err := models.UpdatePoll(db, index, poll.Name, poll.Upvotes, poll.Downvotes)\n    \n            if err == nil {\n                return c.JSON(http.StatusCreated, H{\n                    \"affected\": id,\n                })\n            }\n    \n            return err\n        }\n    }\n```\n\nThe `GetPolls` function returns the `StatusOK` status code and passes the received instance of the database to a model function that we will create soon. In the next section, well create the models package, define its functions and import it into the handlers package.\n\nThe `UpdatePoll` function is defined to work on a single poll, it calls `c.Bind` on an instance of `models.Poll`; this call is responsible for taking a `JSON` formatted body sent in a `PUT` request and mapping it to a Poll struct. The Poll struct will be defined in the models package. \n\nSince this handler will be receiving an `index` parameter from the route, we are using the `strconv` package and the `Atoi` (alpha to integer) function to make sure the index is cast to an integer. This will ensure that we can correctly point to a row when we query the database. We have also done a bit of error checking in this function, we want to ensure that the application terminates properly if there is ever an error.\n\nLets move on to the creation of the models package.\n\n## Creating the models\n\nIt is a good practice to keep codebases as modular as possible so we have avoided making direct calls to the database in the `handlers` package. Instead, we will abstract the database logic into the models package so that the interactions are performed by the models.\n\nLets create a new directory in the working directory of our application. This is where the models package will go, we can run this command:\n\n```\n    $ mkdir models\n```\n\nIn the `models` directory create a new `models.go` file and paste the following into the code:\n\n``` go\n    package models\n    \n    import (\n        \"database/sql\"\n        _ \"github.com/mattn/go-sqlite3\"\n    )\n```\n\nNext import the models package into the `handlers.go` file:\n\n``` go\n    package handlers\n    \n    import (\n        // [...]\n    \n        \"gopoll/models\"\n        \n        // [...]\n    )\n```\n\nIn the models package, lets create a Poll `type` which is a struct with six fields:\n\n- `ID` -  the id of the poll.\n- `Name` - the name of the poll.\n- `Topic` - the topic of the poll.\n- `Src` - the link to an image  for the poll.\n- `Upvotes` - the number of upvotes on the poll.\n- `Downvotes` - the number of downvotes on the poll.\n\nIn Go, we can add metadata to variables by putting them within backticks. We can use this feature to define what each field should look like when converted to `JSON`. This will also help the `c.Bind` function in the `handlers.go` file to know how to map `JSON` data when registering a new Poll.\n\nWe will also use the `type` keyword to define a collection of Polls, this is required for when there is a request to return all the Polls in the database. Wed simply aggregate them into an instance of this collection and return them.\n\n``` go\n    type Poll struct {\n        ID        int     `json:\"id\"`\n        Name      string  `json:\"name\"`\n        Topic     string  `json:\"topic\"`\n        Src       string  `json:\"src\"`\n        Upvotes   int     `json:\"upvotes\"`\n        Downvotes int     `json:\"downvotes\"`\n    }\n    \n    type PollCollection struct {\n        Polls []Poll `json:\"items\"`\n    }\n```\n\nNow lets define the `GetPolls` function. This function will be responsible for getting the polls from the database, returning them as an instance of a Poll collection and returning them to the function that invoked it. This function doesnt use any new features and is pretty straight forward:\n\n``` go\n    func GetPolls(db *sql.DB) PollCollection {\n        sql := \"SELECT * FROM polls\"\n        \n        rows, err := db.Query(sql)\n    \n        if err != nil {\n            panic(err)\n        }\n    \n        defer rows.Close()\n    \n        result := PollCollection{}\n    \n        for rows.Next() {\n            poll := Poll{}\n            \n            err2 := rows.Scan(\u0026poll.ID, \u0026poll.Name, \u0026poll.Topic, \u0026poll.Src, \u0026poll.Upvotes, \u0026poll.Downvotes)\n    \n            if err2 != nil {\n                panic(err2)\n            }\n            \n            result.Polls = append(result.Polls, poll)\n        }\n        \n        return result\n    }\n```\n\nWe also need to define an `UpdatePoll` method that will update the state of the `upvotes` and `downvotes` of a Poll. In the same file paste the following code:\n\n``` go\n    func UpdatePoll(db *sql.DB, index int, name string, upvotes int, downvotes int) (int64, error) {\n        sql := \"UPDATE polls SET (upvotes, downvotes) = (?, ?) WHERE id = ?\"\n    \n        // Create a prepared SQL statement\n        stmt, err := db.Prepare(sql)\n    \n        // Exit if we get an error\n        if err != nil {\n            panic(err)\n        }\n    \n        // Make sure to cleanup after the program exits\n        defer stmt.Close()\n    \n        // Replace the '?' in our prepared statement with 'upvotes, downvotes, index'\n        result, err2 := stmt.Exec(upvotes, downvotes, index)\n    \n        // Exit if we get an error\n        if err2 != nil {\n            panic(err2)\n        }\n        \n        return result.RowsAffected()\n    }\n```\n\nYou might have noticed we are using [prepared SQL statements](https://en.wikipedia.org/wiki/Prepared_statement) in the `UpdatePoll` function. There are several benefits to doing this. We ensure SQL statements are always cleaned up and safe from [SQL injection](https://en.wikipedia.org/wiki/SQL_injection) attacks. Prepared SQL statements also help our program execute faster since the statements will be compiled and cached for multiple uses.\n\n## Building out our frontend\n\nNow that we are done with the backend, lets add some frontend code. Create a `public` directory in the root directory of your project. In this directory create an `index.html` file. This is where we will add most of the frontend magic.\n\nBecause we want to keep things simple, we will include the Vue.js and jQuery code in the `index.html` file. Open the file and paste the following HTML code into it:\n\n``` html\n    \u003c!DOCTYPE html\u003e\n    \u003chtml lang=\"en\"\u003e\n    \u003chead\u003e\n        \u003cmeta charset=\"UTF-8\"\u003e\n        \u003cmeta name=\"viewport\" content=\"width=device-width, initial-scale=1\"\u003e\n        \u003cmeta http-equiv=\"X-UA-Compatible\" content=\"ie=edge\"\u003e\n        \u003clink rel=\"stylesheet\" href=\"https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0-beta.2/css/bootstrap.min.css\"\u003e\n        \u003clink rel=\"stylesheet\" href=\"https://use.fontawesome.com/releases/v5.0.13/css/all.css\"\u003e\n        \u003cscript src=\"https://unpkg.com/axios/dist/axios.min.js\"\u003e\u003c/script\u003e\n        \u003cscript src=\"https://cdn.jsdelivr.net/npm/vue@2.5.16/dist/vue.js\"\u003e\u003c/script\u003e\n        \u003cscript src=\"https://cdnjs.cloudflare.com/ajax/libs/jquery/2.2.4/jquery.min.js\"\u003e\u003c/script\u003e\n        \u003ctitle\u003eA GO Voting Poll Application With Pusher \u003c/title\u003e\n    \u003c/head\u003e\n    \u003cbody\u003e\n        \u003cdiv id=\"msg\" style=\"display: none; padding: 1em; position: fixed; margin: 0px 5px;\"\u003e\u003c/div\u003e\n        \u003cdiv id=\"app\" class=\"container\"\u003e\n            \u003cdiv class=\"row\" style=\"margin: 1em 0em\" v-for=\"(poll, index) in polls\"\u003e\n            \u003cdiv class=\"card col-md-4\" style=\"margin: 20px auto; width: 25rem; background: rgb(93, 95, 104)\"\u003e\n                \u003cimg class=\"card-img-top\" :src=\"poll.src\" alt=\"Card image\"\u003e\n                \u003cdiv class=\"card-body\" \u003e\n                  \u003cp class=\"card-text text-center\" style=\"font-size: 1.5em; color: white; font-weight: bold\"\u003e {{ poll.topic }} as the best JS framework \u003c/p\u003e\n                    \u003cform\u003e\n                        \u003cdiv style=\"background: white; color: black; padding: 1em; border-radius: 5px;\"\u003e \u003cinput type=\"radio\" :value=\"poll.name\" :name=\"poll.name\" @change=\"upvote(index)\"\u003e Yes \u003cspan style=\"padding-left: 60%;\"\u003e\u003ci class=\"fas fa-thumbs-up\"\u003e\u003c/i\u003e ({{ poll.upvotes }}) \u003c/span\u003e\u003c/div\u003e\n                        \u003chr\u003e\n                        \u003cdiv style=\"background: white; color: black; padding: 1em; border-radius: 5px;\"\u003e \u003cinput type=\"radio\" :value=\"poll.name\" :name=\"poll.name\" @change=\"downvote(index)\" \u003e No \u003cspan style=\"padding-left: 60%;\"\u003e\u003ci class=\"fas fa-thumbs-down\"\u003e\u003c/i\u003e ({{ poll.downvotes }}) \u003c/span\u003e\u003c/div\u003e\n                    \u003c/form\u003e\n                    \u003cbutton class=\"btn btn-block\" style=\"margin: 1em 0; background: #1bff8b; cursor: pointer; font-weight: bold\" v-on:click=\"UpdatePoll(index)\"\u003e Vote \u003c/button\u003e\n                \u003c/div\u003e\n              \u003c/div\u003e\n            \u003c/div\u003e\n        \u003c/div\u003e\n    \u003c/body\u003e\n    \u003c/html\u003e\n```\n\nNext in the same file, paste the following code before the closing `body` tag of the HTML:\n\n``` javascript\n    \u003cscript\u003e\n    var app = new Vue({\n        el: '#app',\n        data: {\n            polls: [],\n            click: [],\n        },\n        created: function () {\n            axios.get('/polls')\n                .then(res =\u003e this.polls = res.data.items ? res.data.items : [])\n                .catch(e =\u003e this.failed('Unsuccesful'))\n        },\n        methods: {\n            upvote: function (n) {\n                if (this.click[n] == true) {\n                    this.polls[n].downvotes -= 1;\n                    this.polls[n].upvotes += 1;\n                } else {\n                    this.polls[n].upvotes += 1;\n                    this.click[n] = true;\n                }\n            },\n            downvote: function (n) {\n                if (this.click[n] == true) {\n                    this.polls[n].upvotes -= 1;\n                    this.polls[n].downvotes += 1;\n                } else {\n                    this.polls[n].downvotes += 1;\n                    this.click[n] = true;\n                }\n            },\n            UpdatePoll: function (index) {\n                let targetPoll = index + 1;\n                axios.put('/poll/' + targetPoll, this.polls[index])\n                    .then(res =\u003e this.approved('Successful'))\n                    .catch(e =\u003e this.failed('Unsuccesful'))\n            },\n            approved: function (data) {\n                $(\"#msg\").css({\n                    \"background-color\": \"rgb(94, 248, 94)\",\n                    \"border-radius\": \"20px\"\n                });\n                $('#msg').html(data).fadeIn('slow');\n                $('#msg').delay(3000).fadeOut('slow');\n            },\n            failed: function (data) {\n                $(\"#msg\").css({ \"background-color\": \"rgb(248, 66, 66)\", \"border-radius\": \"20px\" });\n                $('#msg').html(data).fadeIn('slow');\n                $('#msg').delay(3000).fadeOut('slow');\n            }\n        }\n    })\n    \u003c/script\u003e\n```\n\nAbove we have our Vue code. We added the `created()` life cycle hook so that [Axios](https://www.npmjs.com/package/axios) can make a `GET` request to the backend API.\n\nWeve also defined two functions to keep track of the clicks on `upvotes` or `downvotes` to any members of the poll. These functions call another function, `UpdatePoll`, which takes the index of the affected poll member as argument and makes a PUT request to the backend API for an update.\n\nLastly, we used jQuery to display matching `divs`  depending on if the update request was successful or unsuccessful.\n\nHeres a display of the application at the current level:\n\n![voting-app-go-stage-1](//images.ctfassets.net/1es3ne0caaid/3E8smWapTOSuska6mm0mgw/7e961cac0db5b12ec2a0994c9deaaa11/voting-app-go-stage-1.png)\n\nNext, head over to [Pusher](https://pusher.com), you can [create a free account](https://pusher.com/signup?plan=sandbox) if you dont already have one. On the [dashboard](https://dashboard.pusher.com/), create a new app and copy out the app credentials (App ID, Key, Secret, and Cluster). We will use these credentials shortly.\n\n### Sending realtime data from the backend\n\nTo make sure our application is realtime, our backend must trigger an event when the poll is voted on. \n\nTo do this lets pull in the [Pusher Go library](https://github.com/pusher/pusher-http-go), which we will use to trigger events. Run the command below to pull in the package:\n\n```\n    $ go get github.com/pusher/pusher-http-go\n```\n\nIn the `models.go` file, lets import the Pusher Go library:\n \n``` go\n    package models\n    \n    import (\n        // [...]\n    \n        pusher \"github.com/pusher/pusher-http-go\"\n    )\n```\n \nThen initialize the Pusher client. In the same file before the type definitions paste in the following:\n\n``` go\n    // [...]\n    \n    var client = pusher.Client{\n            AppId:   \"PUSHER_APP_ID\",\n            Key:     \"PUSHER_APP_KEY\",\n            Secret:  \"PUSHER_APP_SECRET\",\n            Cluster: \"PUSHER_APP_CLUSTER\",\n            Secure:  true,\n    }\n    \n    // [...]\n```\n\nHere, we have initialized the Pusher client using the credentials from our earlier created app.\n\n\u003e   Replace `PUSHER_*`  keys with your app credentials.\n\nNext, we will use our Pusher client to trigger an event, which will include the updates on the specific row in the database to be displayed as an update to the votes in our view. We will do this in the `UpdatePoll` method, which updates the state of `upvotes` and `downvotes` in the database.\n\nReplace the `UpdatePoll` function with the following code:\n\n``` go\n    func UpdatePoll(db *sql.DB, index int, name string, upvotes int, downvotes int) (int64, error) {\n        sql := \"UPDATE polls SET (upvotes, downvotes) = (?, ?) WHERE id = ?\"\n    \n        stmt, err := db.Prepare(sql)\n    \n        if err != nil {\n            panic(err)\n        }\n    \n        defer stmt.Close()\n    \n        result, err2 := stmt.Exec(upvotes, downvotes, index)\n    \n        if err2 != nil {\n            panic(err2)\n        }\n    \n        pollUpdate := Poll{\n            ID:        index,\n            Name:      name,\n            Upvotes:   upvotes,\n            Downvotes: downvotes,\n        }\n    \n        client.Trigger(\"poll-channel\", \"poll-update\", pollUpdate)\n        return result.RowsAffected()\n    }\n```\n\nAbove, we create a `pollUpdate` object that holds the data for the most recent update to a row in the `polls` table. This `pollUpdate` object has all the data required for a realtime update on the client-side of our application, so will be passed to Pusher for transmission.\n\n### Displaying data in realtime on the client\nTo display the realtime updates on votes, we will use the Pusher JavaScript client. Open your `index.html` file and include the Pusher JavaScript library inside the `head` tag like this:\n\n```\n    \u003cscript src=\"https://js.pusher.com/4.1/pusher.min.js\"\u003e\u003c/script\u003e\n```\n\nNext, we want to go to the `created()` method and create a Pusher instance using our apps credentials:\n\n``` go\n    created: function() {\n        const pusher = new Pusher('PUSHER_APP_KEY', {\n            cluster: 'PUSHER_APP_CLUSTER',\n            encrypted: true\n        });\n        \n        // [...]\n    }\n```\n\n\u003e   Replace `PUSHER_APP_*` with values from your applications credentials.\n\nNext, lets subscribe to the `poll-channel` and listen for the `poll-update` event, where our votes updates will be transmitted. Right after the code we added above, paste the following:\n\n``` go\n    const channel = pusher.subscribe('poll-channel');\n    \n    channel.bind('poll-update', data =\u003e {\n        this.polls[data.id - 1].upvotes = data.upvotes;\n        this.polls[data.id - 1].downvotes = data.downvotes;\n    });\n```\n\n\u003e **Note**:  We are subtracting from the `polls` array index because we need it to match the data received from Pusher. JavaScript arrays begin their index at 0, while SQL id starts at 1.  \n\nNow we can build our application and see that the realtime functionality in action.\n\n```\n    $ go run poll.go\n```\n\nOnce the application is running, we can point our browser to this address [http://localhost:9000](http://localhost:9000/)\n\n![voting-app-go-demo-1](//images.ctfassets.net/1es3ne0caaid/5ZJDwcs6Zig28MsWIoqoMM/7b1fed4d80b0e15f596adde3027bcc9c/voting-app-go-demo-1.gif)\n\n## Conclusion\n\nIn this article, we were able to trigger realtime updates on new votes and demonstrate how Pusher works with Go applications. We also learnt, on an unrelated note, how to consume APIs using Vue.js.\n\nThe source code to the application is available on [GitHub](https://github.com/neoighodaro/realtime-poll-go-pusher).","metaTitle":"Build a voting application with Go and Vue.js","metaDescription":"This short tutorial will show you how to build a simple web app with Go and Vue.js. You will create a polling app. Users will be able to vote on their favorite JavaScript frameworks, and see responses update instantly."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5qEUPHIZVeWEQqQUECSoIe","type":"Entry","createdAt":"2018-05-14T09:35:22.610Z","updatedAt":"2018-05-14T09:35:22.610Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a data visualization using Angular and Chart.js","url":"data-visualization-angular","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5Q1I2HN8pqGKG2wYcCeaO2","type":"Entry","createdAt":"2018-02-12T17:24:27.398Z","updatedAt":"2018-02-14T10:09:48.043Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christian Nwamba","url":"christian-nwamba","email":"nwambachristian@gmail.com","twitter":"https://twitter.com/codebeast","github":"https://github.com/christiannwamba","guestWriter":true}}],"projectRepo":"https://github.com/christiannwamba/angular-demographics","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"1SM1spRqTeua46Ew0W6UwC","type":"Entry","createdAt":"2018-02-12T14:40:52.995Z","updatedAt":"2018-02-12T14:40:52.995Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":1,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Angular","url":"angular","type":"framework"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5JtEWXIwaAaOM4KOWMO0qK","type":"Entry","createdAt":"2018-02-12T15:10:44.896Z","updatedAt":"2018-02-26T12:31:11.752Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Data Visualization","url":"data-visualization","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}}],"reviewDate":"2018-05-13","description":"Build a live poll of user ages, with Angular, Node, Express and Chart.js.","prerequisite":"A basic understanding of Angular and Node is required. You will need to have Node and npm installed on your machine.","content":"\u003e **Data visualization** is viewed by many disciplines as a modern equivalent of visual communication. It involves the creation and study of the visual representation of data. - [Wikipedia](https://en.wikipedia.org/wiki/Data_visualization)\n\n An important advantage of data visualization is how it enables users to more effectively see connections as they are occurring between operating conditions and business performance. Adding realtime functionality using [Pusher](https://pusher.com) improves this experience as data changes are witnessed in realtime.\n\nUsing [Angular](https://angular.io) you can extend the template language with your own components and use a wide array of existing components. \n\nTo follow this tutorial a basic understanding of Angular and Node.js is required. Please ensure that you have Node and npm installed before you begin.\n\nIf you have no prior knowledge of Angular, kindly follow the tutorial [here](https://angular.io/tutorial). Come back and finish the tutorial when youre done.\n\nWell be using these tools to build our application:\n\n- [Express](https://expressjs.com/) \n- [Node](https://nodejs.org/en/)\n- [Pusher](https://pusher.com)\n- [Angular](https://angular.io)\n- [Chart.js](http://www.chartjs.org/)\n\nWell be creating an application that will present data about the age demographics of a given population. Using our application, users will complete a poll and then see the data from the polls in realtime.\n\nHeres a screenshot of the final product:\n\n![angular-data-visualization-preview](//images.ctfassets.net/1es3ne0caaid/2pLq90Q6FKCoIM640YKww8/ce513a337fbfcb2e2dbb55bb1daa30d2/angular-data-visualization-preview.png)\n\n## Initializing application and installing dependencies\n\nTo get started, we will use the [CLI](https://cli.angular.io/) (command line interface) provided by the Angular team to initialize our project.\n\nFirst, install the CLI by running `npm install -g @angular/cli`. [NPM](https://www.npmjs.com/) is a package manager used for installing packages. It will be available on your PC if you have [Node](https://nodejs.org/en/) installed.\n\nTo create a new Angular project using the CLI, open a terminal and run:  \n`ng new angular-data-visualization --style=scss`\n\nThis command is used to initialize a new Angular project; the project will make use of SCSS for styling.\n\nNext run the following command in the root folder of the project to install dependencies.\n\n```\n    // install depencies required to build the server\n    npm install express body-parser dotenv pusher\n    \n    // front-end dependencies\n    npm install chart.js ng2-charts pusher-js\n```\nStart the Angular development server by running `ng serve` in a terminal in the root folder of your project.\n\n## Building our server\n\nWell build our server using [Express](https://expressjs.com). Express is a fast, unopinionated, minimalist web framework for [Node.js](https://nodejs.org/en/).\n\nCreate a file called `server.js` in the root of the project and update it with the code snippet below\n\n``` javascript\n    // server.js\n    \n    require('dotenv').config();\n    const express = require('express');\n    const bodyParser = require('body-parser');\n    const Pusher = require('pusher');\n    \n    const app = express();\n    const port = process.env.PORT || 4000;\n    const pusher = new Pusher({\n      appId: process.env.PUSHER_APP_ID,\n      key: process.env.PUSHER_KEY,\n      secret: process.env.PUSHER_SECRET,\n      cluster: 'eu',\n    });\n    \n    app.use(bodyParser.json());\n    app.use(bodyParser.urlencoded({extended: false}));\n    app.use((req, res, next) =\u003e {\n      res.header('Access-Control-Allow-Origin', '*');\n      res.header(\n        'Access-Control-Allow-Headers',\n        'Origin, X-Requested-With, Content-Type, Accept'\n      );\n      next();\n    });\n    \n    app.listen(port, () =\u003e {\n      console.log(`Server started on port ${port}`);\n    });\n```    \n\nThe calls to our endpoint will be coming in from a different origin. Therefore, we need to make sure we include the CORS headers (`Access-Control-Allow-Origin`). If you are unfamiliar with the concept of CORS headers, you can find more information [here](https://developer.mozilla.org/en-US/docs/Web/HTTP/Access_control_CORS).\n\nThis is a standard Node application configuration, nothing specific to our app.\n\nCreate a Pusher account and a new Pusher Channels app if you havent done so yet and get your `appId`, `key` and `secret`. Create a file in the root folder of the project and name it `.env`. Copy the the following snippet into the `.env` file and ensure to replace the placeholder values with your Pusher credentials.\n\n```\n    // .env\n    \n    // Replace the placeholder values with your actual pusher credentials\n    PUSHER_APP_ID=PUSHER_APP_ID\n    PUSHER_KEY=PUSHER_KEY\n    PUSHER_SECRET=PUSHER_SECRET\n```\nWell make use of the `dotenv` library to load the variables contained in the `.env` file into the Node environment. The `dotenv` library should be initalized as early as possible in the application.\n\n### Send votes\n\nTo let users send requests to the server, well create a route to handle incoming requests. Update your `server.js`  file with the code below. \n\n``` javascript\n    // server.js\n    require('dotenv').config();\n    ...\n    \n    app.use((req, res, next) =\u003e {\n      res.header('Access-Control-Allow-Origin', '*');\n      ...\n    });\n    \n    \n    app.post('/vote', (req, res) =\u003e {\n      const {body} = req;\n      const data = {\n        ...body,\n        // set the selected property of the body to true\n        selected: true,\n      };\n      // trigger a new-entry event on the vote-channel\n      pusher.trigger('vote-channel', 'new-entry', data);\n      res.json(data);\n    });\n    \n     ...\n```\n\n- We added a POST route(`/vote`) to handle incoming requests.\n- Using [object destructuring](https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Operators/Destructuring_assignment), we got the body of the request.\n-  The trigger is achieved using the `trigger` method which takes the trigger identifier(`vote-channel`), an event name (`new-entry`), and a payload.\n- The payload being sent contains the body of the request sent in. The `selected` property of the payload is set to `true`.\n\n\n## Home view\n\nThe home view of the project will house both the polling area and the area where the data is visualized. Both views will appear side by side.\n\nOpen the `app.component.html` file and replace it with the content below.\n\n``` html\n    // app.component.html\n    \n    \u003cdiv class=\"main\"\u003e\n      \u003cdiv class=\"section census\"\u003e\n        \u003ch4\u003eAge Demographics\u003c/h4\u003e\n        \u003cp\u003eSelect your age range\u003c/p\u003e\n    \n        \u003cdiv class=\"option-list\"\u003e\n          \u003cbutton class=\"option\" *ngFor=\"let option of options; index as i\" (click)=\"takeVote(i)\" [ngClass]=\"{selected: option.selected}\"\n          \n                  [disabled]=\"selectedOption \u0026\u0026 selectedOption !== option.value\"\u003e\n            \u003ci class=\"fas fa-check\" *ngIf=\"option.selected\"\u003e\u003c/i\u003e\n            {{option.label}}\n          \u003c/button\u003e\n        \u003c/div\u003e\n        \u003cp class=\"error\"\u003e{{message}}\u003c/p\u003e\n      \u003c/div\u003e\n    \n      \u003cdiv class=\"section data\"\u003e\n        \u003c!-- Include the chart component here --\u003e\n      \u003c/div\u003e\n    \n    \u003c/div\u003e\n```    \n\n\n- In the code snippet above, we looped through `options` to create a view based on the players information. \n- The `takeVote` method will make use of the `HttpClient`  to send the users selection as a request to the server.\n- We append a `selected` class if the `option`'s selected property is true.\n- Options are disabled if the current `selectedOption` isnt equal to the options `value`.\n\nVariables used will be defined in the components Typescript file.\n\n### Styling\n\n``` scss\n    // app.component.scss\n    \n    $buttonShadow: #ca6eca + 120;\n    .main {\n      display: flex;\n      padding-top: 5%;\n      .section {\n        padding: 20px;\n      }\n      .section.census {\n        flex: 1;\n        display: flex;\n        flex-direction: column;\n        align-items: center;\n      }\n      .data{\n        flex: 2;\n      }\n    }\n    p.error{\n      font-size: 13px;\n      color: red + 100;\n      margin: 5px 0;\n    }\n    h4 {\n      margin-bottom: 8px;\n      font-size: 13px;\n      text-transform: uppercase;\n      color: #ca6eca;\n      font-weight: bold;\n      letter-spacing: 0.4px;\n    }\n    p {\n      margin: 5px 0;\n    }\n    div.option-list {\n      padding-left: 0;\n      width: 200px;\n      display: flex;\n      flex-direction: column;\n      button.option {\n        padding: 15px;\n        box-shadow: 0 2px 4px 0 $buttonShadow;\n        margin: 7px 0;\n        background: #ca6eca;\n        color: white;\n        border-radius: 5px;\n        cursor: pointer;\n        font-size: 12px;\n        text-transform: uppercase;\n        font-weight: bold;\n        .fas {\n          font-size: 1.2em;\n          margin-right: 10px;\n        }\n      }\n      button[disabled] {\n        background: #ca6eca;\n        box-shadow: none;\n        cursor: default;\n      }\n      button.selected {\n        background: #ca6eca + 50;\n      }\n      button:focus {\n        background: #ca6eca + 50;\n        outline: none;\n      }\n    }\n```    \n    \n\nThese styles are meant to add a bit of life to our application. It also helps distinguish between states during application use. \n\nAlso, well be using the FontAwesome icon set for our project and the Roboto font. Include a link to their respective stylesheets in the `index.html`  file.\n\n``` html\n    // index.html\n    \n    \u003chead\u003e\n      ...\n      \u003cmeta name=\"viewport\" content=\"width=device-width, initial-scale=1\"\u003e\n      \u003clink href=\"https://fonts.googleapis.com/css?family=Roboto:300,400,500,700\" rel=\"stylesheet\"\u003e\n      \u003clink rel=\"stylesheet\" href=\"https://use.fontawesome.com/releases/v5.0.12/css/all.css\" integrity=\"sha384-G0fIWCsCzJIMAVNQPfjH08cyYaUtMwjJwqiRKxxE/rx96Uroj1BtIQ6MLJuheaO9\" crossorigin=\"anonymous\"\u003e\n      ...\n    \u003c/head\u003e\n    ...\n```      \n\nThen well select Roboto as our default font family. Open the `styles.scss` file and update it with the following content: \n\n``` scss\n    // styles.scss\n    \n    body{\n      font-family: 'Roboto', sans-serif;\n    }\n```    \n\n### App component\n\nIn the html snippet, we made reference to some variables that werent yet defined, well create the variables here with the logic behind our application. \n\n``` typescript\n    // app.component.ts\n    \n    import {Component, OnInit} from '@angular/core';\n    import {HttpClient} from '@angular/common/http';\n    export interface Option {\n      label: string;\n      selected: boolean;\n      value: string;\n    }\n    @Component({\n      selector: 'app-root',\n      templateUrl: './app.component.html',\n      styleUrls: ['./app.component.scss']\n    })\n    export class AppComponent implements OnInit {\n      constructor(private http: HttpClient) {\n      }\n      selectedOption = null;\n      message: string = '';\n      options: Option[] = [\n        {\n          label: '14 - 25',\n          selected: false,\n          value: '14-25'\n        },\n        {\n          label: '25 - 35', \n          selected: false, \n          value: '25-35'\n        },\n        {\n          label: '35 - 45',\n          selected: false,\n          value: '35-45'\n        },\n        {\n          label: '45 - 60',\n          selected: false,\n          value: '45-60'\n        },\n        {\n          label: '60 and above', \n          selected: false, \n          value: '60+'\n        }\n      ];\n      censusData = {\n        '14-25': 0,\n        '25-35': 0,\n        '35-45': 0,\n        '45-60': 0,\n        '60+': 0\n      };\n      takeVote(index) {\n        const selectedOption = this.options[index];\n        if (!selectedOption.selected) {\n          this.http\n            .post('http://localhost:4000/vote', selectedOption)\n            .subscribe((res: Option) =\u003e {\n              const options = this.options.map(\n                (option, i) =\u003e (index === i ? { ...res } : { ...option })\n              );\n              this.options = [...options];\n              this.selectedOption = res.value;\n            });\n        }\n        else{\n          this.message = \"You've already placed a vote\";\n        }\n      }\n      onNewEntry(data) {\n        this.censusData[data.value] += 1;\n      }\n      ngOnInit() {\n      }\n    }\n```\n\n- `takeVote`: this method makes use of the native HttpClient service to make requests to our server. A request is sent, only if the user hasnt made a previous selection. When a response is returned, it loops through the available `options` and replaces the selected option with the data returned from the request. The `selectedOption` property is then set to the value of the response.\n\n\n- `censusData`:  this will be used when we create the charts view. \n\n\n- `onNewEntry`: this method will be used to update the `censusData` whenever theres a new Pusher event. Well use this method after weve created the chart component.\n\nTo make use of the HttpClient service, well need to import the `HttpClientModule` into the `app.module.ts` file. Update your app module file as follows:\n\n``` typescript\n    // app.module.ts\n    import { BrowserModule } from '@angular/platform-browser';\n    import { NgModule } from '@angular/core';\n    import { AppComponent } from './app.component';\n    import {HttpClientModule} from '@angular/common/http';\n    \n    ....\n    @NgModule({\n      declarations: [AppComponent],\n      imports: [BrowserModule, HttpClientModule],\n      providers: [],\n      bootstrap: [AppComponent],\n    })\n      ....\n```\n\nLets check how our application looks at this point. Make sure the server(`node server`) and Angulars dev server(`ng serve`) are both running.\n\n![angular-data-visualization-stage-1](//images.ctfassets.net/1es3ne0caaid/5ghTCbycXYE4UY4wAQsAEq/09fc04236c8afaa93e808aaf73d42801/angular-data-visualization-stage-1.png)\n\n## Introducing Pusher\n\nSo far we have an application that allows users be a part of the polling process but data updates arent happening in realtime.  Lets create a service that will make it easier to include Pusher in our components.\n\nTo make the pusher library available in our project, add the library as a third party script to be loaded by Angular CLI. All CLI config is stored in `.angular-cli.json` file. Modify the `scripts` property to include the link to `pusher.min.js`.\n\n``` json\n    // .angular-cli.json\n    ...\n    \"scripts\": [\n      \"../node_modules/pusher-js/dist/web/pusher.min.js\",\n    ]\n     ...\n```          \n\nAfter updating this file, youll need to restart the angular server so that the CLI compiles the new script files added.\n\nNow well create a Pusher service to be used application wide. The Angular CLI can aid in the service creation.  Open a terminal in your projects root folder and run the following command.\n\n```\nng generate service pusher\n```\n\nThis command simply tells the CLI to generate a service named `pusher`. Now open the `pusher.service.ts` file and update it with the code below.\n\n``` typescript\n    // pusher.service.ts\n    import { Injectable } from '@angular/core';\n    declare const Pusher: any;\n    \n    @Injectable()\n    export class PusherService {\n      constructor() {\n        const pusher = new Pusher('PUSHER_KEY', {\n          cluster: 'eu',\n        });\n        this.channel = pusher.subscribe('vote-channel');\n      }\n      channel;\n      public init() {\n        return this.channel;\n      }\n    }\n```\n\n- First, we initialize Pusher in the constructor. \n- The  `init` method returns the Pusher property we created. \n\n\n\u003e **Note**: ensure you replace the `PUSHER_KEY` string with your actual Pusher key.\n  \n\nTo make the service available application wide, import it into the `app.module.ts` file.\n\n``` typescript\n    // app.module.ts\n    import {PusherService} from './pusher.service'\n    ...\n    \n    @NgModule({\n       ....\n       providers: [PusherService],\n       ....\n     })\n```\n \n\n## Charts component\n\nTo visualize the data in our application, well be making use of [Chart.js](http://www.chartjs.org/) to create charts and present the data in a graphical format. Lets create the charts component using the CLI by running the following command:\n\n```\n    ng generate component charts\n```\nOpen the `charts.component.html` file and update it with the contents below:\n\n``` html\n    // charts.component.html\n    \n    \u003cdiv\u003e\n      \u003ccanvas baseChart [chartType]=\"chartType\" [labels]=\"chartLabels\" [data]=\"chartData\"\u003e\n      \u003c/canvas\u003e\n    \u003c/div\u003e\n    \u003cdiv class=\"stats\"\u003e\n      \u003cdiv class=\"stat\"\u003e\n        \u003cdiv class=\"header\"\u003e\n          \u003cp\u003eTotal Pop.\u003c/p\u003e\n        \u003c/div\u003e\n        \u003cdiv class=\"body\"\u003e\u003ch4\u003e{{totalPopulation}}\u003c/h4\u003e\u003c/div\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"stat\"\u003e\n        \u003cdiv class=\"header\"\u003e\n          \u003cp\u003ePop. under 35\u003c/p\u003e\n        \u003c/div\u003e\n        \u003cdiv class=\"body\"\u003e\n          \u003ch4\u003e{{under35 || 0}}\u003c/h4\u003e\n          \u003cspan class=\"percent\"\u003e%\u003c/span\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"stat\"\u003e\n        \u003cdiv class=\"header\"\u003e\n          \u003cp\u003ePop. between 35 and 60\u003c/p\u003e\n        \u003c/div\u003e\n        \u003cdiv class=\"body\"\u003e\n          \u003ch4\u003e{{between35and60 || 0}}\u003c/h4\u003e\n          \u003cspan class=\"percent\"\u003e%\u003c/span\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n      \u003cdiv class=\"stat\"\u003e\n        \u003cdiv class=\"header\"\u003e\n          \u003cp\u003ePop. above 60\u003c/p\u003e\n        \u003c/div\u003e\n        \u003cdiv class=\"body\"\u003e\n          \u003ch4\u003e{{above60 || 0}}\u003c/h4\u003e\n          \u003cspan class=\"percent\"\u003e%\u003c/span\u003e\n        \u003c/div\u003e\n      \u003c/div\u003e\n    \u003c/div\u003e\n```\nStyle up the component by adding the following styles to the `charts.component.scss` file:\n\n``` scss\n    // charts.component.scss\n    \n    .stats {\n      display: flex;\n      margin-top: 30px;\n      .stat {\n        flex: 1;\n        box-shadow: 0 2px 4px 0 rgba(0,0,0,0.10);\n        margin: 0 15px;\n        background: rgba(0, 0, 0, 0.05);\n        .header {\n          background: white;\n          padding: 10px 5px;\n          p {\n            font-size: 12px;\n            margin: 0;\n            font-weight: bold;\n            text-transform: uppercase;\n            letter-spacing: -.4px;\n            text-align: center;\n            opacity: .7;\n          }\n        }\n        .body {\n          display: flex;\n          justify-content: center;\n          align-items: center;\n          padding: 15px;\n          h4 {\n            text-align: center;\n            font-size: 30px;\n            font-weight: 300;\n            opacity: 0.8;\n          }\n          .percent{\n            font-size: 32px;\n          }\n        }\n      }\n    }\n```\n\n\nAdd the following content to the `charts.component.ts` file:\n\n``` typescript\n    // charts.component.ts\n    \n    import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';\n    import {PusherService} from '../pusher.service';\n    import {Option} from '../app.component';\n    @Component({\n      selector: 'app-charts',\n      templateUrl: './charts.component.html',\n      styleUrls: ['./charts.component.scss']\n    })\n    export class ChartsComponent implements OnInit {\n      constructor(private pusher: PusherService) {\n      }\n      @Input() censusData = {};\n      @Output() newEntry: EventEmitter\u003cOption\u003e = new EventEmitter();\n      chartType = 'pie';\n      chartData = [];\n      chartLabels = [];\n      totalPopulation = 0;\n      under35 = 0;\n      between35and60 = 0;\n      above60 = 0;\n      \n      computeData() {\n        this.chartData = Object.values(this.censusData);\n        this.chartLabels = Object.keys(this.censusData);\n        this.totalPopulation = this.getTotalPopulation();\n        this.under35 = Math.round(this.getPopulationUnder35());\n        this.between35and60 = Math.round(this.getPopulationBetween35and60());\n        this.above60 = Math.round(this.getPopulationAbove60());\n      }\n      \n      getTotalPopulation() {\n        const values: number[] = Object.values(this.censusData);\n        return values.reduce((defaultValue, val) =\u003e defaultValue + val, 0);\n      }\n      \n      getPopulationUnder35() {\n        const total = this.getTotalPopulation();\n        const populationUnder35 = Object.keys(this.censusData).reduce(\n          (initVal, val) =\u003e {\n            if (val === '14-25' || val === '25-35') {\n              return initVal + this.censusData[val];\n            }\n            return initVal;\n          },\n          0\n        );\n        return populationUnder35 / total * 100;\n      }\n      \n      getPopulationBetween35and60() {\n        const total = this.getTotalPopulation();\n        const populationBetween35and60 = Object.keys(this.censusData).reduce(\n          (initVal, val) =\u003e {\n            if (val === '35-45' || val === '45-60') {\n              return initVal + this.censusData[val];\n            }\n            return initVal;\n          },\n          0\n        );\n        return populationBetween35and60 / total * 100;\n      }\n      \n      getPopulationAbove60() {\n        const total = this.getTotalPopulation();\n        const above60 = Object.keys(this.censusData).reduce((initVal, val) =\u003e {\n          if (val === '60+') {\n            return initVal + this.censusData[val];\n          }\n          return initVal;\n        }, 0);\n        return above60 / total * 100;\n      }\n      \n      ngOnInit() {\n        this.computeData();\n        const channel = this.pusher.init();\n        channel.bind('new-entry', (data: Option) =\u003e {\n          this.newEntry.emit(data);\n          this.computeData();\n        });\n      }\n    }\n```\n\n\nWeve defined a few methods for computing the data to be used in the view:\n\n- `getPopulation`: this method maps the values to an array and gets the total amount of people that took part in the polls.\n\n\n- `getPopulationUnder35`: this method checks for the percentage of the people under the age of 35 taking part in the polls.\n\n\n- `getPopulationBetween35and60`: in this method, we loop through the data and check for the percentage of users between the age of 35 and 60.\n\n\n- `getPopulationAbove60`: using this method we get the percentage of people over the age of 60.\n\n\n- Finally, in the `computeData` method, we run the different methods to get the required data for the view.\n\n\n- In the `ngOnInit` lifecycle, we call the `computeData` method. Also, we listen for the `new-entry`  event on the Pusher channel.\n\nTo make use of the `ng2-charts` package, well have to import the `ChartsModule` into our module file. Also, well have to load the `chart.js` into our application by including it into the `.angular-cli.json` file.\n\nUpdate the `app.module.ts` file like so:\n\n``` typescript\n    // app.module.ts\n    ...\n    import { ChartsComponent } from './charts/charts.component';\n    import {ChartsModule} from 'ng2-charts';\n    \n    @NgModule({\n      declarations: [\n        ...\n      ],\n      imports: [\n        BrowserModule,\n        HttpClientModule,\n        ChartsModule\n      ],\n      ...\n    })\n    ...\n```\nInclude the `chart.js` file in the `.angular-cli.json` file. Update the CLI file like so:\n\n``` json\n     // .angular-cli.json\n     ...\n      \"scripts\": [\n        \"../node_modules/pusher-js/dist/web/pusher.min.js\",\n        \"../node_modules/chart.js/dist/Chart.min.js\"\n      ],\n    ...\n```\n\nUpdate the `app.component.html` file to include the Charts component. \n\n``` html\n    // app.component.html\n    \u003cdiv class=\"main\"\u003e\n      \u003cdiv class=\"section census\"\n        ...\n      \u003c/div\u003e\n      \n      \u003cdiv class=\"section data\"\u003e\n        // include the charts component here\n        \u003capp-charts [censusData]=\"censusData\" (newEntry)=\"onNewEntry($event)\"\u003e\n        \u003c/app-charts\u003e\n      \u003c/div\n```\n\n\n At this point, your application should have realtime updates when votes are placed. Ensure that the server is running alongside the Angular development server. If not, run `node server` and `ng serve` in two separate terminals. Both terminals should be opened in the root folder of your project.\n \n![angular-data-visualization-preview](//images.ctfassets.net/1es3ne0caaid/2pLq90Q6FKCoIM640YKww8/ce513a337fbfcb2e2dbb55bb1daa30d2/angular-data-visualization-preview.png)\n\n \nTo test the realtime functionality of the application, open two browsers side-by-side and engage the application. Data updates should be in realtime.\n\n\n## Conclusion\n\nUsing Pusher,  weve built out an application using the pub/sub pattern to recieve realtime updates. With the help of Chart.js, our data was well presented using charts. You can check out the repo containing the demo on [GitHub](https://github.com/christiannwamba/angular-demographics).","metaTitle":"Build a data visualization using Angular and Chart.js","metaDescription":"This tutorial will guide you through building a simple web app with Angular, Node, Express and Chart.js. The app polls user ages and displays the results in a live graph."}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"7vLhFeS6u4i6SOqQeMSc4U","type":"Entry","createdAt":"2018-05-14T09:28:31.972Z","updatedAt":"2018-05-14T14:22:55.201Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"post"}},"locale":"en-US"},"fields":{"title":"Build a polling web app with Next.js","url":"polling-web-app-nextjs","authors":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"5Q1I2HN8pqGKG2wYcCeaO2","type":"Entry","createdAt":"2018-02-12T17:24:27.398Z","updatedAt":"2018-02-14T10:09:48.043Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"author"}},"locale":"en-US"},"fields":{"name":"Christian Nwamba","url":"christian-nwamba","email":"nwambachristian@gmail.com","twitter":"https://twitter.com/codebeast","github":"https://github.com/christiannwamba","guestWriter":true}}],"projectRepo":"https://github.com/Robophil/pet-wars","products":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"SRRvSlyWCAyg2KK4U4EW0","type":"Entry","createdAt":"2018-02-12T14:37:35.058Z","updatedAt":"2019-08-28T08:41:41.310Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"product"}},"locale":"en-US"},"fields":{"name":"Channels","url":"channels","isBeta":false,"featured":true}}],"tags":[{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"uGIU8YRFv2icaWKKwW4GQ","type":"Entry","createdAt":"2018-01-26T11:40:16.226Z","updatedAt":"2018-02-12T14:41:04.134Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"JavaScript","url":"javascript","type":"language"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"4E7t0TtVFSAyeeA0ckeYe0","type":"Entry","createdAt":"2018-02-12T15:09:41.899Z","updatedAt":"2018-02-26T12:31:11.510Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Chart","url":"realtime-chart","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6i1HPBqlwsE24YEKwqQyuq","type":"Entry","createdAt":"2018-02-12T15:09:31.050Z","updatedAt":"2018-02-26T12:31:11.548Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Realtime Graph","url":"realtime-graph","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6KUSaq9zVuw0SWMYaWAUQI","type":"Entry","createdAt":"2018-02-12T15:11:33.107Z","updatedAt":"2018-02-26T12:31:11.535Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":2,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Social","url":"social","type":"use case"}},{"sys":{"space":{"sys":{"type":"Link","linkType":"Space","id":"1es3ne0caaid"}},"id":"6N8l3o0yD6QK2ooI2ME8wW","type":"Entry","createdAt":"2018-02-12T15:09:06.190Z","updatedAt":"2018-02-26T12:31:10.478Z","environment":{"sys":{"id":"master","type":"Link","linkType":"Environment"}},"revision":3,"contentType":{"sys":{"type":"Link","linkType":"ContentType","id":"tag"}},"locale":"en-US"},"fields":{"name":"Live Polls","url":"live-polls","type":"use case"}}],"reviewDate":"2018-05-13","description":"Build a voting webapp using Next.js. Users can vote for their favorite pet, and the results are displayed in realtime on a graph in their browser.","prerequisite":"You will need Node and npm or Yarn installed on your machine. A basic understanding of JavaScript will be helpful.","content":"Polls are a good way of capturing popular opinions from people within a limited set of options. We'll set out on a quest to find out the most popular household pet. Dogs, cats or hamsters.\n\nLike all polls, we'll want to see the results in realtime and we'll call this app \"PET WARS: The polls awakening\".\n\nAt the end of this, users should be able to cast their votes and see the results change as other users cast theirs too.\n\n![nextjs-poll-demo](//images.ctfassets.net/1es3ne0caaid/2lGBS6yEL2AaAsqgg6eyAy/363a4c7688edf300da871ce2b3fb9bfe/nextjs-poll-demo.gif)\n\n## Prerequisite\n\nKindly ensure you have [Node](https://nodejs.org/en/download/), [Npm](https://www.npmjs.com/get-npm) or [Yarn](https://yarnpkg.com/en/docs/install) installed on your machine before moving past this section. This will be needed for running and managing the dependencies needed by our application.\nAlso, no knowledge of React is required, but a basic understanding of JavaScript may be helpful.\n\n\n1. [Next.js](https://github.com/zeit/next.js): this is a framework for developing server-side rendered applications, just as you would with PHP, but this time with React.\n2. [Pusher](https://pusher.com/): this is a framework that allows you to build realtime applications with its easy to use pub/sub messaging API.\n3. [Chart.js](https://pusher.com/): this is a library that makes plotting charts pretty easy. More specifically, we'll be using [react-chartjs2](https://github.com/jerairrest/react-chartjs-2) which is a simple wrapper to make this easier to use in [React.js](https://reactjs.org/) applications.\n\n\n\n## App structure\n\nWe'll start by setting up our Next.js application. The easiest way to go about this is to clone the [nextjs-javascript-starter](https://github.com/robophil/nextjs-javascript-starter) repo. Run:\n\n```\n    git clone https://github.com/Robophil/nextjs-javascript-starter.git pet-wars\n```\nThis will clone the starter pack into a directory called `pet-wars`. Our app directory will look like this.\n\n![nextjs-poll-folder-structure](//images.ctfassets.net/1es3ne0caaid/4cAZ9Mnblucy8OAGQ44kOU/8863db814f0e678f9c51d895c4e61c87/nextjs-poll-folder-structure.png)\n\n1. components: any Next.js component we'll create will go here.\n2. css: styles for our components and pages would go here.\n3. pages: any `.js` file in this directory would be served as a page. So any page we'll want to create would go here.\n\n\n## Install dependencies\n\nTo install the dependencies needed by our app, run:\n\n```\n    # enter app directory\n    cd pet-wars\n    \n    # install dependencies from nextjs-javascript-starter\n    yarn \n    # OR\n    npm install\n    \n    # add client-side dependencies\n    yarn add react-chartjs-2 chart.js axios pusher-js\n    # OR\n    npm install --save react-chartjs-2 chart.js axios pusher-js\n    \n    # add server-side dependencies\n    yarn add cors express pusher body-parser\n    # OR\n    npm install -save cors express pusher body-parser\n```\nNow we have all dependencies needed by our app installed.\n\n\n## Getting our Pusher credentials\n\nIf you don't have a Pusher account already, kindly create one [here](https://pusher.com/signup). Once you have an account, simply head down to you [dashboard](https://dashboard.pusher.com/) and create an app. Once that is done, click on **App Keys** and note your credentials. We'll be needing them in a bit.\n\n![nexts-poll-app-keys](//images.ctfassets.net/1es3ne0caaid/1sEBQc3ZHyeWCyCauEE242/ca8f12dc8cab0ab03209eee6eaab2c7b/nexts-poll-app-keys.png)\n\nNow that we have all dependencies and credentials needed to build our application, let's get building!\n\n## Create the chart component\n\nWe'll need to display the results of the polls as they happen to users using a bar chart. Start by creating the file `Chart.js` in the components directory.\n\n````javascript\n    // components/Chart.js\n    import React from 'react'\n    import { Bar } from 'react-chartjs-2'\n    \n    export default class Chart extends React.Component {\n      render () {\n        return (\n          \u003cBar\n            data={parseData(this.props.data)}\n            width={50}\n            height={100}\n            options={options}\n            /\u003e\n        )\n      }\n    }\n```\n\nThe first two lines import our dependencies. In our `render` method, we declare the `Bar` component and pass in required props.\n\nThe `parseData` method is responsible for passing in the styles and configuration needed by the `Bar` component. The `data` parameter it receives is an array of poll values to be displayed on the graph. \nCopy the code block below and paste after line 5.\n\n``` javascript\n    const parseData = data =\u003e ({\n      labels: ['Dogs %', 'Cats %', 'Hamsters %'],\n      datasets: [\n        {\n          label: 'The polls awakening',\n          backgroundColor: [\n            'rgba(255, 99, 132, 0.2)',\n            'rgba(54, 162, 235, 0.2)',\n            'rgba(255, 206, 86, 0.2)'\n          ],\n          borderColor: [\n            'rgba(255, 99, 132, 0.2)',\n            'rgba(54, 162, 235, 0.2)',\n            'rgba(255, 206, 86, 0.2)'\n          ],\n          borderWidth: 5,\n          hoverBackgroundColor: 'rgba(255,99,132,0.4)',\n          hoverBorderColor: 'rgba(255,99,132,1)',\n          data\n        }\n      ]\n    })\n    \n    const options = {\n      scales: {\n        yAxes: [{\n          ticks: {\n            beginAtZero: true\n          }\n        }]\n      },\n      maintainAspectRatio: false\n    }\n```\n## Creating the voting component\n\nLike any good poll, users need to be able to cast their votes easily. We'll be building a `Vote-buttons` component. When the user votes for their selected candidate, the result will be sent to the server and other users will receive updates. Start by creating the file `Vote-buttons.js` in the component directory.\n\n``` javascript\n    // components/Vote-buttons.js\n    import React from 'react'\n    import '../css/vote-buttons.css'\n    \n    export default class VoteButtons extends React.Component {\n      render () {\n        return (\n          \u003cdiv className={'vote-button-group'}\u003e\n            \u003cbutton className={'vote-button'} onClick={() =\u003e this.props.handleVote({vote: 'dogs'})}\u003eVote Dogs\u003c/button\u003e\n    \n            \u003cbutton className={'vote-button'} onClick={() =\u003e this.props.handleVote({vote: 'cats'})}\u003eVote Cats\u003c/button\u003e\n    \n            \u003cbutton className={'vote-button'} onClick={() =\u003e this.props.handleVote({vote: 'hamsters'})}\u003eVote Hamsters\u003c/button\u003e\n          \u003c/div\u003e\n        )\n      }\n    }\n```\n\nThis simply creates three buttons that accept an `onClick` event that would send the votes down to the server.\n\n\n## Styling the button component\n\nWe want our buttons to sit on the same line right beneath the bar chart. Simply create the file `vote-buttons.css` in the CSS directory and add the following:\n\n\n``` css\n    /* css/vote-buttons.css */\n    .vote-button-group {\n      display: flex;\n      width: 100%;\n    }\n    \n    button.vote-button {\n        flex-direction: row;\n        flex: 1;\n        margin: 20px;\n        height: 40px;\n        border-radius: 5%;\n    }\n```\n\n## Creating the index page\n\nAny `.js` file created in the `pages` directory becomes a page that can be served by `Next.js`. We already have a `index.js`  file in our page directory. Replace the content with the following below\n\n``` javascript\n    // pages/index.js\n    import React from 'react'\n    import axios from 'axios'\n    import Pusher from 'pusher-js'\n    import Chart from '../components/Chart'\n    import VoteButtons from '../components/Vote-buttons'\n    \n    var pusher = new Pusher('app_key', {\n      cluster: 'cluster',\n      encrypted: true\n    })\n    const channel = pusher.subscribe('pet-wars')\n    \n    export default class Index extends React.Component {\n      constructor (props) {\n        super(props)\n        this.state = {\n          data: [0, 0, 0]\n        }\n      }\n    \n      render () {\n        return (\n          \u003cdiv\u003e\n            \u003cChart data={this.state.data} /\u003e\n            \u003cVoteButtons handleVote={this.handleVote.bind(this)} /\u003e\n          \u003c/div\u003e\n        )\n      }\n    }\n```\n\nWe start by importing our dependencies which include the components we just created. Well also initialize Pusher ****with our credentials and subscribe to the channel `pet-wars`. Replace `app_key` with your app key gotten from your Pusher dashboard. At the bottom of the page, well render our `Chart` component with the `VoteButtons` component right below it.\n\nStill, in our `pages/index.js` file, we want our application to receive changes update when voting happen elsewhere. So we subscribe our app to listen for updates once the component has mounted. The `handleVote` method simply submits votes made for any pet to the server.\n\nCopy the code block below and paste on line 21.\n\n``` javascript\n      componentDidMount () {\n        this.receiveUpdateFromPusher()\n      }\n    \n      receiveUpdateFromPusher () {\n        channel.bind('new-votes', data =\u003e {\n          this.setState({\n            data\n          })\n        })\n        console.log('app subscription to event successful')\n      }\n    \n      handleVote (data) {\n        axios.post('http://localhost:8080/vote', data)\n        .then(res =\u003e {\n          console.log('received by server')\n        })\n        .catch(error =\u003e {\n          throw error\n        })\n      }\n```\n\n## Create our simple server\n\nVotes made are sent to the server and dispersed to all users using Pusher. First, we need to build the route where votes will be sent to. Start by creating the file `server.js` in your root directory.\n\nHere, Express is initialized with some middleware and our application started on port `8080`. Pusher is also initialized with its credentials which can be gotten from the dashboard. The function `getPercentage` turns the cast votes to a percentage.\n\n``` javascript\n    // server.js\n    const express = require('express')\n    const app = express()\n    const bodyParser = require('body-parser')\n    const cors = require('cors')\n    const Pusher = require('pusher')\n    \n    app.use(cors())\n    app.use(bodyParser.urlencoded({ extended: true }))\n    app.use(bodyParser.json())\n    \n    const port = process.env.PORT || 8080\n    \n    const pusher = new Pusher({\n      appId: 'app_id',\n      key: 'key',\n      secret: 'secret',\n      cluster: 'cluster',\n      encrypted: true\n    })\n    \n    let dogs = 0\n    let cats = 0\n    let hamsters = 0\n    \n    const getPercentage = value =\u003e (value * 100) / (dogs + cats + hamsters)\n    \n    app.listen(port, function () {\n      console.log('Node app is running at localhost:' + port)\n    })\n```\n\nNext, we create the endpoint where votes will be sent to. If the vote is for a dog, the count is increased and we update the channel `pet-wars` with the event `new-votes`. Before the values are published, the method `getPercentage` is called to transform the vote counts to a percentage.\n\nCopy the code block below and paste on line 25.\n\n``` javascript\n    app.post('/vote', function (req, res) {\n      const {vote} = req.body\n      if (vote === 'dogs') {\n        dogs++\n      }\n      if (vote === 'cats') {\n        cats++\n      }\n      if (vote === 'hamsters') {\n        hamsters++\n      }\n      pusher.trigger('pet-wars', 'new-votes', [getPercentage(dogs), getPercentage(cats), getPercentage(hamsters)])\n      res.sendStatus(200)\n    })\n```\n\n## Test our application\n\nFor convenience, update the `script` field of our `package.json` file with the snippet below. Also update the `name` field, changing it's value from `nextjs-javascript-starter` to `pet-wars`.\n\n``` javascript\n    \"scripts\": {\n        \"client\": \"next\",\n        \"server\": \"node server.js\"\n    }\n```\nThis would allow us to start both our Next.js app and our api server by running the following.\n\n```\n    # start next.js app\n    yarn run client\n    \n    # start api server\n    yarn run server\n```\nYour app will be running on http://localhost:3000. Open it in as many browser tabs as possible and cast your votes. Watch it update in one tab as votes are cast in another tab.\n\n## Conclusion\n\nWe've been able to build a realtime polling app for finding out the favourite household pets between dogs, cats and hamsters. We learnt how to use Pusher to publish and subscribe to an event in a Next.js application.\nThe complete source code can be found [here](https://github.com/Robophil/pet-wars).","metaTitle":"Build a polling web app with Next.js","metaDescription":"Learn to build a webapp that accepts user votes, using Next.js and Chart.js. Users can vote for their favorite pet, and the results are displayed in realtime on a graph in their browser."}}],"defaultValue":"Channels \u0026 Live Polls","queryType":"tag","pages":2,"route":"/tutorials","query":{"product":"Channels","tag":"Live Polls"}}},"page":"/tutorials","query":{"product":"Channels","tag":"Live Polls"},"buildId":"5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f","isFallback":false,"customServer":true,"ids":[]}</script><script nomodule="" src="_next/static/runtime/polyfills-6cf13c089a5684eabd63.js"></script><script async="" data-next-page="/tutorials" src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/pages/tutorials.js"></script><script async="" data-next-page="/_app" src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/pages/_app.js"></script><script src="_next/static/runtime/webpack-b65cab0b00afd201cbda.js" async=""></script><script src="_next/static/chunks/framework.8f8ac35b6c50c5d44e18.js" async=""></script><script src="_next/static/chunks/commons.5bea89f818e6d7d50d28.js" async=""></script><script src="_next/static/runtime/main-584a95128cf78f0fbea3.js" async=""></script><script src="_next/static/chunks/76f4f697.bba43166686acfbb5f45.js" async=""></script><script src="_next/static/chunks/40541903ab1c1ac841a57dd2b1fba9dfbed8c9ab.d608edb81271263c90db.js" async=""></script><script src="_next/static/chunks/5cad565c69a90ad2361a05d3169ab69cc3ef1b5b.50169a8607cbe0f0091c.js" async=""></script><script src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/_buildManifest.js" async=""></script><script src="_next/static/5ca6c1ecbe5c9b23ac66836b5e096d6b6c6ce25f/_ssgManifest.js" async=""></script><link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/nprogress@0.2.0/nprogress.css"/></body>
